@-moz-document domain("rumanga.net") {
#header {background-color:transparent!important;color:black;}
#header a {border:1px solid black; border-radius:3px;padding:2px;display:inline-block;}
h1,h2,h3{color:#000080!IMPORTANT;}
#login,#settings {width:0px;background-color:black;padding:0px 0px 0px 31px;}
#login {right:32px!important;}
#settings {right:0px!important;}
.manga {background:transparent;background-image:none!important;border-width:1px!important;display:inline-block;height:120px!important;overflow-x:hidden;owerflow-y:scroll;width:30%!important;font-size:12px;float:none!important;border-radius:3px!important;background: rgba(125,125,255,0.8);box-shadow: inset 0px 0px 10px white;}
.manga h2{height: 2em; overflow: hidden;display:block;width:100%;}
