css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("uchan.to") {


/* container */

  html {
    background:#eee !important;
    color:#222 !important;  
    text-align:center !important;
  }

  body { 
    background:none !important;
    color:inherit !important;
    display:inline-block !important;
    line-height:20px !important;
    max-width:1000px !important;
    text-align:left !important;
  }


/* decor */

  a, .postername, .commentpostername, .filetitle {
    color:#597adf !important;
  }

  .barsadmin[href] strong {
    text-decoration:underline !important;
  }

  .replytitle {
    color:#962 !important;
  }

  input, textarea, .postblock, .reply, .omittedposts {
    color:inherit !important;
  }

  input, textarea, .postblock, .reply, .post_button {
    background-color:#ddd !important;
    border-color:#ccc !important;
  }

  input {
    line-height:22px !important;
  }

  textarea {
    line-height:18px !important;
    padding:2px 3px !important;
  }

  a[href] + blockquote ol, a[href] + blockquote ul {
    display:table !important;
  }

  h1 {
    font-size:32px !important;
    line-height:36px !important;
  }

  hr, .thread, .omittedposts + table, table + table {
    clear:both !important;
  }

  hr {
    border:0 !important;
    background-color:#ccc !important;
    color:#ccc !important;
  }

  hr, table[border], table[border] td {
    border:0 !important;
  }

  .texts .Author {
    background:none !important;
    padding:0 !important;
  }
  
  .postarea {
    margin-top:25px !important;
  }
  
  .postarea a + div {
    margin-bottom:50px !important;
  }
  
  .theader {
    display:inline;
  }


/* hide */

  .adminbar + br + .adminbar,
  .adminbar + br,
  noindex + br + .adminbar,
  .adminbar + br + center,
  .texts,
  body>br + br,
  style + br,
  #postform tbody tr:first-child,
  #postform tbody tr:first-child + tr,
  #postform tbody tr:first-child + tr + tr + tr + tr + tr,
  #hidememenu + div + hr,
  font[size="2"] + br + font[size="1"],
  #hidememenu,
  #hidememenu + div,
  .theader + hr + div,
  .theader + hr + div + hr,
  #showme,
  #addthisBlock,
  #gbBlock,
  .addthisBlock,
  .gbBlock,
  .box-outer1 center:first-child a,
  #hidememenu br,
  span img[height="18"],
  hr + hr,
  .postarea>a:first-child,
  .rules,
  a[href="/zaibalo/"] {
    display:none !important;
  }
  
  br + .adminbar + br + .adminbar + br + .adminbar {
    display:block !important;
    margin:10px 0 15px !important;
  }


/* logo */

  .logo {
    line-height:48px !important;
  }

  div.logo {
    margin:20px 0 15px !important;
  }

  a.logo, p.logo {
    margin-top:4px !important;
  }


/* sagespoiler */

  font[color="red"] {
    color:#f62 !important;
  }

  .spoiler {
    background:#ddd !important;
    color:transparent !important;
  }

  .spoiler:hover {
    color:inherit !important;
  }

  .highlight .spoiler {
    background:#ccc !important;
  }


}
