/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

#commentsDiv, #logoTagDiv, #utilDiv, #gNavDiv, #gNavBottom, #actionsAreaDiv, #ratingAndStatsDiv, #asDiv, #vrParentDiv, #footerDiv, #ticker, #leaderboardAd, #footer, #copyright, #channelBrandCap, #channelVidsBody, #promotedVidsContainer, 
#nav-item-home, #nav-item-videos, #nav-item-channels, #nav-item-community,
#masthead .upload-button, #relatedVidsToggle, #embedDiv, #urlDiv, .wsHeading,   
.nav, #masthead .bar .leftcap, #masthead .bar .rightcap, #watch-active-sharing,
#channelBrandDiv, .hpContentBlock, #hpSideContent, #upload-button,
#masthead .search-type, #related-videos-panel>.expand-header,
#watch-promoted-container, #watch-actions-area, #watch-comments-stats, #more-from-panel, #watch-ratings-views,
#watch-embed-div, #watch-related-videos-panel+*, #watch-channel-brand-cap, #watch-related-vids-toggle,
#watch-related-vids-toggle+*, .user-info, .logo, #subscribeDiv, #watch-channel-brand-div, #top-margin-links-wrapper {

        display: none !important;

    }

#masthead #search-button, #masthead #search-term {

      float: left !important; 

    }

#masthead .search-bar {

    background: white !important; 

    }

#masthead .bar, #masthead .search-bar {
 width: 693px !important;
}

#vidTitle {
font-size: 23px !important;
height: 30px !important; 
margin-bottom: 5px !important; 
margin-top: 0px !important; 
}

#masthead {
width: 1000px !important;
height: 80px; 
left: 0px !important; 
margin-bottom: 0px !important; 
margin-top: 30px !important; 
}

#masthead .user-info {
float: right !important;
}

#relatedVidsBody scroll {
height: 292px !important; 
}

#masthead { height: 80px !important; }

#searchSectionHeader {
width: 550px; 
}

#searchSectionHeader, #mainContent, #hpMainContent {
margin: auto !important; 
}

#mainContent, #hpMainContent { float: none !important; }

#vidTitle {padding-top: 15px; padding-bottom: 2px; }

#masthead #search-form {padding-left: 20px !important;
Padding-top: 0px !important; }

/* Dark Grey Carbon background */
body {
	background: #333 url("data:image/jpg;base64,") repeat !important;
	color: #ddd !important;
}

/* Common to All links */
a:link { color: #A8A8A8 !important; text-decoration: none !important; }
a:visited { color: #33FFFF !important; }
a:hover { color: #ff6600 !important; }
:focus { outline: none; }
.eLink { border-bottom: 1px solid #666 !important; }

/* Text colours */
/*body,td,th,.p,p,a,div,.h { font: 11px Tahoma, sans-serif !important; }*/
span, div, p, li, td { color: #ddd !important; }
input *, textarea * { color: -moz-fieldtext !important; }

/* watch videos page */
#watch-channel-vids-div, #watch-customize-embed-div, #watch-video-response, .watch-comment-head, .watch-comment-head-hidden, .watch-comment-auth-head {
	background: #333 !important;
	border-color : #444 !important;
}

#wsSearchForm, #watch-more-from, #watch-related-vids-body, .watch-discoverbox-list-view { 
	background-color: #333 !important;
	border: 1px dotted #666 !important;
	border-right: none !important;	
}

/* campaign ads + post */
.search-type-selected, .watch-tab-contents { background: #282828 !important; }
#dems-header, #reps-header { background: #b1b1b1 !important; }

/* border - all */
#watch-actions-area, #watch-comments-stats, .watch-tab-contents, .expand-panel.small-expand-panel, .watch-comment-entry, #watch-promoted-container, hr, div, [class], [id], [style*="border"], tr,td {
	border-color : #444 !important;
}

/* Usericon/Video/Channel Thumbnails border */
.video-embed, .video-thumb-micro, .video-thumb-small, .video-thumb-medium, .video-thumb-normal, .video-thumb-large, .video-thumb-jumbo, .user-thumb-micro, .user-thumb-small, .user-thumb-medium, .user-thumb-partner, .user-thumb-large, .user-thumb-xlarge, .user-thumb-jumbo {
	background: transparent !important;
	border-color: #666 !important; 
}

/* thumbnail borders */
.feedmodule-thumbnail, [class$="WrapperInner"], .videobarthumbnail_white, .videobarthumbnail_gray, [class$="WrapperOuter"], [class^="vimg"] { border: transparent !important; }

/* border-bottom-dotted */
.watch-discoverbox-divider, .watch-shareopts-loginforgot { border-bottom: 1px dotted #555555 !important; }

/* Image Replacements @ 2008-07-30
----------------------------------*/
#masthead .logo img,
#logoHome,
#iconMail,
#iconMail.noMail,
.activeSharingGreen,
.activeSharingRed,
.globalFlag,
.flag_en_US,
#mastheadUploadBtn,
#debates_footer_img,
.commentDown,
.commentDownHover,
.commentDownHover:hover,
.commentDownOn,
.commentUp,
.commentUpHover,
.commentUpHover:hover,
.commentUpOn,
.spotlightDotOff,
.spotlightDotOn,
#masthead .nav-item.selected .leftcap,
#masthead .nav-item.selected .rightcap,
#masthead .nav-item .leftcap,
#masthead .nav-item .rightcap,
#masthead .nav-item,
#masthead .nav-item.selected,
#masthead .search-bar,
#masthead .bar .rightcap,
#masthead .bar .leftcap,
#relatedList,
#relatedNotList,
#relatedGrid,
#relatedNotGrid,
.arrowDown,
.arrowRight,
.expand-container .expand-header .arrow,
#i1,
#a1_i1:hover #i1, #i1:hover,
#i2,
#a2_i2:hover #i2, #i2:hover,
#i3,
#a3_i3:hover #i3, #i3:hover,
#i4,
#a4_i4:hover #i4, #i4:hover,
#ticker img[alt="footer-promo"],
.close-link img,
.picker-close-link img,
#btn-listview-on, #btn-listview-off,
#btn-gridview-on, #btn-gridview-off,
IMG[alt="Share"], IMG[alt="Favorite"], IMG[alt="Add to Playlists"], IMG[alt="Report video as inappropriate"],
IMG[class="arrow"],
.gaiaHelpBtn,
.edit-button-leftcap,
.edit-button-rightcap, .edit-button-text,
.watch-playlist-row-deleter
{
	/* Convert to base64 encode */	
	/* master image containing many smaller images */
	background-color: transparent !important;
}

#masthead .logo img {
	width: 132px !important;
	height: 63px !important;
	margin: 0 !important;
}

.flag_en_US { background-position: -162px -22px !important; }

/* Red "selected" dot */
img[src="/img/pic_selected_dot_9x9.gif"] {
	width: 0 !important;
	height: 9px !important;
	padding-left: 9px !important;
	background: url("data:image/png;base64,") top left no-repeat !important;
}

/* search result-drop down */
#search-options-container .dropdown-link a, #search-options-container .dropdown-link a.arrow {
	background-image: url(data:image/png;base64,) !important;
	background-color: transparent !important;
}

/* bar */
#search-options-container .dropdown-link a { height: 25px !important; background-position: -194px 0 !important; }

/* bar-hover */
#search-options-container .show-dropdown .dropdown-link a { background-position: -1px 0 !important; }

/* arrow */
#search-options-container .dropdown-link a.arrow { width: 26px !important; height: 25px !important; background-position: -345px 0 !important; }

/* arrow-hover */
#search-options-container .show-dropdown .dropdown-link a.arrow { background-position: -165px 0 !important; }

/* arrow - sub */
.img-php-down-arrow, .img-php-up-arrow { opacity: 0.7 !important; }
.img-php-down-arrow:hover, .img-php-up-arrow:hover { opacity: 1.0 !important; }

/* star/rating - new image */
.ratingVS-left,
.ratingVS-right,
img.ratingVS,
.ratingL-left,
.ratingL-right,
img.ratingL,
.icn_star_full_19x20,
.icn_star_half_19x20,
.icn_star_empty_19x20 {
background-image: url(data:image/png;base64,) !important;
        background-color: transparent !important;
}

/* larger thumbnails */
/*****#mainContentWithNav .video-thumb-medium,
#mainContentWithNav .video-thumb-medium img,
#mainContentWithNav .vimg120,
.v120vEntry .video-thumb-medium,
.v120vEntry .video-thumb-medium img {
  width: auto !important;
  height: auto !important;
  background-color: transparent !important;
}

.QLIcon:hover { opacity: 1.0 !important; }*/

textarea, input, select { opacity: 0.3 !important; }

textarea:hover, input:hover, select:hover, 
input[type="button"], input[type="submit"],
input:focus, textarea:focus, select:focus { 
  opacity: 1.0 !important; 
}

}
