css:
@-moz-document url-prefix("http://getpocket.com/") {
.page-reader #PKT_header {
  display: none;
}

.page-reader #page .pkt-nav {
  position: relative !important;
  top: -.5em;
  box-shadow: none;
  background: #fafafa;
  height: 40px !important;
}

body.page-reader {
  background: url(data:image/jpeg;base64,) center;
}

.page-reader .toolbar_reader.navigationItem.wrapper.active {
  position: relative;
  bottom: .8em;
}

.page-reader #page_reader.articleview {
  padding-top: 5.5em;
}

.page-reader .reader_content {
  background: #fefefe;
  padding: 2.1em;
  border-radius: 5px;
  box-shadow: 0 1px 5px rgba(0, 0, 0, 0.2);
  position: relative;
  max-width: 672px !important;
}

.page-reader .reader_head h1 {
  font-size: 1.3rem;
  font-weight: normal;
  color: #005c78;
  padding: 1em 0 2em
}

.page-reader .original {
  color: #fefefe;
  background-color: #62af43;
  padding: .3em .5em !important;
  border-radius: 2px;
  box-shadow: 0 1px 0 #4c8533;
  position: absolute;
  left: -.5em;
  top: 1em;
}
 
.page-reader .original a {
  color: #fefefe !important;
  font-size: 14px !important;
}

.page-reader .original::before {
  content: '';
  display: block;
  width: 0px;
  height: 0px;
  border-style: solid;
  border-width: 0 7px 7px 0;
  border-color: transparent #4c8533 transparent transparent;
  position: absolute;
  left: 0;
  top: 1.53em;
}

.page-reader .authorsdomain {
  height: 0;
  position: relative;
  bottom: 3.4em;
}

.page-reader .authorsdomain a {
  color: #4a8db8 !important;
  font-size: 14px !important;
  text-decoration: underline;
}
    
.page-reader .text_body a {
  color: #4a8db8 !important;
}

.page-reader .text_body blockquote {
  font-style: normal;
  border-color: #cfcfcf;
  background: #f8f8f8;
}

.page-reader strong, .page-reader b {
  background: rgba(242,242,111,.64);
  font-weight: normal;
}

.page-reader .text_body {
  font-size: 15px !important;
  margin-top: -1.3em;
}

}
