css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("duckduckgo.com") {
/*
Results :
    -0click box :   off
    -Site icons :   off
Color :
    -all to "default"
Look &_feel :
    -width :        wide
Layout :
    -Ads :          off (if you want)
*/

/*==Main styles==*/
    body, .highlight, #side{
        background:#FAFAFA!important;
    }
    #header_button_wrapper, #side_powered_by_wrapper, #side .button{
        display:none!important;
    }
    body{
        font-family: 'Open Sans','Droid Sans',arial,sans-serif!important;
    }
    a:visited{
        color:#09AD00!important;
    }
    a.url{
        color: #A55959!important;
    }
    #tagline_homepage a{
        color: #A55959!important;
    }
    a{
        color: #474747!important;
    }
/*==Header==*/
    #header{
        background-image:none!important;
        background-color:#FAFAFA!important;
    }
    #header_content{
        max-width:100%!important;
        width:100%!important;
        padding:0px!important;
    }
    #header_wrapper{
        position:relative!important;
    }
    h2{
        font-weight:bold!important;
    }
/*==Forms==*/
    #search_form_input_homepage, #search_form_input, #search_form_input_clear {
        box-shadow: none!important;
        border-color:#D7D7D7!important;
    }
    #search_form{
        margin: 25px auto 25px auto!important;
        float:none!important;
        width:828px!important;
        border:0px!important;
    }
    #search_form_input{
        width:719px!important;
    }
    
/*==Sidebar==*/
    #side{
        padding: 25px!important;
        border-left:1px #D7D7D7 dashed !important;
    }
    #side_suggestions{
        padding-top:0px!important;
    }
    #loading{
        width:16px!important;
        margin:10px auto!important;
    }
    
/*==Content==*/
    #content{
        margin-top:0px!important;
        padding-top:0px!important;
    }
    .results_links_deep{
        border:0px!important;
    }
}
