css:
@-moz-document domain("ixbt.com")
{
  html > body
  {
    padding-top: 0!important;
    background: none!important;
  }

  div.limiter
  {
    margin-top: 0!important;
    padding-top: 0!important;
    background: none!important;
  }

  span.age18
  {
    display: none!important;
  }
}

@-moz-document domain("ixbt.com")
{
  body > center > div, #col3 > a[href], img[src*="banner"], .b-blockadblock, div[class^="clickable_zone"], div[style] > script[src="//ixbt.com/js/ad_wrapper.js"] + div[style="text-align: right"], a[href^="http://www.ixbt.com/pda/"]
  {
    display: none!important;
  }

  #col3 > .front:nth-of-type(1)
  {
    margin: 0!important;
  }

  .b-ixbt_tv.index
  {
    margin-top: 0!important;
  }
/*Максимальная ширина статей*/
  td.main_content > div.content
  {
    max-width: 750px!important;
  }

  .right_col_banner_and_import
  {
    display: none!important;
  }
}

@-moz-document domain("ixbt.com")
{
  .news-mostpopular-block:after, .product .art_top_items li p::after
  {
    background: none!important;
  }

  #flow > div.product > ul[class^="art_top_items _fl"] > li > p
  {
    height: 6em!important;
  }

  #flow > div.product > ul[class^="art_top_items _fl"] > li
  {
    height: 100%!important;
  }

  .news-mostpopular-block, .news-mostpopular-block > div.helper
  {
    height: 3.75em!important;
    line-height: 1.25em!important;
  }
}

@-moz-document domain("forum.ixbt.com")
{
  tr > td.right_col
  {
    display: none!important;
  }

/* postauthor width */
td.t1[width="18%"],
td.t2[width="18%"] {
    width: 9% !important
}

td.t1[width="82%"],
td.t2[width="82%"]{
    width: 91% !important
}
}

