body > center:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1),
table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(1) > td.s[valign="top"]:nth-child(1) {
