css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.linux.org.ru") {
 body, html {
  background-color: #ffdfff !important;
  /*background-image: url(https://raw.github.com/bodqhrohro/LOR_ZOMG_PONIES/master/background.jpg) !important;*/
  background-image: url(https://www.linux.org.ru/zomg_ponies/img/background.jpg) !important;
  background-size: cover !important;
  background-attachment: fixed !important;
 }
 body {
  color: #000 !important;
 }
 #hd, table.head, body > footer, #ft, .nav, #query, h1, h1 a,
 #whois_userpic + div, #bd > ul, #bd > form, #bd > ol, #bd > h3,
 body > a[href="/"]:after, .ui-menu-item{
  background-color: rgba(255,255,255,0.7) !important;
  color: #4f0f4f !important;
  padding: 5px !important;
 }
 #hd .menu, #sitetitle{
 padding-top: 0em !important;
 }
 #bd {
 padding-left: 1em !important;
 padding-right: 1em !important;
 margin-left: 0em !important;
 margin-right: 0em !important;
 }
 #navPath {
    font-size: 1.5em !important;
 }
 #bd .nav li a {
 font-size: smaller !important;
 }
 a {
  text-decoration: underline;
 }
 a:hover{
  text-decoration: none !important;
 }
 a, .standardchange{
  color: #000040 !important;
 }
 h2, h3, h4, #whois_userpic{
  background-color: transparent !important;
  color: #000 !important;
 }
 .title{
  background: transparent !important;
  padding-left: 20px;
 }
 #hd, table.head{
  border-bottom: 1px red solid;
  margin-bottom: 5px;
 }
 #sitetitle{
  font-size: 0em !important;
  text-transform: none;
  background: transparent !important;
  color: #000;
  padding-left: 0px !important;
 }
 #sitetitle:after{
  display: inline-block !important;
  content: "PONY.ORG.RU";
  margin-right: 20px;
  font-size: 32px !important;
  text-transform: none;
  background: transparent !important;
  color: #000 !important;
 }
 img[src*="/black/"], #hdtux{
  display: none;
 }
 table.head td, div.head, #loginGreating{
  color: transparent;
 }
 body > a[href="/"]:after, table.head a[href="/"]:after{
  display: block;
  content: "PONY.ORG.RU";
  font-size: 2em;
  font-weight: bold !important;
 }
  table.head a[href="/"]:after{
  display: inline-block !important;
 }
 
 #sitetitle:before, table.head a[href="/"]:before{
  display: inline-block !important;
  /*background: url(https://raw.github.com/bodqhrohro/LOR_ZOMG_PONIES/master/twilight_logo.gif) no-repeat !important;*/
  background: url(https://www.linux.org.ru/zomg_ponies/img/twilight_logo.gif) no-repeat !important;
  background-size: 32px !important;
  width: 32px !important;
  height: 32px !important;
  margin-right: 2px !important;
  content: ""
 }
 .menu li a, #loginGreating a{
  text-decoration: none !important;
  font-size: 1em !important;
  color: #000 !important;
 }
 #sitetitle:hover,.menu li:hover, .menu a:hover, table.head a:hover{
  background-color: #ccccff;
  color: #000 !important;
  border-radius: 4px;
 }
 #loginGreating:hover {
 border-radius: 4px !important;
 color: transparent !important;
 }
 table.head a[href="/"]:hover {
 background-color: transparent !important;
 }
 #loginGreating a:before, .head a[href*="people"]:before{
  content: "дружбомагии тебе, ";
  font-size: 1em;
 }
 #main_events_count{
  background-color: #ff6666;
  border-radius: 15px;
  height: 24px;
  width: 24px;
  color: white;
  padding: 6px;
 }
 #main_events_count:empty{
  display: none;
 }
 .infoblock, .boxlet, .news {
 border: 1px solid gray;
  /* no !important there! */
 border-top: 1px solid white !important;
 }
 #bd .news {
 padding: 5px 10px !important;
 margin-bottom: 1em !important;
 }
 .boxlet h2 {
 text-align: left !important;
 font-size: 1.5em !important;
 margin-bottom: 0.25em !important;
 }
 .news h2 {
 font-weight: bold !important;
 font-size: 1.6em !important;
 }
 article, .boxlet, .infoblock,  .messages .msg, #warning-text, #tag-page-forum, fieldset, legend, #tag-page-news, #related-topics, .tags-first-letters{
  background-color: rgba(255,255,255,0.9) !important;
  border-top-color: white !important;
  border-left-color: white !important;
  border-radius: 5px !important;
  border-right-color: #bbbbbb !important;
  border-bottom-color: #bbbbbb !important;
  padding: 5px 10px !important;
  margin-top: 10px !important;
  margin-bottom: 10px !important;
 }
 .messages .msg:target {
  border-color: #400040 !important;
 }
 legend, .message-table a.secondary, span[itemprop="articleSection"] span {
 color: #000000 !important;
 }
 article h1, article h1 a{
  background: transparent !important;
 }
 div.msg-top-header span{
  background-color: #ffffdd;
  color: transparent;
 }
 div.msg-top-header a{
  color: #000 !important;
  text-decoration: none !important;
 }
 div.msg-top-header a:nth-child(odd){
  background-color: #ffdddd;
 }
 div.msg-top-header a:after, div.msg-top-header a:before{
  max-width: 0;
  max-height: 0;
  display: inline-block;
  content: "";
  vertical-align: bottom;
 }
 div.msg-top-header a:nth-child(odd):after{
  border-top: 11px solid #ffffdd;
  border-bottom: 11px solid #ffffdd;
  border-left: 11px solid #ffdddd;
 }
 div.msg-top-header a:nth-child(2n+3):before{
  border-top: 11px solid #ffdddd;
  border-bottom: 11px solid #ffdddd;
  border-left: 11px solid #ffffdd;
 }
 .tags{
  color: transparent !important;
 }
 .tag , .nav-buttons a, .tab-menu li, .group-label{
  background-color: #ffdddd !important;
  color: #000 !important;
  text-decoration: none !important;
 }
 .tag:nth-child(even), .nav-buttons li:nth-child(even) a, .tab-menu li:nth-child(even){
  background-color: #ffffdd !important;
 }
 code .tag, code .tag:nth-child(even){
 background-color: inherit !important;
 }
 .message-table tbody, .btn-default, .ui-widget-content{
  background-color: rgba(255,255,255,0.9) !important;
  color: #000 !important;
 }
 .ui-widget-content, .ui-state-focus{
  background-image: none !important;
  border: 0px !important;
 }
 .ui-state-focus{
  background-color: #ffffdd !important;
 }
 .message-table thead, .message-table tfoot{
  background-color: #ffdddd !important;
 }
 .message-table tr:hover{
  background-color: #ffddff !important;
 }
 .message-table td, .message-table th{
  border-color: #000 !important;
  color: #000 !important;
 }
 .page-number{
  background-color: #ff6666 !important;
  border-radius: 30px !important;
  border: 0 !important;
  padding: 5px !important;
 }
 a.page-number{
  color: #ccffcc !important;
 }
 strong.page-number{
  color: #ffffff !important;
 }
 span.page-number{
  color: #ccccff !important;
 }
 .penguin_progress span {
  background-repeat: repeat-x !important;
  height: 19px !important;
  /*background-image: url(https://raw.github.com/bodqhrohro/LOR_ZOMG_PONIES/master/rainbow.gif) !important;*/
  background-image: url(https://www.linux.org.ru/zomg_ponies/img/rainbow.gif) !important;
 }
 .userpic img, .nav {
  border-radius: 4px;
 }
 .code, pre, code {
  background-color: #400040 !important;
  color: #ffffdf !important;
 }
 .code, pre {
 border-radius: 5px !important;
 }
 pre a, code a, .code a{
 color: #CCCCFF !important;
 }
 pre {
 padding: 0.5em !important;
 }
 .icon-tag, .stars, .icon-tag-color, .icon-user-color, .icon-pin-color, .icon-reply-color {
  color: #4f0f4f !important;
 }
 .user-remark{
  color: #400000 !important;
 }
 #bd .btn-default, .tag-page-buttons a, .btn-primary {
 background-color: #CCCCFF !important;
 border-color: #7777FF !important;
 color: #000000 !important;
 }
 #bd .btn.btn-danger{
 background-color: #400040 !important;
 color: #FFFFFF !important;
 }
 pre .shebang,pre .comment,pre .template_comment,pre .javadoc{
 color: #DDDDDD !important
 }
 pre .keyword,pre .tag,pre .ruby .function .keyword, pre .function .keyword,pre .sub .keyword,pre .method,pre .list .title, pre .css .class{
 color: #FE6464 !important;
 }
 pre .string,pre .attribute .value,pre .cdata,pre .filter .argument,pre .attr_selector,pre .apache .cbracket,pre .date {
 color: #44F27D !important;
 }
 pre .function .title,pre .sub .identifier,pre .pi,pre .decorator,pre .ini .title{
 color: #BDBDFF !important;
 }
 pre .class .title,pre .smalltalk .class,pre .javadoctag,pre .phpdoc, pre .css .rule .keyword,pre .css .id{
 color:#8484FF !important;
 }
 pre .symbol,pre .number,pre .variable,pre .vbscript,pre .literal{
 color: #A0F974 !important;
 }
 pre .subst{
 color: #DACCFF !important;
 }
 pre .regexp{
 color: #FFD28C !important;
 }
 pre .number{
 color: #8AE183 !important;
 }
 pre .hexcolor{
 color: #E0A8E0 !important;
 }
 pre .ruby .constant{
 color: #F173CB !important;
 }
 pre .css .keyword{
 color: #BEE015 !important;
 }
 .search-hl {
 background-color: transparent !important;
 }
 .icon-comment {
 color: #400040 !important;
 font-size: 0.75em !important;
 }
 img[src="/img/good-penguin.jpg"]{
 width: 0px !important;
 height: 0px !important;
 padding-left: 280px !important;
 padding-top: 292px !important;
 /*background: url(https://raw.github.com/bodqhrohro/LOR_ZOMG_PONIES/master/pinkie_pie.png), no-repeat !important;*/
 background: url(https://www.linux.org.ru/zomg_ponies/img/pinkie_pie.png), no-repeat !important;
 }
 #ft:before{
  display: inline-block !important;
  /*background: url(https://raw.github.com/bodqhrohro/LOR_ZOMG_PONIES/master/derpy.gif) no-repeat !important;*/
  background: url(https://www.linux.org.ru/zomg_ponies/img/derpy.gif) no-repeat !important;
  background-size: 70px !important;
  width: 70px !important;
  height: 70px !important;
  float: left !important;
  content: ""
  }
 body{
  font-size: medium !important;
  }	
 body, h1, h2, h3, h4, h5, h6 {
  font-family: sans-serif !important;
 }
 .sign {
 margin-bottom: 0.5em !important;
 }
}
 /* SPECIAL PAGES ZONE! ADD USUAL RULES BEFORE OR AFTER IT! */
 @-moz-document url("http://www.linux.org.ru/about"), url("https://www.linux.org.ru/about"), url("http://www.linux.org.ru/forum/"), url("https://www.linux.org.ru/forum/"), url-prefix("http://www.linux.org.ru/help/"), url-prefix("https://www.linux.org.ru/help/") {
 #bd {
 background-color: rgba(255,255,255,0.7) !important;
 color: #4f0f4f !important;
 margin: 10px !important;
 border-radius: 2px !important;
 }
 h1, h2, #bd > h3, #bd > ol, #bd > ul {
 background-color: transparent !important;
 }
 }
 @-moz-document url("http://www.linux.org.ru/forum/"), url("https://www.linux.org.ru/forum/") {
 h1, #bd > ul {
 background-color: rgba(255,255,255,0.6) !important;
 border-radius: 5px !important;
 }
 }
	
@-moz-document regexp("https?://.*linux.org.ru/forum/.*/archive/"){
	#bd {
 background-color: rgba(255,255,255,0.7) !important;
 color: #4f0f4f !important;
 margin: 10px !important;
 border-radius: 2px !important;
 }
 }
 /* SPECIAL PAGES ZONE ENDS! */
