css:
@-moz-document domain("apps.facebook.com"), regexp("https?://www\\.facebook\\.com/(?!plugins/).*") {
 
/* i really want this to be global */

body {
color: #333;
line-height: 1.28;
text-align: left;
direction: ltr;
background-image: url(http://i41.tinypic.com/23sbqjt.jpg);
}

._4lh .fbTimelineScrubber a:hover, ._4lh .fbTimelineScrubber a:focus {
border-left-color: #05E8FC;
}

#blueBar {
background-color: #0E0E0E;
border-bottom: 3px solid #ffffff;
min-width: 981px;
position: relative;
z-index: 300;
background-image: url(http://i43.tinypic.com/2zo9eva.jpg);
}

#blueBar:hover {
background-color: #333535;

}

.fbJewel a.jewelButton:active, .fbJewel a.jewelButton:focus, .fbJewel a.jewelButton:hover {
background-color: #000000;
outline: none;
text-decoration: none;
border-radius: 7px;
}


#pageLogo a:hover, #pageLogo a:focus, #pageLogo a:active {
background-color: black;
border-radius:5px;
}


.slim #blueBar.fixed_elem {
left: 0;
right: 0;
top: 0;
height: 43px;
}

._s0:only-child {
display: block;
border-radius: 5px;
border: 2px solid #242323;
}

#fbTimelineHeadline .profilePic {
background-color: #3B5998;
border: 3px solid #747474;
border-radius: 2px;
height: 160px;
width: 160px;
}

._6dh ._1dsl {
border-bottom: 1px solid #6D6D6D;
padding: 12px 0 8px 12px;
}



.fbTimelineStickyHeader .back {
background-color: #ffffff;
border: 2px solid #747474;
border-radius: 0 0 2px 2px;
border-top: 0;
height: 47px;
position: absolute;
width: 849px;
border-bottom-left-radius: 7px;
border-bottom-right-radius: 7px;
box-shadow: 3px 27px 69px 1px rgba(0,0,0,1);
background-image: url(http://i43.tinypic.com/2zo9eva.jpg);
}

.fbNubFlyoutTitlebar {
background-color: #030303;
border: 1px solid rgba(79, 80, 82, 0.76);
border-bottom: none;
cursor: pointer;
border-top-left-radius: 7px;
border-top-right-radius: 7px;
background-image: url(http://i41.tinypic.com/264sx7s.jpg);
}

.fbNubFlyoutTitlebar:hover, .menuOpened .fbNubFlyoutTitlebar {
background-color: #3C3C3C;
}

._2yj .uiIconText:hover {
background: #070707;
border-bottom-right-radius: 7px;
border-bottom-left-radius: 7px;
}

#fbNotificationsJewel.west a.jewelButton {
background-image: url(http://i42.tinypic.com/25gqo80.jpg);
background-repeat: no-repeat;
background-size: auto;
background-position: 0 -220px;
}

a.jewelButton:focus, #fbMessagesJewel a.jewelButton:hover {
background-image: url(http://i42.tinypic.com/25gqo80.jpg);
background-repeat: no-repeat;
background-size: auto;
background-position: -50px -255px;
}


#fbMessagesJewel a.jewelButton {
background-image: url(http://i42.tinypic.com/25gqo80.jpg);
background-repeat: no-repeat;
background-size: auto;
background-position: -75px -220px;
}

#fbRequestsJewel a.jewelButton {
background-image: url(http://i42.tinypic.com/25gqo80.jpg);
background-repeat: no-repeat;
background-size: auto;
background-position: 0 -185px;
}

#fbRequestsJewel a.jewelButton:hover {
background-image: url(http://i42.tinypic.com/25gqo80.jpg);
background-repeat: no-repeat;
background-size: auto;
background-position: -75px -185px;
}

#fbNotificationsJewel.west a.jewelButton:hover {
background-image: url(http://i42.tinypic.com/25gqo80.jpg);
background-repeat: no-repeat;
background-size: auto;
background-position: -50px -220px;
}

.timelineLayout #globalContainer {
clear: both;
background-image: url(http://i41.tinypic.com/23sbqjt.jpg);
}

html ._4lh, ._4lh .fbTimelineScrubber, ._4lh .fbTimelineTimePeriod, ._4lh .fbTimelineSectionExpandPager .uiMorePagerLoader, ._4lh .fbTimelineCapsule li.anchorUnit:last-child, ._4lh .fbTimelineSectionLoading .loadingIndicator, ._4lh .timelineTourStarted .contextualBlind, ._4lh .lifeEventAddPhoto:hover {
background-image: url(http://i41.tinypic.com/23sbqjt.jpg);
}

.fbTimelineScrubber {
background-color: #e7ebf2;
padding: 8px 0 8px 1px;
top: 38px;
width: 122px;
z-index: 1;
background-image: url(http://i41.tinypic.com/23sbqjt.jpg);
font-size: 11px;
font-family: sans-serif;
color: white;
outline: none;
text-decoration: none;
}

._4lh .fbTimelineCapsule .timelineUnitContainer {
border-left: 1px solid #d3d6db;
border-right: 1px solid #d3d6db;
padding: 12px;
background-image: url(http://i43.tinypic.com/dgjo9d.jpg);


}

._70k {
clear: right;
padding-left: 201px;
width: 649px;
background-image: url(http://i44.tinypic.com/k20j1h.jpg);


}

.hasLeftCol #contentCol {
border-left: 1px solid #ccc;
margin-left: 181px;
min-height: 600px;
background-image: url(http://i41.tinypic.com/23sbqjt.jpg);
box-shadow: 10px 10px 53px -24px rgba(0,0,0,0.75);
}

.fbChatSidebar .fbChatOrderedList, .fbChatSidebar .fbChatTypeaheadView {
padding-top: 8px;
background-image: url(http://i43.tinypic.com/2dukzr8.jpg);
}

.uiScrollableArea.nofade .uiScrollableAreaWrap, .uiScrollableArea.fade .uiScrollableAreaWrap {
margin-right: -30px;
padding-right: 30px;
background-image: url(http://i43.tinypic.com/2dukzr8.jpg);
}

.UFIBlingBoxLikeIcon {
background-image: url(http://i39.tinypic.com/qs6ro1.jpg);
background-repeat: no-repeat;
background-size: auto;
background-position: 0 -35px;
display: inline-block;
height: 13px;
width: 15px;
}

.UFILikeIcon {
background-image: url(http://i39.tinypic.com/qs6ro1.jpg);
background-repeat: no-repeat;
background-size: auto;
background-position: 0 -35px;
display: block;
height: 13px;
width: 15px;
}

.UFIBlingBoxCommentIcon {
background-image: url(http://i39.tinypic.com/qs6ro1.jpg);
background-repeat: no-repeat;
background-size: auto;
background-position: 0 -18px;
display: inline-block;
height: 16px;
width: 16px;
}

}
