css:
@-moz-document url-prefix("http://www.nicovideo.jp/tag/"), url-prefix("http://www.nicovideo.jp/search/") {
.hidariue, [data-ads-header-banner], .searchOption .unavailable .notice {
  display: none !important;
}

.siteHeader > .inner {
  position: relative;
  right: 4.6em;
}

.column.main {
  padding-top: 1em !important;
}

.main .contentBody:first-of-type {
  display: none;
}

.container .inner {
  display: flex;
  flex-direction: column;
}

.container.columns .column {
  display: block;
  width: 100%;
  padding: 0;
}

.video.contentBody:not(.videoList01):not(.videoList02) > .list {
  display: flex;
  flex-flow: row wrap;
}

.video.contentBody:not(.videoList01):not(.videoList02) .item {
  float: none;
  background: #fefefe;
  flex: 0 0 15em;
  width: auto;
  border: 1px solid #cfcfcf;
  margin: 0 0 1em;
  box-shadow: 0 1px 5px #cfcfcf;
  position: relative;
}
.video.contentBody:not(.videoList01):not(.videoList02) .item:not(:nth-child(5n)) {
  margin-right: 0.8em;
}
.video.contentBody:not(.videoList01):not(.videoList02) .item:nth-child(4n+1) {
  clear: none;
}

.video.contentBody:not(.videoList01):not(.videoList02) .itemTime {
  color: #313131;
  padding: 0.3em 0 0.3em;
  margin-bottom: 0;
  text-indent: 0.5em;
}

.video.contentBody:not(.videoList01):not(.videoList02) .itemThumb {
  width: 194px;
  height: 133px;
}

.video.contentBody:not(.videoList01):not(.videoList02) .itemThumb + .balloon.recent.active {
  display: none !important;
}

.video.contentBody:not(.videoList01):not(.videoList02) .itemThumbWrap:before {
  background: none !important;
}

.video.contentBody:not(.videoList01):not(.videoList02) .videoLength {
  font-size: 0.8rem;
  color: #cfcfcf;
  background: rgba(0, 0, 0, 0.6);
  bottom: 112px;
  right: 1px;
}

.video.contentBody:not(.videoList01):not(.videoList02) .itemThumbBox {
  width: 193px !important;
  height: 134px !important;
}
.video.contentBody:not(.videoList01):not(.videoList02) .itemThumb img {
  width: 199px !important;
  max-width: 199px !important;
  margin-top: -19px !important;
  margin-left: 0 !important;
  height: 152px !important;
  max-height: 152px !important;
}

.video.contentBody:not(.videoList01):not(.videoList02) .itemContent {
  padding: 0;
}

.video.contentBody:not(.videoList01):not(.videoList02) .itemTitle {
  font-size: 1rem;
  line-height: 1.2;
  padding: 0.5em;
}

.video.contentBody:not(.videoList01):not(.videoList02) .item .wrap {
  width: calc(100% - 1em);
  background: rgba(0, 0, 0, 0.6);
  padding: 0.3em 0.5em 0.1em;
  position: absolute;
  top: 9.2em;
  z-index: 100;
}
.video.contentBody:not(.videoList01):not(.videoList02) .item .wrap .list {
  display: flex;
  flex-flow: row wrap;
}
.video.contentBody:not(.videoList01):not(.videoList02) .item .wrap .count {
  color: #cfcfcf;
  flex: 49%;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
.video.contentBody:not(.videoList01):not(.videoList02) .item .wrap .value {
  color: #cfcfcf;
}
.video.contentBody:not(.videoList01):not(.videoList02) .item .wrap .value a {
  color: inherit;
}

.searchOption {
  display: block !important;
  border: none;
  position: fixed;
  top: 4.3em;
  right: -3em;
  z-index: 10000;
  box-shadow: 0 1px 5px #cfcfcf;
}

.searchOption .unavailable .searchOptionNotice {
  top: 8.8em;
  right: 3.3em;
}

.searchOption .optionList ul li {
  margin-bottom: 0.5em;
}

.searchOption .optionList ul .active {
  background: rgba(66, 139, 202, 0.15);
  color: #515151;
}

.musicDl h3 {
  width: 8em;
  white-space: nowrap;
  overflow: hidden;
}

.column700-300 .sub {
  background: none;
  border-top: 1px solid #cfcfcf;
}

}
