css:
@-moz-document url-prefix("http://stri.ms") {
body {
	background-color: #222 !important;
  	color:#eee !important;
}
.brand:nth-child(3){
	color:#0088cc !important;
}
.well{
	background-color: #222 !important;
  	color:#eee !important;
  	border-radius: 0 !important;
}

hr {
	color:#333 !important;
    border-top: 1px solid #333 !important;
    border-bottom: 1px solid #333 !important;
}

.span2 {
	background-color: #222 !important;
  	border-radius: 0 !important;
  	border: 1px solid #000 !important;
}

.span2 ul li {
	background-color: #222 !important;
  	border-radius: 0 !important;
  	border: 0 !important;
  	color:#fff !important;
}
.span2 ul li a {
	color:#fff !important;
}
.span2 ul li a:hover {
	color:orange !important;
    text-decoration:none !important;
}
.span2 h5 {
	color:#0088cc !important;
}
li.media div{
  	padding:5px;
	background-color: #333 !important;
}
li.media div h4.media-heading a{
  	color:orange !important;
}
li.media div h4.media-heading a:hover{
  	text-decoration:underline !important;
}
  
.content-row{
	background: #333 !important;
  	color:#eee !important;
  	border-radius: 0 !important;
 	border-left:0 !important;
	-webkit-box-shadow:  1px 2px 2px 1px rgba(0, 0, 0, 1) !important;   
    box-shadow:  1px 2px 2px 1px rgba(0, 0, 0, 1) !important;
}
.span7 .media-body a {
	color:orange !important;
}
.media-body p a:nth-child(2) {
	color:#0088cc !important;
}
.media-body p a:nth-child(3) {
	color:#aaa !important;
}
.media-body a:nth-child(3){
	color:#0088cc !important;
}
.media-body h6 a:nth-child(2), .media-body h6 a:nth-child(3){
	color:#bbb !important;
}
ul.content{
	background-color: #222 !important;
    border-radius: 0 !important;
 	border:1px solid #000 !important;
  
}
p.comment_text{
	border-top: 1px solid #888 !important;
}
p.comments_addons a {
	color:orange  !important;
}

.media .media-body p.comment_text a {
	color:orange !important;
}

.btn{
  	background-color: #222 !important;
	border-radius: 0 !important;

 	border:1px solid #111 !important;
  	background-image: none !important;
	text-shadow: 0 1px 1px #000 !important;

}
.btn:hover{
  	background-color: #ccc !important;
    color:orange !important;
}
.addContentButton{
	color:#eee !important;
    text-shadow: 0 1px 1px #000 !important;
}
.btn-group .btn{
	border-radius: 0 !important;
  	background-color: #444 !important;
  	color:orange !important;

  
}
.btn-group .btn:first-child{
	border-bottom-left-radius:0 !important;
  	border-top-left-radius:0 !important;
}
.btn-success:hover {
	background-color:#51a351 !important;
}
.btn-danger:hover{
  background-color:#bd362f !important;
}
.content-row .voting button{
	width: 45px !important;
  	height:35px !important;
}
.media, .media-heading{
	border:0 !important;
}
.pagination ul li:first-child a {
	border-radius: 0 !important;
}

.pagination ul li:last-child a {
	border-radius: 0 !important;
}
    
.pagination ul li a{
  	
	background-color: #333 !important;
}
textarea {
	background-color:#555 !important;
    border-radius: 0 !important;
    color:#fff !important;
}
}
