css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain('ps3hax.net'), url-prefix('http://ps3hax.net/forum.php') {
body{
	background:transparent url('http://i.imgur.com/XYhMGdA.png') no-repeat top left !important;
}
.toplinks {

padding-right: 200px !important;
}



.tabs li{
	list-style:none;
	float:left;
	line-height:27px;
	border-right:1px solid #CAD9EA;
	height:27px;
	font-size:9px!important;
	cursor:pointer
}
.custom_banner{
	width:699px;
	min-height:137px;
	float:left !important;
}
.banner_register{
	width:699px;
	min-height:137px;
	float:left!important;
	background:transparent url('http://www.ps3hax.net/PS3HAX/Custom_images/banner-register.png') no-repeat
}
.ps3_ps4{
	background:transparent url('http://www.ps3hax.net/PS3HAX/Custom_images/ps3-ps4.png') no-repeat;
	position:absolute;
	right:300px!important;
	width:379px;
	z-index:100;
	min-height:184px
}
.doc_header{
	margin:0px 0;
	width:1000px
}

#wrapper{
	background:#fff;
	-moz-border-radius-bottomleft:6px;
	-moz-border-radius-bottomright:6px;
	-webkit-border-bottom-left-radius:6px;
	-webkit-border-bottom-right-radius:6px;
	border-bottom-left-radius:6px;
	border-bottom-right-radius:6px;
	-moz-border-radius-topleft:6px;
	-moz-border-radius-topright:6px;
	-webkit-border-top-left-radius:6px;
	-webkit-border-top-right-radius:6px;
	border-top-left-radius:6px;
	border-top-right-radius:6px;
	margin:0px 0px !important;
	width:1000px !important;
}
.footer{
	margin:0 0px!important;
	min-height:27px;
	-moz-border-radius-bottomleft:4px;
	-moz-border-radius-bottomright:4px;
	-webkit-border-bottom-left-radius:4px;
	-webkit-border-bottom-right-radius:4px;
	border-bottom-left-radius:4px;
	border-bottom-right-radius:4px;
	-moz-border-radius-topleft:4px;
	-moz-border-radius-topright:4px;
	-webkit-border-top-left-radius:4px;
	-webkit-border-top-right-radius:4px;
	border-top-left-radius:4px;
	border-top-right-radius:4px;
	width:1000px!important;
}
}
