#wikipage-inner input:focus:invalid:focus, #wikipage-inner textarea:focus:invalid:focus, #wikipage-inner select:focus:invalid:focus {  border-color: #e9322d !important;  -webkit-box-shadow: 0 0 6px #f8b9b7 !important;  -moz-box-shadow: 0 0 6px #f8b9b7 !important;  box-shadow: 0 0 6px #f8b9b7 !important; }
#wikipage-inner table {  max-width: 100% !important;  background-color: transparent !important;  border-collapse: collapse !important;  border-spacing: 0 !important; }
#wikipage-inner html, #wikipage-inner  body {  height: 100% !important; }
.key-command-table, .key-command-table caption {
	width:250px;
}

.key-command-table {
  position:relative;
  margin-bottom:1em;
  /*margin-right:auto;
  margin-left:auto;*/
}

.key-command-table:before {
  content:"";
  top:-1px;
  left:-1px;
  position:absolute;
  display:block;
  width:100%;
  height:100%;
  border: 1px solid rgb(221, 221, 221);;
  border-radius: 4px 4px 4px 4px;
  box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.05);
}

.key-command-table caption {
  background-color: rgb(245, 245, 245);
  padding: 10px 0;
  /*font-size: 16px;*/
}

.key-command-table td:last-child {
	text-align:right;
	white-space:nowrap;
}

.key-command-table td {
	border-top: 1px solid rgb(221, 221, 221);
	padding: 10px 10px 9px;
    line-height: 18px;
	/*min-width:25%;*/
}

.key {
	display: inline-block;
	min-width: 1em;
	padding: .2em .3em;
	font: normal .85em/1 "Lucida Grande", Lucida, Arial, sans-serif;
	text-align: center;
	-webkit-border-radius: .3em;
	border-radius: .3em;
	color: rgb(50, 50, 50);
	box-shadow: inset 0 0 .1em rgb(200, 200, 200), 0 .1em 0 rgb(130, 130, 130), 0 .11em 0 rgba(0, 0, 0, .4), 0 .1em .1em rgba(0, 0, 0, .9);
	margin:0 .1em;
	transform:translate(0,-.1em);
}
}
