css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.linux.org.ru/wiki/"),
               url-prefix("http://www.linux.org.ru/wiki/") {
body {
	background: #2E3436 !important;
        background-image: inherit !important;
	color: #BABDB6 !important;
	margin: 0 !important;
	padding: 0 !important;
}
body, input, select {
	font: 95% sans-serif, tahoma !important;
}
a {
	text-decoration: none !important;
	color: #729FCF !important;
}

table {
	background-color: #272C2D !important;
	border-color: #BABDB6 !important;
}



a:hover {
	border-bottom: 0px !important;
}
a.logo {
	border: 0 !important;
}
a.externallink:after {
	content:url("https://www.linux.org.ru/wiki/images/external.png") !important;
}
a.interwikilink:after {
	content:url("https://www.linux.org.ru/wiki/images/external.png") !important;
}
a.edit {
	color: #aa1111 !important;
	border-color: #aa1111 !important;
}
a.edit:hover {
	border-bottom: 1px dotted #aa1111 !important;
}
a.redirect {
	font-size: 150% !important;
	text-decoration: none !important;
	margin-left: 20px !important;
}
a.redirect:before {
	content:url("https://www.linux.org.ru/wiki/images/redirect-arrow.png") !important;
}
p {
	margin: .4em 0 .5em 0 !important;
	line-height: 1.5em !important;
}
h1, h2, h3, h4, h5 {
	color: #BABDB6 !important;
	margin: 0 !important;
	padding: 0.5em 0em 0.2em 0em !important;
	text-align: left !important;
}
h1, h2 {
	margin-bottom: 0.5em !important;
}
h3, h4, h5 {
	margin-bottom: 0.2em !important;
}
h1 {
	border-bottom: 1px solid #D8D8E7 !important;
	font-size: 200% !important;
	font-weight: normal !important;
}
h2 {
	border-bottom: 1px solid #D8D8E7 !important;
	font-size: 175% !important;
	font-weight: normal !important;
}
h3 {
	font-size: 150% !important;
	font-weight: normal !important;
}
h4 {
	font-size: 125% !important;
	font-weight: bold !important;
}
h5 {
	font-size: 110% !important;
	font-weight: bold !important;
}
pre {
	background-color: #2E3436 !important;
	border: 0px !important;
	color: white !important;
	border-radius: 1em !important;
	font-size: 110% !important;
	line-height: 1.1em !important;
	padding: 1em !important;
}
code {
	color: white !important;
	font-size: 120% !important;
}
dt {
	font-weight: bold !important;
	margin-bottom: .1em !important;
}
dl {
	margin-top: .2em !important;
	margin-bottom: .5em !important;
}
dd {
	line-height: 1.5em !important;
	margin-left: 2em !important;
	margin-bottom: .1em !important;
}
ul {
	line-height: 1.5em !important;
	list-style-type: square !important;
	list-style-image: url(https://www.linux.org.ru/wiki/images/bullet.gif) !important;
	margin: .3em 0 0 1.5em !important;
	padding: 0 !important;
}
ol {
	line-height: 1.5em !important;
	margin: .3em 0 0 3.2em !important;
	padding: 0 !important;
	list-style-image: none !important;
}
li {
	margin-bottom: .1em !important;
}
textarea {
	width: 99% !important;
}
fieldset {
	padding: 0 7px 7px !important;
	border: 1px solid #D8D8E7 !important;
	margin-top: 10px !important;
}
legend {
	padding: 0.5em !important;
	font-weight: bold !important;
}
.red {
	font-weight: bold !important;
	color: #f00 !important;
}
.green {
	font-weight: bold !important;
	color: #090 !important;
}
div.message {
	margin: 1.5em 0 1.5em 0 !important;
	padding: 0 5px !important;
}
div.submenu {
	margin: 1.0em 6em 0.8em 6em !important;
	text-align: center !important;
}
.clear {
	clear: both !important;
}
.hidden {
	display: none !important;
}

/* global positioning */

#wiki-page {
	font-size: 100% !important;
	color: black !important;
	padding: 0 5px !important;
}
#wiki-navigation {
	position: absolute !important;
	top: 5px !important;
	left: 5px !important;
	font-size: 90% !important;
	padding: 5px 5px 5px 0 !important;
	vertical-align: top !important;
	width: 180px !important;
}
#wiki-content {
	position: relative !important;
	margin-left: 185px !important;
	font-size: 100% !important;
	vertical-align: top !important;
}
#wiki-footer {
	color: gray !important;
	font-size: 80% !important;
	margin-left: 185px !important;
	padding: 15px 10px !important;
}

/* main content area */

#contents {
	background: #272C2D !important;
	padding: 14px !important;
	border: 1px solid #272C2D !important;
	border-radius: 1em !important;
	color: #BABDB6 !important;
	list-style-type: disc !important;
	padding-bottom: 25px !important;
	
}
#contents-header {
    background: #272C2D !important;
	margin-bottom: 0.1em !important;
}
#contentSub {
	color: #7d7d7d !important;
	margin: 0 0 1em 1em !important;
}

/* navbar elements */

#logo {
	border: 0 !important;
	margin: 8px 0 0 0 !important;
	text-align: center !important;
	vertical-align: middle !important;
}
.portlet {
	background: #272C2D !important;
	border: 1px solid #272C2D !important;
	border-radius: 1em !important;
	font-size: 90% !important;
	margin: 16px 0 8px !important;
	overflow: hidden !important;
	padding: 0 8px 8px !important;
}
.portlet h5 {
	font-size: 90% !important;
	font-weight: normal !important;
	padding: 5px 0 0 !important;
}
#nav-menu {
	border-bottom: 0 !important;
	margin-top: 2px !important;
	padding: 0 !important;
}
#nav-menu hr {
	display: none !important;
}
#nav-menu ul {
	padding: 8px !important;
	list-style-type: none !important;
	list-style-image: none !important;
	border-bottom: 1px solid #D8D8E7 !important;
	margin-left: 0 !important;
}
#nav-search {
	text-align: center !important;
}
#nav-search form {
	padding: 0 !important;
	margin: 0 !important;
}
#nav-search br {
	padding-top: 4px !important;
}
#nav-search input {
	margin-top: 8px !important;
	background-color: white !important;
	border: 1px solid #D8D8E7 !important;
}
#nav-search input[type="text"] {
	width: 150px !important;
}

/* diff page */

#diff_heading {
	margin-top: 10px !important;
	width: 98% !important;
}
#diff_heading td {
	text-align: center !important;
	vertical-align: top !important;
	width: 50% !important;
}
#left_revision, #right_revision {
	font-weight: bold !important;
}
table#diff {
	table-layout: fixed !important;
	width: 98% !important;
}
table#diff col.diff-marker {
	width: 2% !important;
}
table#diff col.diff-content {
	width: 48% !important;
}
.diff-delete {
        color: black !important;
	background: #eeee88 !important;
	font-weight: bold !important;
	padding: 0.1em !important;
	overflow: hidden !important;
}
.diff-add {
        color: black !important;
	background: #88ee88 !important;
	font-weight: bold !important;
	padding: 0.1em !important;
	overflow: hidden !important;
}
.diff-unchanged {
	padding: 0.1em !important;
	overflow: hidden !important;
}
.diff-indicator {
	font-weight: bold !important;
	padding: 0.1em !important;
	text-align: right !important;
	width: 7px !important;
}
.diff-line {
	padding: 0.5em 0.1em 0.1em 0.1em !important;
	font-weight: bold !important;
}
.diff-entry {
	margin: 1px !important;
	padding: 1px !important;
}
.diff-change {
	font-weight: bold !important;
	color: red !important;
}

/* topic elements */

#toc {
	border: 1px solid #D8D8E7 !important;
	margin: 0.2em auto 0.2em 0em !important;
	padding: 0.5em 0.8em 0.5em 0.8em !important;
	background: #272C2D !important;
}
#toc ul {
	list-style-type: none !important;
	list-style-image: none !important;
	margin-left: 0 !important;
}
#toc ul ul {
	margin-left: 1em !important;
}
#category-index {
	border: 1px solid #aaa !important;
	padding: 10px 5px 10px 5px !important;
	margin-top: 1em !important;
	clear: both !important;
}
#topic-file-download {
	border: 1px solid #aaa !important;
	background-color: #f9f9f9 !important;
	padding: 10px 5px 10px 5px !important;
	margin-top: 1em !important;
	clear: both !important;
}

/* tab menu */

#tab-menu {
margin-left: 1em !important;
}

.tab-menu a {
color: #FCAF3E !important;
}

ul.tab-menu {
	display: inline-block !important;
	list-style: inherit !important;
	list-style-type: none !important;
}
ul.tab-menu li {
	color: #AAAAAA !important;
	background-color:  #272C2D !important;
	border: 1px solid #171B2D !important;
	float: left !important;
	border-bottom: none !important;
	min-width: 40px !important;
	text-align: center !important;
}
ul.tab-menu li.active {
	margin-bottom: -1px !important;
	border-bottom: 1px solid #272C2D !important;
	font-weight: bold !important;
}
ul.tab-menu li.active a:hover {
	border-bottom: none !important;
}
ul#tab_menu {
	padding: 0 !important;
	margin: 0 !important;
	margin-left: 6px !important;
}
ul#tab_menu li {
	padding: 0 0.5em !important;
	margin: 0 !important;
	margin-right: 4px !important;
	font-size: 85% !important;
}
ul#tab_menu li.active {
	font-size: 90% !important;
}
ul#tab_submenu {
	margin: 10px 0 0 !important;
}
ul#tab_submenu li {
	background-color: #f9f9f9 !important;
	padding: 5px 7px !important;
	margin: 0 0 0 8px !important;
/*	-moz-border-radius-topleft: 5px !important;*/
	-webkit-border-top-left-radius: 5px !important;
/*	-moz-border-radius-topright: 5px !important;*/
	-webkit-border-top-right-radius: 5px !important;
}
ul#tab_submenu li.active {
	background-color: #fff !important;
}
.submenu-tab-content {
	background: white !important;
	padding: 14px !important;
	padding-bottom: 25px !important;
	border: 1px solid #D8D8E7 !important;
}

/* user menu */

#user-menu {
	float: none !important;
	margin-bottom: 10px !important;
	background-color: #272C2D !important;
	padding-bottom: 1em !important;
	padding-top: 1em !important;
	padding-left: 0 !important;
	padding-right: 0 !important;
	width:100% !important;
	margin-right: 2em !important;
	text-align: center !important;
	border-radius: 10px !important;
	margin-bottom: 20px !important;
}

#user-menu li {
	display: inline !important;
	list-style-type: none !important;
	list-style-image: none !important;
	padding: 0 0.8em !important;
	font-size: 90% !important;
}

#user-menu a {
 color: #E9B96E !important;
 font-size: 1.2em !important;
 text-decoration: none !important;
}



/* footer */

#footer-logo {
	font-size: 0.9em !important;
	padding-top: 10px !important;
	text-align: right !important;
}
#footer-custom {
	font-size: 0.9em !important;
	text-align: center !important;
}

/* recent changes, user contributions, history */

li.minorchange {
	color: #777777 !important;
}

li.deletechange {
	color: #770000 !important;
}
li.importchange {
	color: #007777 !important;
}
li.movechange {
	color: #777777 !important;
}
li.undeletechange {
	color: #007777 !important;
}
span.edit-comment {
	font-style: italic !important;
}
span.section-link {
	color: #999 !important;
}
span.section-link a {
	color: #333 !important;
}

/* topic pages */

#content-article {
	padding: 10px 0 10px 0 !important;
}
#content-article pre {
	overflow: hidden !important;
}
.editsection {
	font-size: 67% !important;
	float: right !important;
	margin-left: 5px !important;
	position: relative !important;
}
#shared-image-message {
	border: 1px solid red !important;
	font-size: 125% !important;
	font-weight: bold !important;
	margin: 10px 50px !important;
	padding: 10px !important;
	text-align:center !important;
}

/* images */

img.wikiimg {
	border: 0 !important;
}
a.wikiimg {
	border: 0 !important;
}
img.thumbborder {
	border: 1px solid #ddd !important;
}
div.thumbcaption {
	text-align:left !important;
	border: none !important;
	font-size:94% !important;
	line-height:1.4em !important;
	padding:3px !important;
}
div.floatleft {
	border: none !important;
	clear:left !important;
	float:left !important;
	margin: 0 0.5em 0.5em 0 !important;
	position:relative !important;
}
div.floatright {
	border: none !important;
	clear:right !important;
	float:right !important;
	margin: 0 0 0.5em 0.5em !important;
	position:relative !important;
}
div.center {
	text-align:center !important;
	width:100% !important;
}
div.center * {
	margin-left:auto !important;
	margin-right:auto !important;
}
div.tright {
	border: none !important;
	margin: 0.5em 0 0.8em 1.4em !important;
	clear:right !important;
	float:right !important;
}
div.thumb {
	border: none !important;
	margin-bottom:0.5em !important;
	width:auto !important;
}
div.thumbinner  {
	background-color:#f9f9f9 !important;
	border: 1px solid #ccc !important;
	font-size:94% !important;
	overflow:hidden !important;
	padding: 3px !important;
	text-align:center !important;
	min-width:10px !important;
}
table.gallery {
	border: 1px solid #ccc !important;
	margin: 2px !important;
	padding: 2px !important;
	background-color: white !important;
}
table.gallery caption {
	font-weight: bold !important;
	padding-bottom: 5px !important;
}
table.gallery td {
	background-color: #f9f9f9 !important;
	border: solid 2px white !important;
	text-align: center !important;
	vertical-align: top !important;
}
table.gallery-cat td {
	vertical-align: middle !important;
	padding: 2px !important;
}
img.gallery {
	border: 1px solid #bbbbbb !important;
	padding: 0.3em !important;
}
div.gallerytext {
	font-size: 94% !important;
	overflow: hidden !important;
	padding: 2px 4px
}
div.gallerytext p {
	line-height: normal !important;
	margin: auto !important;
}
div.gallerybox {
	margin: 2px !important;
}
table.gallery-cat div.gallerybox {
	width: 155px !important;
}
#edit-toolbar {
	margin: 3px 0 !important;
}
.toolbar-group {
	/*-moz-border-radius:5px !important;*/
	-webkit-border-radius: 5px !important;
	background-color: #e9e9e9 !important;
	border-radius: 5px !important;
	display: inline-block !important;
	margin: 3px 5px 3px 0 !important;
	padding-left: 3px !important;
}
.toolbar-button a {
	border-radius: 3px !important;
	cursor: default !important;
	display: inline-block !important;
	outline: none !important;
	padding: 2px 3px !important;
}
.toolbar-button {
	border-right: 1px solid #fff !important;
	cursor: pointer !important;
	display: inline-block !important;
	padding: 2px 2px 2px 0 !important;
	/*-moz-border-radius: 3px !important;*/
	-webkit-border-radius: 3px !important;
}
.toolbar-button a:hover, .toolbar-button a:focus, .toolbar-button a:active {
	background-color: #bfd1ff !important;
	border-bottom: none !important;
}
.toolbar-icon {
	background: url(https://www.linux.org.ru/wiki/images/toolbar-icons.png) 0 0 no-repeat !important;
	cursor: default !important;
	display: inline-block !important;
	height: 22px !important;
	width: 22px !important;
}
#button-bold .toolbar-icon {
	background-position: 0 -66px !important;
}
#button-italic .toolbar-icon {
	background-position: 0 -88px !important;
}
#button-underline .toolbar-icon {
	background-position: 0 -110px !important;
}
#button-strike .toolbar-icon {
	background-position: 0 -132px !important;
}
#button-subscript .toolbar-icon {
	background-position: 0 -154px !important;
}
#button-superscript .toolbar-icon {
	background-position: 0 -176px !important;
}
#button-nowiki .toolbar-icon {
	background-position: 0 0 !important;
}
#button-orderedlist .toolbar-icon {
	background-position: 0 -198px !important;
}
#button-unorderedlist .toolbar-icon {
	background-position: 0 -220px !important;
}
#button-indent .toolbar-icon {
	background-position: 0 -242px !important;
}
#button-wikilink .toolbar-icon {
	background-position: 0 -264px !important;
}
#button-link .toolbar-icon {
	background-position: 0 -22px !important;
}
#button-image .toolbar-icon {
	background-position: 0 -286px !important;
}
#button-signature .toolbar-icon {
	background-position: 0 -44px !important;
}
#button-hr .toolbar-icon {
	background-position: 0 -308px !important;
}
#button-heading1 .toolbar-icon {
	background-position: 0 -330px !important;
}
#button-heading2 .toolbar-icon {
	background-position: 0 -352px !important;
}
#button-heading3 .toolbar-icon {
	background-position: 0 -374px !important;
}
table.categories {
	width: 100% !important;
}
table.categories td {
	vertical-align: top !important;
	width: 33% !important;
}

/* search results */

.highlight {
	background:yellow !important;
}
.searchresult {
	background-color: #dcdcdc !important;
	margin: 1em 0 0 0 !important;
}
.searchsummary {
	margin: 0.5em 0 0.7em 0 !important;
}
#searchpower {
	font-size: 90% !important;
	font-style: italic !important;
	margin: 1em 0 1em 0 !important;
}
#searchhints {
	margin: 1.5em 0 1.5em 0 !important;
}

/* form styles */

.formhelp {
	font-size: 90% !important;
	color: #5f5f5f !important;
	clear: both !important;
}
.darkbg {
	background-color: #d8d8e7 !important;
}
.translationElement {
	float: left !important;
	vertical-align: top !important;
	padding: 3px !important;
	width: 49% !important;
	overflow: hidden !important;
}
textarea.translation {
	width: 100% !important;
	overflow:auto !important;
	height:5em !important;
}
textarea.medium {
	width: 400px !important;
	height: 60px !important;
}
table.wiki-admin {
	border: 1px solid #fff !important;
	border-collapse: collapse !important;
	border-spacing: 0 !important;
	margin: 0 auto !important;
	width: 99% !important;
}
table.wiki-admin th {
	background-color: #d8d8e7 !important;
	border: 1px solid #fff !important;
	text-align: center !important;
	padding: 5px !important;
}
table.wiki-admin th.first {
	width: 250px !important;
}
table.wiki-admin td {
	border-bottom: 1px solid #fff !important;
	padding: 5px !important;
}
.noborder {
	border: none !important;
}
.center {
	text-align: center !important;
}
div.row {
	clear: right !important;
	padding: 5px 7px !important;
}
.admin div.row {
	background-color: #e9e9e9 !important;
	border-top: 1px solid #fff !important;
}
div.row span {
	display: inline-block !important;
	vertical-align: top !important;
	text-align: left !important;
	padding-left: 5px !important;
}
div.row label {
	display: inline-block !important;
	text-align: left !important;
	width: 240px !important;
	font-weight: bold !important;
}
div.row span.form-button {
	margin-left: 240px !important;
}
div.row .formhelp {
	padding-top: 3px !important;
}
div.rowhelp {
	border: 1px solid #e9e9e9 !important;
	margin: 10px !important;
	padding: 5px 10px !important;
}
.translationElement label {
	vertical-align: top !important;
	padding: 3px !important;
	width: auto !important;
	overflow: hidden !important;
}
div.row label.nonblock {
	display: inherit !important;
	text-align: inherit !important;
	width: auto !important;
	font-weight: inherit !important;
}
div.callout {
	border: 1px solid #e9e9e9 !important;
	margin: 10px !important;
	padding: 10px !important;
}
div.callout input {
	margin-right: 20px !important;
}
.expander {
	padding: 5px !important;
}
.expander-open {
	height: auto !important;
}
.expander > label {
	cursor: pointer !important;
	font-weight: bold !important;
}
.expander > label:before {
	content:url("https://www.linux.org.ru/wiki/images/arrow-right.png") !important;
}
.expander-open > label:before {
	content:url("https://www.linux.org.ru/wiki/images/arrow-down.png") !important;
}
.expander > ul {
	display: none !important;
	margin-top: 10px !important;
}
.expander-open > ul {
	display: block !important;
}
.captcha {
	padding: 5px 0 !important;
}
.captcha-label {
	padding: 5px 0 !important;
}
#manage div.row label, #move div.row label, #register div.row label {
	width: 180px !important;
}
#manage div.row span.form-button, #move div.row span.form-button, #register div.row span.form-button {
	margin-left: 180px !important;
}
#translation div.row label {
	font-weight: normal !important;
}
#login div.row label {
	width: 100px !important;
}
#login div.row span.form-button {
	margin-left: 100px !important;
}
}
