css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("twitter.com") {
  @media screen and (min-width:1236px) {
    /* left and right 14px gutter */ 
    body.three-col .wrapper {
      width: calc(100% - 28px);
    }
    
    /* left and right 14px gutter, and left and left and right dashboard columns */
    body.three-col .content-main {
      width: calc(100% - 608px);
    }

    .ProfilePage.three-col .Grid--withGutter {
      margin: 0;
    }

    .AppContainer {
      max-width: inherit;
    }

    .permalink-container,
    .AdaptiveSearchPage.three-col .content-main,
    .ProfilePage.three-col .AppContent-main.content-main {
      width: 100%;
    }
  }
}
