css:
@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document domain("xda-developers.com"){

/* For "XDA 2013 Beta - 1024" - NOT "XDA 2013 Beta" */

/* Fonts */
body{font-family:verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif!important}
td,th,p,li{font:verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif!important}
.tcat{font:bold verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif!important}
.thead{font:bold tahoma,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif!important}
.wysiwyg{font:verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif!important}
textarea,.bginput{font:verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif!important}
.button{font:bold "Trebuchet MS",Arial,sans-serif!important}
select{font:verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif!important}
.smallfont{font:verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif!important}
.navbar{font:verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif!important}
legend{font:tahoma,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif!important}
.vbmenu_control{font:bold tahoma,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif!important}
.vbmenu_option{font:bold "Trebuchet MS",Arial,sans-serif!important}
.vbmenu_hilite{font:bold "Trebuchet MS",Arial,sans-serif!important}

/* Container bg */
body{background:rgb(239,232,221)!important}
#posts{background:rgb(251,248,244)!important}
/*#page_leftPane table{background:#fff!important;padding:5px!important}*/
.tborder{background:#fff!important;padding:5px!important}

/* Quotes */
.alt2[style='border:1px solid #B4B4B4;border-radius: 3px;background-color: #E9E9E9;padding: 10px;']{background-color:rgb(235,231,229)!important}
.alt2[style='border:1px solid #B4B4B4;border-radius: 3px;background-color: #E9E9E9;padding: 10px;'] div[style="font-style:italic"]{font-style:normal!important}
.alt2[style$="overflow: auto"]{background:none repeat scroll 0% 0% rgb(249,246,241)!important}

/* Replace XDA banner */
img[src="http://img.media.xda-developers.com/images/XDA2013/header/xdaLogo.png"]{width:0!important;background-image:url(http://media.xda-developers.com/images/sitelogo.png)!important;padding-left:242px!important;height:34px!important}

/* Links */
a{color:#b35400!important}
a:hover{text-decoration:underline!important}
.thead > a{color:rgb(255,255,255)!important}
.thead a:hover{color:rgb(255,255,0)!important}

/* Top bar */
#xda_titleHeader{background:-moz-linear-gradient(top,#53360c 0%,#3d2708 100%)!important;background:-webkit-linear-gradient(top,#53360c 0%,#3d2708 100%)!important;background:-o-linear-gradient(top,#53360c 0%,#3d2708 100%)!important}
#xda_navbar{background:-moz-linear-gradient(top,#4E3713 0%,#241705 100%)!important;background:-webkit-linear-gradient(top,#4E3713 0%,#241705 100%)!important;background:-o-linear-gradient(top,#4E3713 0%,#241705 100%)!important}
#xda_navbar td > a{color:rgb(194,189,181)!important;text-shadow:none!important;background:none!important;border-left:1px solid rgb(87,62,27)!important;border-right:1px solid rgb(68,50,24)!important}
#xda_navbar td > a:hover{color:rgb(255,210,0)!important;text-decoration:none!important}
#navtab_spacer{border-left:1px solid rgb(87,62,27)!important}
#xda_header  a{color:rgb(255,210,0)!important}
#navtab_findDevice{border-right:1px solid rgb(68,50,24)!important}
#navtab_searchForums{border-left:1px solid rgb(87,62,27)!important}
#navtab_xdatv{width:75px!important}
#navtab_links{width:125px!important}

/* Headers */
.tcat{background:none repeat scroll 0% 0% rgb(248,248,248)!important;color:rgb(85,85,85)!important}
.quickReplyTable .tcat, form > #threadslist.tborder > .threadsTable > tr:nth-child(2), #threadslist .tcat[colspan="2"], .threadsTable tr:first-child, .thead, thead td{background:none repeat scroll 0% 0% rgb(63,48,25)!important;color:#fff!important}

/* Page Footer */
#xda_footer{background-color:rgb(239,232,221)!important;color:#000!important;border:none!important}
#xda_footer td{border-top:none!important}
#xda_footer img[src="http://img1.media.xda-developers.com/images/XDA2013/header/xdaLogo_small.png"]{display:none!important}

/* Footers */
.tfoot{background:none repeat scroll 0% 0% rgb(237,232,226)!important}

/* Thread location (breadcrumb, e.g. "Forum > Sub Forum > Thread Name") */
#breadcrumb a span{color:rgb(179,84,0)!important}
#bread_home{background:url(http://media.xda-developers.com/images/icons/breadcrumbs-home.png) 50% 50% no-repeat!important}

/* Thread title bar */
.postTitle,.submitAsTip{background:rgb(83,60,28)!important}
.postTitle a, .submitAsTip a{color:#fff!important;text-shadow:none!important}

/* Post header (title/post date) */
.posterInfoHead, .postContentHead{background-color:rgb(236,232,223)!important}

/* Post footer */
#thanksTable .alt2, #thanksTable .collapseCell{background:rgb(249,246,241)!important;color:#000!important} /* thanks list */
.posterInfoFoot,.postContentFoot{background-color:transparent!important}
.posterInfoFoot{border-left:1px solid rgb(236,236,236)!important}
.jumpToTop{background-color:#ddd!important}

/* Pages buttons */
.pagenavControls .goto_nav,.pagenavControls .alt1{background:#fff!important;border:1px solid #E4DBCD!important;border-radius:5px!important;box-shadow:none!important}
.pagenavControls .alt1{background:#fff!important;border:1px solid #E4DBCD!important;border-radius:5px!important}
.pagenavControls .smallfont:hover{color:#fff!important;text-decoration:none!important}
.pagenavControls .goto_nav:hover,.pagenavControls .alt2 ,.pagenavControls .smallfont[rel="prev"]:hover,.pagenavControls .smallfont[rel="next"]:hover,.pagenavControls .alt1:hover{color:#fff!important;text-decoration:none!important;background:#675337!important;color:#fff!important;text-decoration:none!important;box-shadow:inset 0 2px 1px #000!important}
.pagenavControls .goto_nav,.pagenavControls .smallfont[rel="prev"],.pagenavControls .smallfont[rel="next"]{background:-moz-linear-gradient(top,#fff 0%,#DFDBCF 100%)!important;background:-webkit-linear-gradient(top,#fff 0%,#DFDBCF 100%)!important;background:-o-linear-gradient(top,#fff 0%,#DFDBCF 100%)!important;box-shadow:0 1px 1px!important;text-shadow:none!important;border:1px solid #E7DCC6!important;color:#988263!important}

/* Profile */
.block_row.block_title.list_no_decoration.floatcontainer{background:rgb(63,48,25)!important}
#username_box{background:none!important;border:none!important}
#main_userinfo .btn-silver{background:none!important;border:none!important;box-shadow:none!important;text-shadow:none!important}
#content{background:#fff!important;margin-left:15px!important}
#profile_tabs .thead{text-shadow:none!important}
#stats_mini,#friends_mini,#groups,#visitors{background:#fff!important}

/* Post buttons */
.postControls > *{color:#7A644B!important}
.postControls .postbitFunction{background:-moz-linear-gradient(top,#fff 0%,#DFDBCF 100%)!important;background:-webkit-linear-gradient(top,#fff 0%,#DFDBCF 100%)!important;background:-o-linear-gradient(top,#fff 0%,#DFDBCF 100%)!important;box-shadow:0 1px 1px!important;text-shadow:none!important;border:1px solid #E7DCC6!important}
.postControls > *:hover{background:#675337!important;color:#fff!important;text-decoration:none!important;box-shadow:inset 2px 2px 5px #000!important}
img[src$="multiquote_on.png"],img[src$="multiquote_off.png"]{width:0!important;background:url(http://media.xda-developers.com/images/VA_XDA/buttons/multiquote_off.gif)!important;padding-left:112px!important;height:30px!important}
img[src$="multiquote_on.png"],img[src$="multiquote_on.png"]:hover,img[src$="multiquote_off.png"]:hover{background:url(http://media.xda-developers.com/images/VA_XDA/buttons/multiquote_on.gif)!important}
.btn-yellow{font-size:10px!important;color:#493415!important;background:-moz-linear-gradient(top,#FFD71C 0%,#E1C12E 100%)!important;background:-webkit-linear-gradient(top,#FFD71C 0%,#E1C12E 100%)!important;background:-o-linear-gradient(top,#FFD71C 0%,#E1C12E 100%)!important;box-shadow:0 1px 1px!important;text-shadow:none!important;border:1px solid #E7DCC6!important}
.btn-yellow:hover{text-decoration:none!important;background:#675337!important;color:#FFD303!important;text-decoration:none!important;box-shadow:inset 2px 2px 5px #000!important}

/* Icons */
img[src="http://buttons.media.xda-developers.com/images/XDA2012/buttons/collapse_generic.gif"]{width:0!important;padding-left:13px!important;height:13px!important;background-image:url(http://media.xda-developers.com/images/VA_XDA/buttons/collapse_generic.gif)}
img[src="http://buttons.media.xda-developers.com/images/XDA2012/buttons/collapse_tcat.gif"]{width:0!important;padding-left:17px!important;height:12px!important;background-image:url(http://media.xda-developers.com/images/VA_XDA/buttons/collapse_tcat.gif)}
#navtab_links a span{width:9px!important;height:8px!important;background:url(http://forum.xda-developers.com/images/darrow.png)!important}
img[src="http://img2.media.xda-developers.com/images/XDA2013/thread/post/report.png"]{width:0!important;padding-left:17px!important;height:14px!important;background-image:url(http://media.xda-developers.com/images/icons/report-postheading.png)}
.infractionIcon img{width:0!important;padding-left:21px!important;height:17px!important;background-image:url(http://media.xda-developers.com/images/VA_XDA/buttons/infraction.gif)}

}
