css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('papktop.com') {

body{
    background: #f8f8f8!important;
    color: #222!important;    
    font-family: Arial!important;    
}

#wrapper{
    width: 1200px!important;
}

/* Header */

header{
    height: auto!important;    
}

header h1,
header h1 a{
    color: #222!important;
}

header h1{
    margin: 15px 0!important;
}

header .description{
    margin: 0 0 15px!important;
    color: #444!important;
}

header nav{
    width: 100%!important;
    background: #222!important;
    border-radius: 0!important;
    height: 60px!important;
}

#content,
#content_post{
    width: 1200px!important;
}

header .menu{
    width: 1000px!important;
    height: 60px!important;
}

header .feedrss{
    background: transparent!important;
    text-indent: 0!important;
    width: auto!important;
    height: auto!important;
    color: #fff!important;
    font-size: 16px!important;
    line-height: 60px!important;    
    height: 60px!important; 
    margin: 0!important;
    padding: 0 20px!important;    
}

header .menu .current_page_item a, header .menu .current-cat a{
    background: #327FDD!important;
    font-size: 16px!important;
    line-height: 60px!important;    
    height: 60px!important;    
    padding: 0 20px!important;
    margin: 0!important;
    width: auto!important;    
}

header .menu li a{
    font-size: 16px!important;
    line-height: 60px!important;    
    height: 60px!important;    
    padding: 0 20px!important;
    margin: 0!important;
    width: auto!important;    
    background: transparent!important;
    text-shadow: 0 0 0 transparent!important;
    color: #eee!important;    
}

header .menu li a:hover{
    background: #327FDD!important;
}

#content,
#content_post{
    background: #eee!important;
    width: 100%!important;
    text-shadow: 0 0 0 transparent!important;
}

#content article{
    width: 900px!important;    
}

/* Articles */

section{
    padding: 15px!important;
    margin: 15px!important;
    background: #fff!important;
    border-bottom: 3px solid rgba(0, 0, 0, 0.2)!important;
    border-radius: 0 0 3px 3px!important;
}

section .title h2{
    text-shadow: 0 0 0 transparent!important;
    color: #222;    
}

section .entry{
    text-shadow: 0 0 0 transparent!important;
    color: #444!important;
    font-size: 14px!important;    
    line-height: 22px!important;    
}

section .entry > div{
    padding: 0!important;    
}

section .entry > div a{
    display: block!important;    
}

section .entry img, section .entry a img{
    display: block!important;    
    border: none!important;
}

section .postmeta{
    font-size: 11px!important;    
    line-height: 28px!important;    
    color: #fff!important;    
}

section .postmeta a{
    background-color: #327FDD!important;
    border-radius: 0!important;
    margin: 0 5px!important;    
    color: #fff!important;    
    padding: 0px 5px!important;    
    display: block!important;
    float: left !important;   
    transition: all 0.1s linear!important;
    border: 2px solid #fff!important;    
}

section .postmeta a:hover{
    text-shadow: 0 0 0 transparent!important;    
    color: #327FDD!important;
    background-color: #fff!important;    
}

/* Sidebar */

aside{
    width: 280px!important;    
    padding-top: 15px!important;    
}

#searchform{
    width: 280px!important;    
}

#searchform #s{
    background: #fff!important;
    padding: 5px 10px!important;
    width: 220px!important;    
    height: 15px!important;    
}

#searchform #searchsubmit{
    background: #327FDD!important;
    height: 25px!important;    
}

#searchform #searchsubmit:hover{
    background: #fff!important;
    cursor: pointer!important;    
}

aside h3{
    background: #222!important;
    color: #fff!important;    
    text-shadow: 0 0 0 transparent!important;
    width: 280px!important;
    height: 30px!important;
    line-height: 30px!important;
    font-size: 16px!important;    
}

aside ul li ul,
aside ul li div,
aside .widget_text div,
aside .widget_calendar div,
aside .widget_tag_cloud div{
    background: #fff!important;
    width: 250px!important;
    border: none!important;
    padding: 15px!important;    
}

aside ul li ul li{
    padding: 0!important;
    margin: 5px 0!important;    
}

aside ul li ul li a{
    font-size: 14px!important;
    height: 30px!important;
    line-height: 30px!important;    
}

aside ul li ul li a:hover{
    text-decoration: none!important;    
}

#wp-calendar{
    margin: 0 auto!important;    
}

#RandomShow{
    display: none!important;    
}

/* Footer */

footer{
    background: #222!important;    
}

/* Navigation */

.istudio_pagenavi .current{
    background: #327FDD!important;
}

.istudio_pagenavi a,
.istudio_pagenavi span,
.page-links a,
.page-links span{
    background: #666!important;    
    margin: 0 2px!important;    
}

.istudio_pagenavi .next{
    margin: 0 2px!important;    
    background-color: #f00!important;    
}

/* Single */

#content_post section{
    width: 1140px!important;    
}

}
