css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.bilibili.tv") {
    body {
        background: url("/*[[img]]*/") left top / 100% auto no-repeat fixed !important;
    }

    .hd {
        background: -moz-linear-gradient(center top , rgba(255, 255, 255, .7), rgba(246, 246, 246, .7) 3px, rgba(246, 246, 246, .7) 15px) repeat scroll 0% 0% transparent !important;
    }
    .swc .on, .swc .on:hover {
        background: -moz-linear-gradient(center top , rgba(238, 238, 238, .5), rgba(255, 255, 255, .5) 3px, rgba(255, 255, 255, .5) 35px) repeat scroll 0% 0% transparent !important;
    }

    .rec, .vidbox, .vidbox .right {
        background: none !important;
    }
    .right_box > li:nth-of-type(2n),
    .rlist > li:nth-of-type(2n) {
        background: rgba(244, 244, 244, 0.7) !important;
    }
    .more a {
        border: none !important;
    }

    .ad-c3 {
        display: none !important;
    }

    .c-link, .footer {
        border-radius: 5px !important;
        background: url("http://static.hdslb.com/images/newcss/80ap.png") repeat scroll left top transparent !important;
    }
    .c-t {
        background-color: rgba(245, 245, 245, 0.5) !important;
    }

    .z > .viewbox, .z > .upinfo {
        border-radius: 5px !important;
        background-color: rgba(255, 255, 255, 0.8) !important;
    }
    .z > .viewbox {
        margin-left: 7px !important;
    }
    .z > .upinfo {
        padding: 10px !important;
    }
    .z-l-tag {
        background-color: rgba(221, 221, 221, 0.7) !important;
    }
    .s_tag {
        background-color: rgba(242, 242, 242, 0.7) !important;
    }
    .intro {
        background-color: rgba(246, 246, 246, 0.7) !important;
    }

    .videobox {
        width: 960px !important;
        margin-left: 7px !important;
    }

    .z > .large.left:not([id = "zt_c_sp"]):not([id = "largeleft"]):not([id = "catabox"]) {
        padding: 0 !important;
        margin: 0 0 20px 5px !important;
        border-radius: 5px !important;
        background: url("http://static.hdslb.com/images/newcss/80ap.png") repeat scroll left top transparent !important;
    }
    .spc_list {
        padding: 0 !important;
    }
    .spc_list .hdt,
    .z-l:not([class $= "iv"]) .hdt,
    .searchlist .hdt {
        padding-left: 4px !important;
    }
    .spc_list li {
        padding: 10px !important;
    }
    .z > .large.left > .pagelistbox {
        margin: 20px !important;
    }

    .searchlist {
        margin: 0 7px !important;
        border-radius: 5px 5px 0 0 !important;
        background: url("http://static.hdslb.com/images/newcss/80ap.png") repeat scroll left top transparent !important;
    }
    .searchlist li.l {
        margin: 0 !important;
        border: none !important;
        box-shadow: none !important;
    }
    .z > .pagelistbox {
        float: none !important;
        margin: 0 7px !important;
        padding: 0 40px 20px !important;
        border-radius: 0 0 5px 5px !important;
        background: url("http://static.hdslb.com/images/newcss/80ap.png") repeat scroll left top transparent !important;
    }
}
