css:
@-moz-document domain("n4g.com") {
* { font-family: sans-serif; } 
pre { font-family: monospace; }
div[id="ad-top-wrap"] { display: none !important; }
body { background-image: none; background: #f0f0f0; }
input { border: 2px solid #ccc; padding: 3px 5px; border-radius: 4px; }

.shsl-wrap { background: #fff; box-shadow: 0 2px 2px #999; -webkit-box-shadow: 0 2px 2px #999; -moz-box-shadow: 0 2px 2px #999; }
.shsl-wrap b { color: #fff; }
.shsl-headerwrap { background: #666; color: #fff; }
.shsl-headerwrap .shsl-timelink { color: #fff; }
.shsl-headerwrap .shsl-timelink:hover { color: #000; }
.shsl-headerwrap .shsl-timelink.sel:hover { background: #000; color: #fff; }
.shsl-headerwrap .shsl-timelink.sel { background: #fff; color: #000; }
.shsl-header-arrow { display: none; }
.shsl-item-bottomwrap { background: none; }
.shsl-item-title { color: #116097; background: rgba(255, 255, 255, 0.80); }
.shsl-item-title:hover { color: #3C3C3C; background: rgba(255, 255, 255, 0.90); }
.shsl-item-typeicon { display: none; }
.bigitem .shsl-item-time, .shsl-item.smlitem .shsl-item-time { color: #999; font-size: 0.9em; text-align: left; }
.bigitem .shsl-item-time { font-size: 1.2em; }
.shsl-item-bottomwrap b { color: #333; }
.shsl-item-source { background-color: rgba(0, 0, 0, 0.75); }

#subsubmenu-more-news, #subsubmenu-more-articles, #subsubmenu-more-media { color: #fff; background: #666; }
.subsubmenu { background: #000; }


.sl-filter-fpwrap { background: none; }
.sl-headerwrap { background: #cacaca; color: #727272; }

.channelpage-wrap { background: none; box-shadow: none; -webkit-box-shadow: none; -moz-box-shadow: none; }
.channelpage-content { background: none; width: 950px; padding: 25px; margin: 0; }
.channelpage-sidecontent { display: none; }

.sl-item { background: #fff; border-radius: 5px; border: 0; box-shadow: 0 2px 2px #999; -webkit-box-shadow: 0 2px 2px #999; -moz-box-shadow: 0 2px 2px #999; margin: 1.2em 0; padding: 1em 1em 1em 1.2em; }

#header-wrap { width: 100%; position: fixed !important; top: 0; z-index: 200 ! important; background: none; }
#header { height: 90px; width: 1000px; margin: auto auto; background: rgba(20, 20, 20, 0.75); -webkit-box-shadow: 0 1px 15px 0px #999 !important; -moz-box-shadow: 0 1px 15px 0px #999 !important; box-shadow: 0 1px 15px 0px #999 !important; border-left: 1px solid #000; border-right: 1px solid #000; border-bottom: 1px solid #000; }
#page { max-width: 1000px; width: 1000px; margin: auto auto 100px auto; position: relative; top: 100px; }
#header-logo { float: left; margin: 0 1em; width: auto; height: auto; }
#header-logo-inner { padding: 0; }
#header-tabbar { width: auto; }
#header-tabbar .tabbar-tab.sel a.tabbar-tabtext, .subsubmenu .sel a { color: #fff; }
#header-tabbar .tabbar-tab.sel, .subsubmenu .sel a { background: rgba(0, 0, 0, 0.75); border-radius: 5px; }
#header-toolmenu {position: absolute; height: 60px; width: 140px; top: 0; right: 0; background: #333; }
#header-newsboiler { display: none; }
#header-submit { left: 0; width: 40px; height: 100%; }
#header-pending { left: 41px; width: 40px; height: 100%; }
#header-account { left: 82px; width: 58px; height: 58px; }
#header-account img { top: 0; right: 0; width: 100%; height: 100%; }
#header-accountlink { top: 0; left: 0; width: 100%; height: 100%; background-position: 0 -700px; }
#header-accountlink:hover { background-position: 0 -700px; }
#header-newsboiler { top: 0; left: 140px; height: 100%; }
.submenu { font-size: 0.85em; }
.submenu2 a, .submenu-item .submenu-link, .submenu-subsubtitle,
.submenu-item.sel .submenu-link, .submenu-item.sel2 .submenu-link, a.submenu-subsubtitle { color: #fff; }
.submenu-item .submenu-link:hover, .submenu-subsubtitle:hover,
.submenu-item.sel .submenu-link:hover, .submenu-item.sel2 .submenu-link:hover, a.submenu-subsubtitle:hover { background: #555; color: #fff; }
.submenu2 a.sel, .submenu-item.sel .submenu-link, .submenu-item.sel2 .submenu-link, a.submenu-subsubtitle { background-color: rgba(0, 0, 0, 0.75); color: #fff; }
.submenu2 a:hover, .submenu-item.sel .submenu-link:hover, .submenu-item.sel2 .submenu-link:hover, a.submenu-subsubtitle:hover { color: #ccc; background-color: rgba(0, 0, 0, 1); }
#header-site:hover { background-color: #f0f0f0; }
#header-site { background-color: #fff; }
#header-pending.sel { color: #fff; }

.smsite-wrap { right: 370px; background: rgba(20, 20, 20, 0.85); border: 0; }
.smsearch-wrap { background: rgba(20, 20, 20, 0.85); border: 0; }
.smsite-wrap ul a, .smaccount-wrap ul a, .smaccount-account, .smaccount-public, .smaccount-pm { color: #fff; background: rgba(0, 0, 0, 0.75); }
.supermenu-searchwrap label { color: #fff; font-size: 1.2em; width: 80px; text-align: right; text-transform: uppercase; }
.smaccount-wrap { background: rgba(30, 30, 30, 0.90); color: #fff; border: 0; -webkit-border-radius: 0 0 5px 5px; border-radius: 0 0 5px 5px; font-size: 1.1em; }
.smaccount-username { color: #ccc; font-size: 1.5em; }
.smaccount-role, .smsite-wrap ul b, .smaccount-wrap ul b, .smmod-wrap ul b { color: #fff; }
.smaccount-wrap ul a, .smmod-wrap ul a { color: #fff; background: rgba(255, 255, 255, 0.1); }
.smaccount-wrap ul a:hover, .smmod-wrap ul a:hover { background: rgba(255, 255, 255, 0.5); }
.smaccount-account, .smaccount-public, .smaccount-pm { color: #1682E6; background-color: rgba(0, 0, 0, 0.80); }
.smaccount-account:hover, .smaccount-public:hover, .smaccount-pm:hover { color: #fff; }
.smaccount-wrap ul a.alert, .smsite-wrap ul a.alert, .smaccount-topwrap a.alert { background: #CC1616; color: #FFFFFF; }
.nextpager { margin: 20px 0; }
.vn-content { background-image: none; box-shadow: 0 2px 2px #999; -webkit-box-shadow: 0 2px 2px #999; -moz-box-shadow: 0 2px 2px #999; }
.vn-content * a { color: #2A8EDB; }
.vn-title { color: #292929; }
.vn-content * a:hover { color: #000; }
.vn-text { margin: 1em 0; font-size: 1.2em; line-height: 1.5em; color: #1F1F1F; }
.tsi-icon-7 { display: none; }
.vn-userinfowrap { text-align: right; font-style: normal; color: #666; font-size: 1em; }

ul.vn-altsources { background: #fff; }
.vn-source a { color: #000; }
.vn-sourcemenu { background: #F0F0F0; overflow: visible; float: left; clear: both; width: 100%; margin: 2em 0; font-size: 1.1em; }
a.vn-sourcevotelink { background: #ccc; text-transform: uppercase; }
a.vn-sourcevotelink:hover { color: #fff; background: #444; }
.successmessage, .failmessage { font-weight: 700; font-size: 1.2em; position: absolute; top: -25px; background: #fff;  }
.successmessage { color: rgb(19, 192, 19); }
.failmessage { color: rgb(221, 18, 18); }
.vn-credit { color: #666; }
.vn-credit a { color: #000; }

.c-pager { padding: 10 0 0 0; }
.c-pager * { font-size: 1.2em; }
.vn-main { width: auto; }
.sl-headerwrap-title2 { font-size: 1.3em; }
.vn-lowercontent { background: none; box-shadow: none; -webkit-box-shadow: none; -moz-box-shadow: none; margin-bottom: 100px; }
.cl-wrap { background: #fff; border-radius: 5px; border-bottom: 1px solid #ccc; margin: 1.2em 0; padding: 0.5em; }
.cl-con { font-size: 1.2em; line-height: 1.5em; }
.cl-bt { margin-bottom: 0; font-size: 1em; }

.cl-bubup, .cl-bubdown { background: none; float: right; clear: both; margin: 0 0 0.5em 0; }
.cl-bubup a { background: #1DAA2A; }
.cl-bubdown a { background: #AA231D; }
.cl-bubup a, .cl-bubdown a { color: #fff; font-size: 1.1em; border-radius: 4px; margin-left: 5px; }
.cl-bubup b, .cl-bubdown b, .cl-up-cancel, .cl-down-cancel { color: #474747 !important; font-size: 1em; }
.cl-bubup b, .cl-bubdown b { margin: 0; padding: 5px 5px 0 0; }
a.cl-bhelp, a.cl-up-cancel, a.cl-down-cancel { float: left; background: none !important; }

.formtype-mini { background: #fff; -webkit-border-radius: 5px; border-radius: 5px; border-bottom: 1px solid #ccc; }
.formtype-mini .f-titlefield-inner { background: none; }
.formtype-mini .f-titlefield h1 { font-size: 1.3em; border: 0; background: 0; }
.formtype-mini .f-buttonfield, .formtype-mini a.togglebox-toggleparent, .formtype-mini .closed a.togglebox-toggleparent, .formtype-mini .f-titlefield, .formtype-mini .f-inputfield, .f-inputfieldclosable { border: 0; background: none; }
.f-buttonfield-inner button { font-size: 1.2em; }

.sv-pointer.pos { background-color: #111; text-transform: uppercase; padding: 0.5em 0.2em 1em 0.2em; }
.sv-bar, .sv-buttons { background: none; }
.sv-buttons a { text-transform: uppercase; }
.sv-buttons a { background: none; }
.sv-buttons a:hover { background: #666; color: #fff; }
.sv-bar b.last { border-right: 2px solid #FFFFFF; }
.sv-bar b.pos { border-left: 2px solid #FFF; background: #4BA755; }
.sv-bar b { border-left: 2px solid #FFF; background: #E44848; }
a.sv-pos { background: #4BA755; color: #FFF; }
a.sv-mid { background: #fff; color: #000; }
a.sv-neg { background: #E44848; color: #FFF; }

.vn-update { background: #BD0E38; color: #fff; font-size: 1.1em; }

.generalpage-top, .generalpage-top-inner, .generalpage-content, .generalpage-bottom, .generalpage-bottom-inner { background: none; }
.generalpage-sub { width: 280px; padding: 10px; background: #fff; }

.sectionpage-content, .url-avwrap { background: none; }
.sl-headerwrap { background: none; }
.url-item, .bl-item { padding: 10px; background: #fff; box-shadow: 0 2px 2px #999; border: 0; border-radius: 5px; width: 940px; margin: 0 0 2em 0; }
.url-ratingwrap { width: 70px; height: 50px;  background: #ccc; color: #666; font-size: 2em; margin: 10px; border-radius: 50px; -webkit-border-radius: 50px; }
.url-textwrap, .bl-textwrap { padding: 0 10px; line-height: 1.5em; font-size: 1.2em; }
.bl-textwrap { margin: 0 0 0 10px; }
.bl-sidewrap { width: 25%; }

.formtype-normal { background: #fff; border-radius: 5px; box-shadow: 0 2px 2px #999; }
.formtype-normal a.togglebox-toggleparent { background: none; border: 0; }
.formtype-normal .f-valiadtion, .formtype-normal label.error { background: none; }
.formtype-normal .f-inputfield, .formtype-normal .f-titlefield, .formtype-normal .f-titlefield-inner, .f-customfield, .formtype-normal .f-buttonfield-inner, .formtype-normal .f-buttonfield, .formtype-normal .f-customfield, .formtype-normal .f-inputfield, .formtype-normal .f-inputfieldclosable, .formtype-normal .closed a.togglebox-toggleparent { background: none; border: 0; }
}
