css:
/* Black Soundcloud by pp.amorim */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("soundcloud.com")
{
    body{
    background: #0C0C0C !important;
    }
    
    .listenContent__inner{
	background:#141414 !important;
    }
    a {
    color: #FFF !important;
    }
    
    .sound__soundActions{
	background:transparent !important;
    }
    .commentForm__inputWrapper{
	background:#141414 !important;
	border: 0 !important;
    }
    .soundBadge{
	background:#141414 !important;
    }
    .commentForm__input {
	background:#000000 !important;
	color: #FFF !important;
	border-color: #FF6600 !important;
    }
    
    .commentForm {
	background:#0C0C0C !important;
	color: #FFF !important;
	border: 0 !important;
    }
    
    .commentForm__avatar {

	border:1px solid #FF6600 !important;
    }
    
    
    .soundTitle__title, h1.userInfo__title{
	color: #FF6600 !important;
	}
	.l-footer{
	background:#141414 !important;
    }
    sidebarHeader__more{
	background: #FF6600 !important;
	}
	sc-button, .sc-button-like, .sc-button-repost, .sc-button-addtoset, .sc-button-share, .sc-button-follow, .sc-button-message, .sc-button-medium, .sc-button-download, .sc-button-addtogroup, .sc-button-edit, .sc-button-delete {
	background:#000000 !important;
	color: #FFF !important;
	border-color: #BC5F18 !important;
	text-shadow: 0 0 0 !important;
	box-shadow: 0 0 0 #FF821C,inset 0 0px 0 #FF821C,inset 0px 0 0 #FF821C,inset 0 0 0 #FF821C,inset 0 0 0 rgba#FF821C !important;
    }
    
    .sc-button{
    border-color: #BC5F18 !important;
	text-shadow: 0 !important;
	box-shadow: 0 0 0 #FF821C,inset 0 0px 0 #FF821C,inset 0px 0 0 #FF821C,inset 0 0 0 #FF821C,inset 0 0 0 #FF821C !important;
    }
    
    .uploadButton:hover{
        background: #FF821C !important;
    }
    
    .sc-button:hover,.sc-button:focus,.sc-button-focus{

    color:#FFF !important;
	outline:0 !important;
	background-color:#FF821C !important;
	border-color: 0 !important;
	box-shadow: 0 0 0 #FF821C,inset 0 0px 0 #FF821C,inset 0px 0 0 #FF821C,inset 0 0 0 #FF821C,inset 0 0 0 #FF821C !important;
	
    }
    .dashbox {
    background: #0C0C0C !important;
    }
    h1.dashbox__title, h1.dashbox__title:first-child, p.dashbox__text, .sc-text-link, h3, p {
    color: #FFF !important;
    }
    
    .trackPreview__all, .sc-link-dark{
    background: #000 !important;
    }
    
    .trackPreview__post:before{
	content:'';
	background: #000 !important;
	background: #000 !important;
	position:absolute;
	top:0;
	right:100%;
	width:60px;
	height:100%
    }
    
    .sc-border-light-bottom, .sc-border-dark-top, .sc-border-dark-bottom, .soundBadgeList__item, .sc-border-light-top, .sc-border-light-right, .sc-link-light {
    border-color: #FF821C !important;
    }
    
    .sc-button-selected {
        text-shadow:0 0 0 #fcfcfc,0 0 0 #fcfcfc,0 0 0 #fcfcfc,0 0 0 #fff !important;
        background: #FF821C !important;
    }
    
    .dialog{
        background: #FF821C !important;
    }
    
    
    .dialog__arrow{
	background:#FF3300 !important;
	border:1px solid #FF5900 !important;
	border-width:1px 0 0 1px;
	position:absolute;
	width:8px;
	height:8px;
	box-shadow:-1px -1px 1px -1px rgba(0,0,0,.5);
	z-index:-1
    }
    .sc-link-dark{
    background:#141414 !important;
    }
    
    .paging-eof:before{
	background:0 !important;
    }
    
}

/* Black Soundcloud by pp.amorim */
