css:
@-moz-document domain('hipchat.com')
{
@font-face
{
	font-weight	: 400;
	font-style	: normal;
	font-family	: 'Scada';
	src			: local('Scada'), local('Scada-Regular'), url('https://themes.googleusercontent.com/static/fonts/scada/v2/lanmJfBMPxhYKQPtj9triqCWcynf_cDxXwCLxiixG1c.woff') format('woff');
}
@font-face
{
	font-weight	: 400;
	font-style	: normal;
	font-family	: 'FontAwesome';
	src			: local('FontAwesome'), url('https://netdna.bootstrapcdn.com/font-awesome/4.0.3/fonts/fontawesome-webfont.woff') format('woff');
}

html *
{
	font-size	: 13px !important;
	line-height	: 1.1em !important;
	font-family	: 'Lucida Grande', 'Arial', sans-serif !important;
}
#topic
{
	font-size	: 15px !important;
	font-weight	: bolder !important;
}
#chats *, textarea#message_input
{
  	font-size	: 15px !important;
	font-family	: 'Scada', sans-serif !important;
}
#chats > .chat_display > .chatText > div > .chatBlock .nameBlock *,
#chats > .chat_display > .chatText > div > .chatBlock .timeBlock
{
	font-size	: 12px !important;
	font-family	: 'Lucida Grande', 'Arial', sans-serif !important;
}
#chats > .chat_display > .chatText > div > .chatBlock .nameBlock *
{
  	font-weight: bold !important;
}

#topic
{
	display		: inline-block !important;
  	margin-top	: 3px !important;
}


#room_actions_button
{
	width: auto !important;
}


#chats
{
	bottom: 123px !important;
}


#message_input
{
	height		: 100px !important;
	background	: #151515 !important;
	color		: #FFFFFF !important;
}
form[name="messageForm"] > table > tbody > tr > td > div.textwrapper
{
	border-radius	: 5px !important;
	background		: #151515 !important;
}
#upload_button
{
	height			: 100px !important;
	border-radius	: 0 5px 5px 0 !important;
	background		: #EEEEEE !important;
}
body > div > input[name="Filedata"]
{
	height: 100px !important;
}
form[name="messageForm"] > table > tbody > tr > td:nth-child(n+4)
{
	display: none !important;
}


#chats > .chat_display
{
	background : #CCCCCC !important;
}
#chats > .chat_display > .chatText > div:empty
{
	display: none !important;
}
#chats > .chat_display > .chatText > div.dateDivider
{
	border		: 0 !important;
	background	: none !important;
}
#chats > .chat_display > .chatText > div > .chatBlock
{
	padding       	: 0 !important;
	margin        	: 10px !important;
	background		: #FFFFFF !important;

	border        	: 0 !important;
	border-radius 	: 5px !important;
	box-shadow    	: 0 0 1px 1px rgba(0, 0, 0, 0.2) !important;
}
#chats > .chat_display > .chatText > div > .chatBlock .nameBlock,
#chats > .chat_display > .chatText > div > .chatBlock .messageBlock
{
  	border	: 0 !important;
	padding	: 5px 0 5px 0 !important;
}
#chats > .chat_display > .chatText > div > .chatBlock .nameBlock
{
	border-top-left-radius		: 5px !important;
	border-bottom-left-radius	: 5px !important;
	border-right        		: 1px solid rgba(0, 0, 0, 0.1) !important;
}
#chats > .chat_display > .chatText > div > .chatBlock .messageBlock
{
	border-top-right-radius		: 5px !important;
	border-bottom-right-radius	: 5px !important;
}
#chats > .chat_display > .chatText > div > .chatBlock .messageBlock th,
#chats > .chat_display > .chatText > div > .chatBlock .messageBlock td
{
	border-radius	: 5px !important;
	padding 		: 5px !important;
}

#chats > .chat_display > .chatText > div > .chatBlock .messageBlock a[target='_blank']:not(.linkImage):not([href$=".gif"]):not([href$=".jpg"]):not([href$=".png"]):after
{
  	content		: '\f08e' !important;
  	font-family	: 'FontAwesome' !important;
  	display		: inline-block !important;
  	margin		: 0 0 0 2px !important;
}
#chats > .chat_display > .chatText > div > .chatBlock .messageBlock a[href^='mailto']:not(.linkImage):not([href$=".gif"]):not([href$=".jpg"]):not([href$=".png"]):after
{
  	content		: '\f0e0' !important;
  	font-family	: 'FontAwesome' !important;
  	display		: inline-block !important;
  	margin		: 0 0 0 2px !important;
}
#chats > .chat_display > .chatText > div > .chatBlock .timeBlock:after
{
  	content		: '\f017' !important;
  	font-family	: 'FontAwesome' !important;
  	display		: inline-block !important;
  	margin		: 0 -5px 0 10px !important;
}

#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock pre,
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock pre *,
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock code,
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock code *,
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock samp,
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock samp *,
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock kbd,
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock kbd *,
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock tt,
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock tt *
{
	font-size	: 12px !important;
	font-family : 'Menlo', 'Monaco', 'Consolas', 'Courier New', monospace !important;
}
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock * pre:not(.prettyprint)
{
	border-radius	: 5px !important;
	margin			: 15px !important;
	padding			: .5em !important;
	background		: #FFFFFF !important;
	border			: 1px solid #CCCCCC !important;
}
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock :not(pre) > code,
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock :not(pre) > code *
{
	border-radius	: 3px !important;
	padding			: 1px 3px 1px 3px !important;
	background		: rgba(0,0,0, 0.05) !important;
	display			: inline-block !important;
}
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock * strong,
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock * strong *
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock * b,
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock * b *
{
	font-weight: bold !important;
}
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock * em,
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock * em *,
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock * i,
#main_chat #chats > .chat_display > .chatText > div > .chatBlock .messageBlock * i *
{
	font-style: italic !important;
}
}
