css:
@-moz-document domain("aniwey.net") {
/*   Candy box style	*/
/*   Arremer			*/

/*/ Main contents /*/

body { /*
  background-color: #2A2D7D;
  color: #ceba21; 
  text-shadow: 1px 1px 0px rgba(206, 186, 33, .25);*/
  font-family: "Lucida Sans", "Droid Serif" ;
  font-size: 0.9em;
  text-shadow: 1px 1px 0px rgba(33, 33, 33, .75);
  background: linear-gradient(to bottom, rgba(16,32,222,0.65) 0%,rgba(0,0,0,0) 100%) repeat-x, url(data:image/gif;base64,R0lGODlhCAAIAIAAABAg3gAAACwAAAAACAAIAAACDURuh5rbvZgKkEr4QgEAOw==) repeat;
  color: #efefef;

}

pre, tt {
  font-family: "Lucida Console", "Luxi Mono", "ProFontWindows" ;
  font-size: 1em;
  text-shadow: 1px 2px 0px rgba(33, 33, 33, .75);
}

b {/*
  color: #ce2721;
  color: #ef1000;*/
  color: #D1271A;
  text-shadow: 1px 1px 0px rgba(103, 39, 33, .75); 
}

.tooltip:hover span {
  text-shadow: 1px 1px 0px rgba(33, 33, 33, .25);
}

a {
  color: #B37728;
}

a:visited {
  color: #866438;
}

a:hover {
  color: #D9B17C;
}

/*/ Buttons /*/
button {
  border: 1px solid #1020de;
  background-color: #000000;
  color: #efefef;
  padding: 1.5px 8px 0px;
  font-family: "Lucida Sans", "Droid Serif" ;
}

button:hover {
  border: 1px solid #464c54;
  background-color: #202020;
  padding: 1.5px 8px 0px;
}

button:disabled {  
  border: 1px solid #464c54;    
  color: graytext;
}

#tabs li button {
  color: #efefef;
  border: 1px solid #1020de;
}

table, th, td {
  border: 1px solid #1020de;
  background-color: #000000;
  padding: 1.5px 8px;
}

.toggle {
  background-color: #000000;
}

button.tooltip {
  padding: 2px 8px !important;
  width: 100%;
}

/*/ Other /*/
pre#quest_potions {
  margin: 0px;
}


/*/ Inventory item colours /*/
span#inventory_key.tooltip pre {
  font-weight: bold;
  color: #ceba21;
}

span#inventory_plate_armour.tooltip pre {
  font-weight: bold;
  color: #cde0e4;
}
 
span#inventory_boots.tooltip pre {
  font-weight: bold;
  color: #B68F65;
}
 
span#inventory_magician_hat.tooltip pre {
  font-weight: bold;
  color: #634ED8;
}
 
span#inventory_pink_ring.tooltip pre{
  font-weight: bold;
  color: #F136B8;
}
 
span#inventory_magical_horn.tooltip pre {
  font-weight: bold;
  color: #ecdc9d;
}

span#inventory_cauldron.tooltip pre {
  font-weight: bold;
  color: #728375;
}

span#inventory_horn_of_plenty.tooltip pre {
  font-weight: bold;
  color: #62412e;
}

span#inventory_old_amulet.tooltip pre { /* Y D N A C */
  font-weight: bold;
  color: #f39898;
}

span#inventory_candies_converter.tooltip pre { 
  font-weight: bold;
  color: #ff4b64;
}

span#inventory_swamp_map.tooltip pre, span#inventory_hut_map.tooltip pre, span#inventory_forge_map.tooltip pre, span#inventory_well_map.tooltip pre {
}





}
