css:
@-moz-document url("http://clickingbad.nullism.com/") {
#make_btn {
  background-color: #39f;
  border-color: #27e;
  text-shadow: -1px -1px 1px #27e;
  border: none !important;
}

#sell_btn {
  background-color: #1c3;
  text-shadow: -1px -1px 1px #0a0;
  width: 284px !important;
  border-radius: 0 !important;
  margin-left: -7px !important;
  border: none !important;
}

#make_btn, #sell_btn {
  padding: 20px 10px 0 !important;
  height: 50px !important;
  width: 100% !important;
  margin-left: -10px !important;
}

#make_btn:hover, #sell_btn:hover {
  background: #303030;
  text-shadow: none !important;
  box-shadow: 0 0 2px #ccc;
}

.tab {
  width: 13.8% !important;
  border-radius: 0 !important;
}

body {
  background: #303030 !important;
}

body * {
  border-radius: 0 !important;
  font-family: Segoe Ui !important;
}

.s_div, .w_div {
  margin: 15px 0 !important;
  box-shadow: 0px 1px 3px 1px #3F3F3F;
  padding: 2px 15px !important;
}

.s_div span {
font-size: 17px !important;
}

#last_saved {
border-top: 1px solid dimGray;
border-bottom: 1px solid dimgray;
padding: 10px;
color: grey !important;
}

#last_message {
width: 80% !important;
margin: auto !important;
font-size: 14px !important;
}

#sell_amt {
font-size: 25px;
color: rgb(98, 156, 10);
}

#make_amt {
font-size: 25px;
color: rgb(43, 147, 187);
}
}
