css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document  domain("xhtml.co.il"),
                url("http://xhtml.co.il/"),
                url-prefix("http://xhtml.co.il/blog/")  {

/* Основные стили */
div.top_header, div.top_header .logo a.main_logo_ru, .lang_block, div.orphusImg, div.googleTop, div.googleBottom, .donate, .inside, .share, .footer, div.lbox.newsletters, div.rbox_right, iframe[src^="https://accounts.google.com"] {
 display: none !important;}
div.top_header {
width: 0px!important;}

body{
background: transparent!important;}
div.bpage, div.mpage, .tryit-header, .maincol100 {
background: #eee!important;
text-shadow: 1px 1px 1px #fff!important;
}
a {
text-decoration: none!important;}
input {
size: 100px!important;}
#tooltip {
visibility:hidden!important;}
img, div.main {
max-width: 100%;}

/* Главное меню */
div.menu, div.menu_center { 
background: gray !important;
border: none !important;
height: 2em !important;
box-shadow: 2px 8px 12px #999!important;
margin-bottom: 10px!important;
border-radius: 20px 20px 20px 20px!important;
border-bottom: 1px solid #555 !important;
}
div.menu {
margin: 5px 5px 15px 5px!important; 
width: 99.3%!important;
}
.menu .menuitem a , .menu .menuitem a:hover, .menu .menuitemHover a {
cursor: pointer!important;
float: left!important;
font-size: 1em !important;
font-weight: bold !important;
height: 2em !important;
line-height: 2em !important;
min-width: 5.25em!important;
padding: 0 0 0 45px !important;
}
.menu .menuitem a, div.rbtitle a {
color: #111 !important;
text-shadow: 0 1px 1px #999!important;
}
.menu .menuitem a:hover, .menu .menuitemHover a, div.rbtitle a:hover {
color: lime !important;
text-shadow: 0 0 10px #fff!important;
}

/* Боковые блоки */
.rbtitle, .newsletters .rbtitle, .content_comm .main_title_comm { 
background: gray!important;
border-radius: 20px 20px 0 0!important;
color: #111!important;
text-shadow: 0 1px 1px #999!important;
font-size: 1em!important;
font-weight: bold!important;
line-height: 20px!important;
width: 100%!important;
}
.rbtext {
background-color: silver!important;
border: 1px solid gray!important;
border-radius: 0 0 20px 20px!important;
color: #3C3C3C!important;
line-height: 1.5em!important;
width: 16.6em!important;
text-shadow: 0 1px 1px #ddd!important;
}
.rbox, .lbox {
border-radius: 20px!important;
margin: 0px 0px 0.81em!important;
padding: 0px!important;
box-shadow: 2px 5px 15px #888!important;
}
.rcol .rbtext {
width: 18.49em!important;
}

/* Вдавленные ссылки */
.rbtext .active, div.rbox div.cube_B_pad a:hover, .rbtext a:hover {
background-color: gray!important;
color: #222!important;
text-shadow: 0px 1px 1px #999!important;
border-radius: 10px!important;
box-shadow: inset 1px 2px 3px #555!important;
}
.lcol .lbox a, .rcol .lbox a, .lcol .rbox a, .rcol .rbox a {
padding-left: 5px!important; margin-left: -2px!important;
}

/* Подзаголовки боковых блоков */
.cube_line a {
color: #3C3C3C!important;
text-shadow: 0 1px 1px #ddd!important;
}

/* Центральные блоки */
div.main_page_cube { 
border: 1px solid gray!important;
border-radius: 60px!important;
}
div.main_page_cube h2.main_page1, div.main_page_cube h1.main_page1, div.main_page_cube h2.main_page2, .example_out , .main_title .name h2 {
 background: transparent!important; }
div.content {
background: transparent!important;
line-height: 1.4em!important;
margin: 0px!important;
padding: 0px 0px 10px 0px!important;
width: 100%!important;
}
div.main_page_cube h2.main_page1, div.main_page_cube h1.main_page1, div.main_page_cube h2.main_page2, .feedbackForm { 
background: transparent!important;
color: #484848!important;
font-size: 1.2em!important;
}
.content a, .maincol .content_sm a { 
color: #444!important;
font-weight: normal!important;
}
.content a:hover, .maincol .content_sm a:hover { 
color: #000!important;
font-weight: normal!important;
text-shadow: 1px 1px 1px #fff!important;
}
.content_comm .main_title_comm h2 { 
background: transparent!important;
color: #444!important;
font-size: 1.3em!important;
margin-top: 7px!important;
padding-left: 15px!important;
}
p.tutintro {font-size: 1em!important;}
.example { 
background-color: transparent!important;
color: #1D580E;
font-size: 0.9em;
font-weight: 400;
padding: 0 0 0 15px!important;
margin-bottom: 0px !important;
 }
.args { 
background-color: silver!important;
border-radius: 15px 15px 0 0!important;
margin-top: 10px!important;
width: 100%;
text-shadow: 0 1px 1px #ddd!important;
}
.args .desc a, .args .desc a:hover { 
color: #0F67A1;
font-size: 1em!important;
font-weight: normal!important;
}
.args .desc { 
float: right;
padding-right: 0.7em!important;
text-align: right;
width: 50%!important;
}
.args .name { 
padding-left: 10px!important;
margin: 0!important;
width: 46%!important;
}
.main_title .desc { 
float: right;
font-size: 0.95em;
margin-right: 0.7em!important;
}
.main_title, .arguments { 
background: gray!important;
border-radius: 20px!important;
border-bottom: 1px solid #555!important;
box-shadow: 2px 5px 15px #888!important;
color: #333!important;
width: 100%;
}
.mainLine { 
background-color: #0F67A1;
border-radius: 15px!important;
border-bottom: 1px solid #00263e!important;
color: #fff!important;
text-shadow: 0 1px 0px #00263e!important;
font-weight: bold!important;
width: 100%;
box-shadow: 2px 5px 15px #888!important;
}
span.new { 
background: gray!important;
border: none!important;
border-radius: 15px!important;
border-bottom: 1px solid #555!important;
color: #fff!important;
text-shadow: 0 1px 0 #555!important;
outline: none!important;
float: right;
margin-left: 0!important;
padding: 0px 0.31em;
width: 7em!important;
text-align: center!important;
 }
blockquote { 
margin: 0!important;
border: 1px solid gray!important;
border-radius: 15px!important;
}
.content ul li  > a, .content ul li  > a:hover {
 font-weight:normal!important; }
.content > img + a {
background-image: url(http://antonlivnev.narod2.ru/xhtml/css.png)!important;
background-repeat: no-repeat!important;
padding: 20px 53px!important;
margin-left: -30px!important;}
.content > img[src="http://support.xhtml.co.il/images/arrow40x44.gif"] {
visibility:hidden!important;}
.divImgPad > img[src="http://support.xhtml.co.il/images/xhtml-html5-web.png"] {
visibility:hidden!important;}
.divImgPad {
background-image: url(http://antonlivnev.narod2.ru/xhtml/html.png)!important;
background-repeat: no-repeat!important; }

/* Комментарии */
.content_comm .main_title_comm {
float: left!important;
height: 2.3em!important;
width: 100% !important;
}
.facebook_thread {
background: transparent!important;
border-radius: 20px!important;
margin-bottom: 15px!important;
padding: 0px!important;
box-shadow: 2px 5px 15px #888!important;
width: 100%!important;
height: 100%!important;
}
.feedbackRow .end {
 color: green!important; }
.fb_iframe_widget {
 width: 99%!important; }

/* Кнопки */
div.previous_page a.previous_page, div.next_page a.next_page, input[type="button"], div.example_button a.example_button { 
background-color: gray!important;
border: 1px solid gray!important;
border-bottom: 1px solid #555!important;
border-radius: 15px!important;
color: #252525!important;
text-shadow: 0px 1px 1px #999!important;
font-size: 1em!important;
font-weight: normal!important;
height: 1.65em!important;
line-height: 1.7em!important;
margin: 0px 0.63em!important;
width: 9.6em!important;
outline: none!important;
box-shadow: 1px 3px 12px #666!important;
}
.srch_btn a:hover, div.example_button a.example_button:hover, div.previous_page a.previous_page:hover, div.next_page a.next_page:hover, div.btn-green131x32Example a.btn-green131x32Example:hover, input[type="button"]:hover {
border-bottom: 1px solid #555!important;
background-color: gray!important;
color: lime!important;
text-shadow: 0 0 10px #fff!important;
}
div.btn-green131x32Example a.btn-green131x32Example {
background-color: gray!important;
border: 1px solid gray!important;
border-bottom: 1px solid #555!important;
border-radius: 20px!important;
color: #252525!important;
text-shadow: 0px 1px 1px #999!important;
font-size: 1em!important;
font-weight: normal!important;
height: 1.65em!important;
line-height: 1.7em!important;
margin: 0px 0.63em!important;
width: 20em!important;
outline: none!important;
box-shadow: 1px 3px 12px #666!important;
}
.playitbtn a { 
background: gray!important;
color: #333!important;
border-radius: 15px!important;
border-bottom: 1px solid #555!important;
font-weight: normal!important;
text-shadow: 0px 1px 1px #999!important;
}
.playitbtn a:hover { 
background: gray!important;
color: #111!important;
border-radius: 15px!important;
border-bottom: 1px solid #555!important;
font-weight: normal!important;
text-shadow: 0px 1px 1px #999!important;
}
/* Заголовки */
h1 {
font-weight: bold;
margin-bottom: 5px;
margin-top: 5px;
}
.pagetitle h1 { 
font-size: 1.6em!important;
line-height: 0.9em!important;
text-align: left!important;
}
.content h2 {
color: #444!important;
font-size: 1.3em!important;
font-weight: bold!important;
line-height: 1em!important;
margin: 0.5em 0px!important;
}
.main_title .name h2 {
color: lime !important;
text-shadow: 0 0 20px #fff!important;
}
.main_title .desc, .main_title .fullname h2, .main_title .desc a, .arguments .name h3, .arguments .version, .arguments .version a {
color: #333 !important;
text-shadow: 0 1px 1px #999!important;
background: transparent!important;
}
.main_title .desc a:hover, .arguments .version a:hover {
color: #111 !important;}

/* Таблицы */
th { 
background-color: #999!important;
color: #222!important;
text-align: center!important;
text-shadow: 0 1px 1px #aaa!important;
}

/* Листинги */
.example_in { 
background-color: #fff;
border: 1px solid gray;
border-radius: 15px!important;
box-shadow: inset 1px 3px 7px #888!important;
direction: ltr;
margin: 0!important;
padding-left: 0!important;
width: 99!important;
}
pre, a.external { 
font-size: 1.2em!important;
font-weight: normal!important;
line-height: 1em!important;
}
pre {
 padding-left: 5px!important; }
iframe { 
border: none!important;
border-radius: 15px!important;
min-height: 250px!important;
height: auto!important;
margin-top: 0!important;
padding-left: 0!important;
background: transparent!important;
width: 100%!important;
}
td > iframe {
 height: 30em!important;}
.code_input {
padding: 5px 0 0 5px!important;
box-shadow: inset 1px 3px 7px #888!important;
color: #0C125F;
font-size: 1em!important;
font-weight: normal!important;
height: 30em!important;
overflow: auto;
width: 100%;
}
.result_header, .bottomtext { 
color: #3C3C3C;
font-size: 0.8em!important;
text-shadow: 0 1px 1px #fff!important;
}
.result_header {
border-radius: 20px!important;
border-bottom: 1px solid #888!important;
border-top: 1px solid #fff!important;
}
.bottomtext {
background: transparent!important;
border: none!important;
height: 30px!important;
}
div.pagetitle { 
color: #278F0D;
float: left;
font-size: 1em!important;
font-weight: bold!important;
min-width: 1.88em;
width: 95%;
}
.tryit-header .pagetitle h1, .playit-header .pagetitle h1 { 
padding-left: 1em;
padding-right: 1em;
text-align: center!important;
}
.maintable[border]{
background-color: transparent!important;
border: none!important;
height: 23.75em;
padding: 0px 0.63em;
width: 100%;
 }
code { 
color: #125F23;
direction: ltr;
font-size: 1.2em!important;
font-weight: normal!important;
}
div.maincol100 > form#viewForm > table.maintable > tbody > tr > td {
 padding-left: 10px!important; }
.example_code, .code td, code div {
border: none!important;
width: 100%!important;
 }

/* Иконки браузеров */
.bsProperty {
height: 67px!important;}
.IE_browser, .IE_browserEx , .bsIE {
 background: none!important; background: url("http://antonlivnev.narod2.ru/xhtml/ie.png") no-repeat transparent!important; height: 64px!important; width: 64px!important; float: right!important; border: none!important; }
.FF_browser, .FF_browserEx, .bsFirefox {
 background: none!important; background: url("http://antonlivnev.narod2.ru/xhtml/firefox.png") no-repeat transparent !important; height: 64px!important; width: 64px!important; float: right!important; border: none!important; }
.Opera_browser, .Opera_browserEx , .bsOpera {
 background: none!important; background: url("http://antonlivnev.narod2.ru/xhtml/opera.png") no-repeat transparent !important; height: 64px!important; width: 64px!important; float: right!important; border: none!important; }
.Safari_browser, .Safari_browserEx , .bsSafari {
 background: none!important; background: url("http://antonlivnev.narod2.ru/xhtml/safari.png") no-repeat transparent !important; height: 64px!important; width: 64px!important; float: right!important; border: none!important; }
.Chrome_browser, .Chrome_browserEx , .bsChrome {
 background: none!important; background: url("http://antonlivnev.narod2.ru/xhtml/chrome.png") no-repeat transparent !important; height: 64px!important; width: 64px!important; float: right!important; border: none!important; }

.bsIE, .bsFirefox, .bsOpera, .bsSafari , .bsChrome {
 float: none!important; background-position: 0 3px!important;}
.IE_browserEx, .FF_browserEx, .Opera_browserEx, .Safari_browserEx , .Chrome_browserEx {
 background-size: 32px 32px!important; height: 38px!important; width: 32px!important;}
.small_browser_support {
 width: 100%!important; margin-right: 12px!important;}

ul.toc-list, ul.toc-list a , ul.toc-list a:hover {
 font-weight: normal!important;}
ul.toc-list > li > a, ul.toc-list > li > a:hover {
 font-weight: bold!important;}
 
/* Блог */
#branding hgroup {
display: none!important;
}
#branding #searchform {
position: absolute!important;
right: 3%!important;
text-align: right!important;
top: 0.4em!important;
}
#branding #s {
border: none!important;
border-radius: 20px!important;
box-shadow: inset 1px 2px 7px #000!important;
}
.assistive-text {
clip: rect(1px, 1px, 1px, 1px)!important;
text-shadow: 0 -1px 0 #000!important;
}
body.blog, body.single, #page, #primary {
background-color: #eee!important;
}
#branding {
border:none!important;
padding-bottom: 0px!important;
}
#main {
padding: 0!important;
}
#content { 
margin: 0px 34% 0px 1%!important;
width: 70%!important;
}
.entry-header {
border-radius: 20px 20px 0 0!important;
padding: 10px!important;
background-color: #ccc!important;
box-shadow: 2px 13px 15px #333, inset 0 0 3px #777!important;
background: radial-gradient(50px 2px, cover, #777, #000)!important;
background: -o-radial-gradient(50px 2px, cover, #777, #000)!important;
background: -moz-radial-gradient(50px 2px, cover, #777, #000)!important;
background: -webkit-radial-gradient(50px 2px, cover, #777, #000)!important;
}
#access { 
box-shadow: 0 10px 15px #333!important;
float: left!important;
margin: 0px auto 15px!important;
border-radius: 20px!important;
background: linear-gradient(bottom, #000 10%, #555 100%)!important;
background: -o-linear-gradient(bottom, #000 10%, #555 100%)!important;
background: -moz-linear-gradient(bottom, #000 10%, #555 100%)!important;
background: -webkit-linear-gradient(bottom, #000 10%, #555 100%)!important;
}
#access li > a, #access a {
text-shadow: 0 -1px 0 #000!important;
}
#access li:hover > a, #access a:focus { 
background: linear-gradient(to bottom, #aaa, #000) transparent!important;
background: -o-linear-gradient(to bottom, #aaa, #000) transparent!important;
background: -moz-linear-gradient(to bottom, #aaa, #000) transparent!important;
background: -webkit-linear-gradient(to bottom, #aaa, #000) transparent!important;
color: #fff!important;
text-shadow: 0 1px 0 #000!important;
}
.entry-title { 
clear: both!important;
font-weight: 700!important;
line-height: 1.5em!important;
padding-bottom: 0.3em!important;
padding-top: 0px!important;
padding-right: 55px!important;
color: #000!important;
font-size: 20px!important;
text-shadow: 0px 1px 1px #777!important;
}
.entry-title a { 
color: #111!important;
text-decoration: none!important;
}
.entry-title a:hover, .entry-title a:focus, .entry-title a:active { 
color: #222!important;
}
.entry-header .comments-link a { 
background: url("http://antonlivnev.narod2.ru/xhtml/aim0.png") no-repeat transparent!important;
color: #333!important;
font-size: 13px!important;
font-weight: normal!important;
height: 36px!important;
line-height: 35px!important;
overflow: hidden!important;
padding: 0px!important;
position: absolute!important;
right: 10px!important;
text-align: center!important;
text-decoration: none!important;
text-shadow: 0 1px 0 #000!important;
top: 1em!important;
width: 43px!important;
}
.entry-header .comments-link a:hover {
background: url("http://antonlivnev.narod2.ru/xhtml/aim.png") no-repeat transparent!important;
}
#page { 
margin: 1em auto!important;
max-width: 1000px!important;
}
.textwidget form {
border: none!important;
}
p { 
margin-bottom: 0.6em!important;
}
.entry-content, .entry-summary { 
padding: 1em 1em 0.1em!important;
border-radius: 0 0 20px 20px!important;
box-shadow: 2px 10px 15px #333!important;
}
.entry-content p {
font-family: 'Times New Roman', Times, serif!important;
color: #222!important;
}
.entry-meta { 
clear: both!important;
color: #222!important;
font-size: 12px!important;
line-height: 18px!important;
border-radius: 10px!important;
box-shadow: 1px 10px 15px #333!important;
width: 23em!important;
padding-left: 1em!important;
margin: 5px auto!important;
background: radial-gradient(160px 0px, cover, #aaa, #101010)!important;
background: -o-radial-gradient(160px 0px, cover, #aaa, #101010)!important;
background: -moz-radial-gradient(160px 0px, cover, #aaa, #101010)!important;
background: -webkit-radial-gradient(160px 0px, cover, #aaa, #101010)!important;
text-shadow: 0 1px 1px #555!important;
}
.entry-header .entry-meta {
padding: 1px 5px 1px 5px!important;
margin: 0px!important;
box-shadow: 1px 2px 6px #111!important;
border-radius: 7px!important;
background-color: #efefef!important;
background: radial-gradient(80px 5px, cover, #777, #000)!important;
background: -o-radial-gradient(80px 5px, cover, #777, #000)!important;
background: -moz-radial-gradient(80px 5px, cover, #777, #000)!important;
background: -webkit-radial-gradient(80px 5px, cover, #777, #000)!important;
width: 13em!important;
color: #111!important;
text-shadow: 0 1px 1px #555!important;
}
.entry-meta a { 
text-shadow: 0 -1px 0 #333!important;
}
.hentry, .no-results {
border: none!important;
}
.widget { 
clear: both!important;
margin: 0px 0px 0.5em!important;
}
#primary { 
float: left!important;
margin: 0px -28% 0px 0px!important;
width: 100%!important;
}
#secondary { 
float: right!important;
margin-right: 0.6%!important;
width: 25%!important;
border-radius: 20px!important;
padding: 10px!important;
box-shadow: 2px 10px 15px #333, inset 0 0 5px #333!important;
background-color: #555!important;
text-shadow: 0 1px 0 #222!important;
color: #fff!important;}
.widget ul li { 
list-style-position: inside!important;
}
.widget-title { 
color: #fff!important;
font-size: 10px!important;
font-weight: 500!important;
letter-spacing: 0.1em!important;
line-height: 2.6em!important;
text-transform: uppercase!important;
text-align: center!important;
border-radius: 15px!important;
box-shadow: 1px 4px 8px #111!important;
margin-bottom: 10px!important;
background: radial-gradient(50px 2px, cover, #777, #000)!important;
background: -o-radial-gradient(50px 2px, cover, #777, #000)!important;
background: -moz-radial-gradient(50px 2px, cover, #777, #000)!important;
background: -webkit-radial-gradient(50px 2px, cover, #777, #000)!important;
}
.entry-content {
background: radial-gradient(50px 2px, cover, #ddd, #555)!important;
background: -o-radial-gradient(50px 2px, cover, #ddd, #555)!important;
background: -moz-radial-gradient(50px 2px, cover, #ddd, #555)!important;
background: -webkit-radial-gradient(50px 2px, cover, #ddd, #555)!important;
}
header.entry-header h1 + div, header.entry-header h1 + div + div, header.entry-header + div {
display: none!important;
}
header.entry-header:hover h1 + div, header.entry-header:hover h1 + div + div, header.entry-header:hover + div, header.entry-header + div:hover {
display: block!important;
cursor: pointer!important;
}
header.entry-header + div {
border-radius: 0 0 28px 28px!important;
}
header.entry-header, header.entry-header + div:hover {
border-radius: 28px!important;
}
header.entry-header:hover {
border-radius: 28px 28px 0 0!important;
}
aside h3 + ul {
height: 0!important;
overflow: hidden!important;
list-style-type: none!important;
}
aside h3 + div {
height: 0!important;
overflow: hidden!important;
}
aside:hover h3 + ul, aside:hover h3 + div, aside:hover {
height: auto!important;
overflow: visible!important;
cursor: pointer!important;
}
aside.widget ul li a, aside.widget_recent_entries ul li a { 
font-weight: normal!important;
color: #ccc!important;
margin: 0 5px!important;
padding: 2px 10px!important;
width: 100%!important;
}
