	#container {width:745px !important; background:none !important}
	.full-page-holder {padding-top:0px !important; color:#000 !important;}
	.full-page-holder a {color:rgb(51,102,153) !important;}
	.status-holder {color:#000 !important;}
	body {background-image:none !important;}
	#opi,.full-page-holder,.profile-panel,.full-page{cursor:inherit !important;}
	#newBackground {background:none !important;}
	.user-name {white-space:normal !important;}
	.panelbarbutton {width:80px !important; padding:0px 24px 0px 0px !important;}
	
	/* This might be buggy, be careful! */
	.panelbarbutton:not([onselectstart="return false;"]) {display:none !important;}
	
	#profile_wrapper {margin:0px auto;}
	.profile-summary dl {display:none !important;}
	a.nav-drop-menu-item:nth-of-type(1) {display:none !important;}
	a.nav-drop-menu-item:nth-of-type(2) {border-top:1px solid #eeeeee;}
	a.optionprofileskin {display:none !important;}
	a.optionlover {display:none !important;} /* WTF is this thing anyway? */
	.avatar_title span a {display:none !important}
	a.vip-icon {display:none !important;}
	img[alt="vip"] {display:none !important;}
	.viewer-actions li:nth-of-type(1) {display:none !important;}
	.viewer-actions li:nth-of-type(3) {display:none !important;}
	.main-side .mod:nth-child(3) {display:none !important;}
	#miniFeed {border:none !important;}
	.nav-tabs {margin-bottom:0px !important;}
	.profile-summary {background:none repeat scroll 0% 0% rgb(236, 239, 245) !important;}
	
	/* No one knows how to spell at renren apparently */
	.avarar-tabs {display:none !important; }
	li#prifile-avatar-image {display:inline !important;}
	li#prifile-avatar-purikura {display:none !important;}
	
	.latest-photos {background-color:inherit !important; border:inherit !important;}
	.filter li.c a {margin-bottom:-1px !important;border-style:solid !important; border-color:rgb(206,225,238) !important; border-width:1px 1px 0px !important; border-bottom:0px !important;}
	ul.filter.tabs.clearfix {border-bottom:1px solid rgb(206,225,238) !important;}
	.mod { border:1px solid rgb(206,225,238) !important;}
	.latest-photos {background-color:#fff !important;}
	.mod-body-out {background-color:#fff !important;}
	.mod-header h4 {color: #000 !important;}
	.mod-header, .mod-header-in {background:none repeat scroll 0% 0% rgb(240,245,248) !important;}
    .personal-url { color:rgb(102,102,102) !important;}
    .tabs-holder {border-bottom:1px solid rgb(160,190,220) !important;width:100% !important;}
    .viewer-actions a {border-bottom:1px solid rgb(216, 223, 234) !important;}
    .tabs-holder li.current a {padding:5px 19px 6px 20px !important;
        background:none !important; border-width:1px !important; border-style:solid;
        border-color: rgb(160,190,220) rgb(160,190,220) rgb(255,255,255) !important;
        border-image:none !important;
    }
    .tabs-holder .tabpanel a {
        float:left !important; font-weight:bold !important; color:rgb(0,94,172) !important;
        border-bottom:1px solid rgb(160,190,220) !important; background:rgb(227,238,248) !important;
    }
    #site-menu-nav.site-menu-apps h3 {display:none !important;}
    #newsfeed-module-box .time { color:rgb(204, 204, 204) !important; }
    h1.username.lively-user {color:#000 !important;}
    
    /* Something to deal with the old nav */
    a[href="http://game.renren.com/"] {display:none !important;}
    
    /* Login page */
    .login #header #navBar .navigation-wrapper { width:360px !important; }
		.login #header #navBar .navigation-wrapper #logo2 { padding-left:81px !important; }
		.login .ft-wrapper { display:none !important; }
    .login-page {width:360px !important;}
    .login-page .side-column {width:360px !important; height:auto !important;}
    .regnow {background:none !important;}
    .login-form dl dd {width:inherit !important;}
    .login-form dl.bottom {text-align:center;}
    .regnow {text-align:center;padding:0px !important;}
    .newStudentReg {display:none !important;}
    dl.login_corp {text-align:center;margin-top:20px !important;}
    dd.login_list {margin-right:20px !important;} /* Dear god these people suck at their job */
    body#syshome #header #navBar .navigation-wrapper .navigation .nav-body .nav-other {display:none !important;}
}

@-moz-document regexp("https?://(.*\\.)?renren\\.com.*profile.*") {
	.navigation-wrapper {width:980px !important;}
	#container {width:inherit !important;}
}

@-moz-document regexp("https?://(.*\\.)?renren\\.com.*friend.*") {
	.navigation-wrapper {width:980px !important;}
	#container {width:980px !important;}
	#main {width:850px !important;overflow:visible;}
	#container2 {width:600px !important;overflow:visible;}
	#main2 {width:600px !important;overflow:visible;}
}

@-moz-document regexp("https?://(blog|safe)\\.renren\\.com.*") {
	.navigation-wrapper {width:980px !important;}
	#container {width:inherit !important;}
}
