css:
@-moz-document url-prefix("http://eat.awiki.org/") {
body {
background:#131313 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
}

div.post img.post-image {
padding: 5px;
margin: 5px 20px 10px 0;
}

.box.left {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
border: 1px solid #303030;
float: left;
}

.box.left h2 {
background:#262626 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
border-bottom: 1px solid #303030;
}

.box.right {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
border: 1px solid #303030;
float: right;
}

.box.right h2 {
background:#262626 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
border-bottom: 1px solid #303030;
}

.box.bleft {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
border: 1px solid #303030;
float: left;
}

.box.bleft p {
padding-left: 8px;
}

.box.bleft h2 {
background:#262626 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
border-bottom: 1px solid #303030;
}

.COINWIDGETCOM_CONTAINER {
padding-left: 5px;
}

div.banner {
color: #e5e5e5;
}

a, a:visited, a:active {
color: #a6a6a6;
text-decoration: none!important;
-webkit-transition:color .5s ease-in!important;  
-moz-transition:color .5s ease-in!important;  
-o-transition:color .5s ease-in!important;  
transition:color .5s ease-in!important;
}

a:hover {
color: #BADAFF;
}

div.post.reply {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
border-color: #303030;
-webkit-border-radius: 6px;
-moz-border-radius: 6px;
-ms-border-radius: 6px;
-o-border-radius: 6px;
border-radius: 6px;
color: #e5e5e5;
width: 100%;
}

form table tr th {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
border-color: #303030;
-webkit-border-radius: 6px;
-moz-border-radius: 6px;
-ms-border-radius: 6px;
-o-border-radius: 6px;
border-radius: 6px;
color: #e5e5e5;
}

p {
color: #e5e5e5;
}

hr {
border-top: 1px solid #161616;
}

div.blotter {
color: #e5e5e5;
}

p.intro span.subject {
color: #FADB87;
font-weight: bold;
}

span.trip {
color: #CEFA87;
}

span.quote {
color: #CEFA87;
}

p.intro span.name {
color: #BADAFF;
}

p.intro a.email span.name {
color: #FAF6BB;
text-decoration: none!important;
-webkit-transition:color .5s ease-in!important;  
-moz-transition:color .5s ease-in!important;  
-o-transition:color .5s ease-in!important;  
transition:color .5s ease-in!important;
}

p.intro a.email span.name:hover {
color: #BADAFF;
}

div.post div.body {
color: #e5e5e5;
padding: 10px;
}

span.omitted {
color: #666;
}

div.banner {
background-color: transparent;
}

header div.subtitle, h1 {
color: #FADB87;
}

div.post.reply div.body a {
color: #FADB87;
}

div.post.reply.highlighted {
background:#303030 url(http://i.imgur.com/iQiT2Yo.png)!important;
}

div.pages {
background:#202020 url(http://i.imgur.com/iQiT2Yo.png)!important;
border-color: #303030;
-webkit-border-radius: 6px;
-moz-border-radius: 6px;
-ms-border-radius: 6px;
-o-border-radius: 6px;
border-radius: 6px;
color: #e5e5e5;
}

div.pages a.selected {
color: #FADB87;
text-decoration: none!important;
-webkit-transition:color .5s ease-in!important;  
-moz-transition:color .5s ease-in!important;  
-o-transition:color .5s ease-in!important;  
transition:color .5s ease-in!important;
}

div.post.reply div.body a {
color: #FADB87;
text-decoration: none!important;
-webkit-transition:color .5s ease-in!important;  
-moz-transition:color .5s ease-in!important;  
-o-transition:color .5s ease-in!important;  
transition:color .5s ease-in!important;
}

div.pages a.selected:hover {
color: #BADAFF;
}

div.post.reply div.body a:hover {
color: #BADAFF;
}

div.thread:hover {
background:#303030 url(http://i.imgur.com/iQiT2Yo.png)!important;
border-color: #303030;
-webkit-border-radius: 6px;
-moz-border-radius: 6px;
-ms-border-radius: 6px;
-o-border-radius: 6px;
border-radius: 6px;
color: #e5e5e5;
}

div.ban {
background:#262626 url(http://i.imgur.com/iQiT2Yo.png)!important;
border: 1px solid #303030;
max-width: 700px;
margin: 30px auto;
}

div.ban h2 {
background:#262626 url(http://i.imgur.com/iQiT2Yo.png)!important;
color: #e5e5e5;
}
}
