css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("teuxdeux.com") {
/*
Palette: http://www.colourlovers.com/palette/2616106/Omar

Colors:
FFF9E6 - Cream
8CC2B6 - Green
EB8E4B - Orange
EB4B4B - Red
422727 - Brown
*/



header { background:#422727!important; }
#calendar .present { color: #EB4B4B!important; }

/* Highlight current task */
#calendar .todo-item:not([class~="is-done"]) { opacity:1!important;font-size: 17px!important; background:#EB4B4B!important; color:#fff!important; padding: 0 5px!important; }
#calendar .todo-item:not([class~="is-done"]) ~ .todo-item:not([class~="is-done"]) { opacity:0.5!important; font-size: 16px!important; background:none!important; color:#EB4B4B!important; }

/* Bottom section */
#lists,.list-column { background:#FFF9E6!important; }
#lists .dot-nav { background:#8CC2B6!important; }

#lists .dot-item-fill { background-color:#EB8E4B!important; }
#lists .dot-item.is-selected .dot-item-fill { background-color:#FFF9E6!important; }

#lists .new-list-button { color: #FFF9E6!important; }
#lists:hover .new-list-button { color: #422727!important; }

/* List tooltips */
#lists .dot-item-label { background-color:#8CC2B6!important; }
#lists .dot-item-label-point { color:#8CC2B6!important; }

/* Bottom columns */
.list-column h3 { color:#EB8E4B!important; text-transform: none!important; }
}
