css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://www.facebook.com/") {
/******text colors******/
/*(85%,0%,0%,.9) - red*/
/*(0%,85%,0%,.9) - green*/
/*(25%,65%,100%,.9) - blue*/
/*(100%,100%,100%,.9) - gray*/


/*post's text color*/
.userContent {color:rgba(100%,100%,100%,.5) !important;}
/*post's link color*/
#rightCol A{color:rgba(25%,65%,100%,.9) !important;}
/*right ccolumn link color*/
#contentCol A.ego_title,#rightCol STRONG{color:rgba(100%,100%,100%,.35) !important;}
/*like button text*/
#mainContainer .uiIconText {color:rgba(100%,100%,100%,.35) !important;}
/*leave comment text*/
#pagelet_home_stream .storyInnerContent INPUT[value="Comment"]
{color:rgba(25%,65%,100%,.9) !important;}
/*post's link color*/
#pagelet_home_stream A {color:rgba(25%,65%,100%,.9) !important;}
/*storyt title color*/
#pagelet_home_stream STRONG {color:rgba(25%,65%,100%,.9) !important;}
/*dock chat title color*/
#fbDockChatBuddylistNub .label {color:rgba(25%,65%,100%,.9) !important;}
/*post timestamp*/
.timestamp.livetimestamp{color:rgba(100%,100%,100%,.35) !important;}
/*left column number news*/
#leftCol #sideNav .count.uiSideNavCount {color:rgba(25%,65%,100%,.9) !important;}
/*welcome name*/
#leftCol .clearfix.fbxWelcomeBox a{color:rgba(25%,65%,100%,.9) !important;}
/*post button*/
#pagelet_composer BUTTON {background:rgba(70,70,70,.7) !important;background-image:none !important;
border-color:rgba(100,100,100,.9) !important;}
/*top nav link colors*/
#headNav A.navLink.bigPadding {color:rgba(25%,65%,100%,.9) !important;}


#leftCol {position:fixed !important;margin:23px 0px 0px 0px}


/*top bar*/
#blueBar.fixed_elem {background-image:none !important;background:rgba(50,50,50,.85) !important;}
#pagelet_bluebar {background-image:none !important;background:rgba(0,0,0,0) !important;}
#blueBarHolder.slim {background-image:none !important;background:rgba(0,0,0,0) !important;}

/*body / wallpaper*/
HTML > BODY > ._li
{background-position:center !important; background-repeat:no-repeat;
background-image:url('http://www.dpaperwall.com/wp-content/uploads/2013/09/Anonymous-HD-Wallpaper-Hack.png') !important;}

BODY > ._li {background:rgba(0,0,0,.0) !important;}

body,html,#facebook.tinyViewport {background:rgba(0,0,0,.85) !important}
#contentCol {background:rgba(0,0,0,.75) !important;border-width:0px !important;}
.hasSmurfbar #blueBarHolder #blueBar:after {border-width:1px 0px 0px 0px;
border-style:solid;border-color:rgba(100,100,100,.9) !important;background-image:none !important;}



/*whats on you're mind, post*/
#contentArea ._2yg {background:rgba(0,0,0,.9) !important;border-radius:3px;border-color:rgba(100,100,100,.9) !important;}
._2yg .clearfix {background:rgba(0,0,0,.85) !important;border-radius:0px;border:0px !important;}
._55d0{margin: 0px 0px 0px -17px !important;border-radius:3px;border:0px !important;}
#pagelet_composer ._1dsp {border-color:rgba(100,100,100,.9) !important;}

/*main content*/
#mainContainer {background:rgba(100,100,100,.1) !important;border-width:1px 1px 1px 1px;
border-style:solid;border-color:rgba(255,255,255,.15) !important;padding:0px 0px 0px 5px;
box-shadow: 0px 0px 2px 5px rgba(0,0,0,.25);}
#globalContainer {border-width:0px !important;}
#contentArea {margin: 0px 0px 5px 5px;width:507px !important;}
#rightCol {margin: 0px 0px 0px 0px;}
/*each story*/
.uiList.uiStream.uiStreamHomepage > * 
{width:505px !important;border-radius:3px;}

/*post's pictures*/
#home_stream .shareRedesign._gxb.fullWidthMedia,#home_stream .clearfix.photoRedesign {width:100% !important;box-shadow: 3px 3px 3px 2px rgba(0,0,0,.25);
padding:7px 9px 7px 7px !important; background:rgba(100,100,100,.1) !important;border-width:1px 1px 1px 1px !important;
border-style:solid !important;border-color:rgba(255,255,255,.2) !important;padding:0px 0px 0px 5px;border-radius:3px;}
#home_stream .uiPhotoThumb.photoRedesignAspect {border-width:1px 1px 1px 1px !important;
border-style:solid !important;border-color:rgba(255,255,255,.3) !important;}

#pagelet_home_stream .uiScaledImageContainer.photoWrap,#pagelet_home_stream .uiPhotoThumb.photoRedesignAspect{opacity:0.65}


/*comments*/

.UFIRow.UFIComment.display {background:rgba(100,100,100,.1) !important;}
.UFIRow.UFIUnseenItem {background:rgba(100,100,100,.1) !important;}
.UFIRow.UFIAddComment {background:rgba(100,100,100,.1) !important;}
#pagelet_home_stream .UFIList {background:rgba(100,100,100,.1) !important;border-width:1px 1px 1px 1px;
border-style:solid;border-color:rgba(255,255,255,.15) !important;padding:0px 0px 0px 5px;border-radius:3px;}
.UFIRow.UFILikeSentence,.UFIRow.UFIPagerRow{background:rgba(100,100,100,.1) !important;}
.uiStream.uiStreamHomepage .UFIRow{background:rgba(100,100,100,.1) !important;}
.UFIBlingBox.uiBlingBox.feedbackBling {border:none !important}

/*write comment*/
#contentArea #home_stream .innerWrap{background:rgba(0,0,0,.8) !important;}
.uiTypeahead.mentionsTypeahead{border-color:rgba(0,0,0,.6) !important;}
#home_stream .storyInnerContent .uiUfi.UFIContainer {box-shadow: 2px 3px 3px 2px rgba(0,0,0,.2) !important;}
#home_stream .uiStreamFooter {padding:0px 0px 13px 0px }


/*simular to...*/
.uiStreamEdgeStoryLineWithLabel .uiStreamEdgeStoryLineTx {background:rgba(100,100,100,.1) !important;border-width:1px 1px 1px 1px !important;
border-style:solid !important;border-color:rgba(255,255,255,.2) !important;padding:0px 0px 0px 5px;border-radius:3px;}




/*left column*/
#leftCol #sideNav .item.clearfix ,.item.clearfix.sortableItem {margin:1px 1px 1px 1px;border-width:1px 1px 1px 1px;border-style:solid;border-color:rgba(100,100,100,.5) !important;
padding:5px;border-radius:3px;background:rgba(100,100,100,.1) !important;}
#leftCol #sideNav .count.uiSideNavCount {background:rgba(100,100,100,.0) !important;}
#leftCol > #pagelet_welcome_box {padding:5px 0px 5px 5px !important; background:rgba(100,100,100,.1) !important;border-width:1px 1px 1px 1px !important;
border-style:solid !important;border-color:rgba(255,255,255,.2) !important;padding:0px 0px 0px 5px;border-radius:3px;}
/*left col title headers*/
#sideNav .navHeader {display:none !important}



/*right column, people you may know*/
#rightCol > .home_right_column.tickerOnTop,.rightColumnWrapper.fixed_elem
{border-width:1px 1px 1px 1px;border-style:solid;border-color:rgba(100,100,100,.5);
padding:5px;border-radius:3px;box-shadow: 3px 3px 3px rgba(0,0,0,.35);background:rgba(255,255,255,.05);
}
.home_right_column.tickerOnTop .clearfix.ego_unit,.rhcFooterBorder {border:none !important;}

#rightCol > .home_right_column.tickerOnTop {margin: 10px 0px 0px -18px;}
.rightColumnWrapper.fixed_elem {margin: 29px 0px 0px -7px;}
#rightCol {margin: 11px 0px 0px -7px !important;}
/*title headers*/
#pagelet_ego_pane .uiHeader.uiHeaderTopBorder.mbs.uiSideHeader
{background:rgba(255,255,255,.05) !important;border-radius:3px;
border-width:1px 1px 1px 1px !important;border-style:solid;border-color:rgba(100,100,100,.7) !important;
box-shadow: 3px 3px 0px rgba(0,0,0,.1);}
/*right column suggestion headers*/
#rightCol .uiHeader.uiHeaderTopAndBottomBorder.uiHeaderSection{background:rgba(255,255,255,.05) !important;border-radius:3px;
border-width:1px 1px 1px 1px !important;border-style:solid;border-color:rgba(100,100,100,.7) !important;
box-shadow: 3px 3px 0px rgba(0,0,0,.1);}






/*each story*/
#home_stream > .uiUnifiedStory.uiStreamStory.genericStreamStory {border-width:1px 1px 1px 1px !important;
background:rgba(255,255,255,.05) !important;margin: 5px 0px 0px 0px;border-color:rgba(100,100,100,.5) !important;
box-shadow: 3px 3px 3px rgba(0,0,0,.35);}

.clearfix.shareRedesignContainer {background:rgba(0,0,0,.0) !important;}
.pam.shareText,.shareRedesignTopBorder,.shareRedesignRightBorder,.shareRedesignBottomBorder{border-width:0px !important;}


/*sort stories*/
.mts.uiStreamHeader {background-color:rgba(100,100,100,.0) !important;}
#pagelet_home_stream #u_ps_0_0_1,.uiStreamHeaderChronologicalForm {
border-width:1px 1px 1px 1px !important;border-style:solid;
background:rgba(255,255,255,.05) !important;margin: -11.5px -35px 0px 0px;
border-color:rgba(200,200,200,.25) !important;
box-shadow: 3px 3px 3px rgba(0,0,0,.35);border-radius:3px;}


/*hide story arrow*/
.uiStream .uiStreamStory .highlightSelector ,.highlightSelectorButton{
margin: 1.5px 23px 0 0 !important;background-color:rgba(100,100,100,.0) !important;opacity:0.7;
}

/*chat*/
.fbNubButton {height:26px !important;margin:-10px 13px 0px -50px;background-color:rgba(100,100,100,.2) !important;border-color:rgba(200,200,200,.25) !important;}
}





@-moz-document url("https://www.facebook.com/blah") {
}
