css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.linux.org.ru/wiki/"),
               url-prefix("http://www.linux.org.ru/wiki/") {
body {
	background: #2E3436 ;
    background-image: inherit ;
	color: #BABDB6 ;
	margin: 0 ;
	padding: 0 ;
}
body, input, select {
	font: 95% sans-serif, tahoma ;
}
a {
	text-decoration: none ;
	color: #729FCF ;
}

table {
	background-color: #272C2D ;
	border-color: #BABDB6 ;
}



a:hover {
	border-bottom: 0px ;
}
a.logo {
	border: 0 ;
}
a.externallink:after {
	content:url("https://www.linux.org.ru/wiki/images/external.png") ;
}
a.interwikilink:after {
	content:url("https://www.linux.org.ru/wiki/images/external.png") ;
}
a.edit {
	color: #aa1111 ;
	border-color: #aa1111 ;
}
a.edit:hover {
	border-bottom: 1px dotted #aa1111 ;
}
a.redirect {
	font-size: 150% ;
	text-decoration: none ;
	margin-left: 20px ;
}
a.redirect:before {
	content:url("https://www.linux.org.ru/wiki/images/redirect-arrow.png") ;
}
p {
	margin: .4em 0 .5em 0 ;
	line-height: 1.5em ;
}
h1, h2, h3, h4, h5 {
	color: #BABDB6 ;
	margin: 0 ;
	padding: 0.5em 0em 0.2em 0em ;
	text-align: left ;
}
h1, h2 {
	margin-bottom: 0.5em ;
}
h3, h4, h5 {
	margin-bottom: 0.2em ;
}
h1 {
	border-bottom: 1px solid #D8D8E7 ;
	font-size: 200% ;
	font-weight: normal ;
}
h2 {
	border-bottom: 1px solid #D8D8E7 ;
	font-size: 175% ;
	font-weight: normal ;
}
h3 {
	font-size: 150% ;
	font-weight: normal ;
}
h4 {
	font-size: 125% ;
	font-weight: bold ;
}
h5 {
	font-size: 110% ;
	font-weight: bold ;
}
pre {
	background-color: #2E3436 ;
	border: 0px ;
	color: white ;
	border-radius: 1em ;
	font-size: 110% ;
	line-height: 1.1em ;
	padding: 1em ;
}
code {
	color: white ;
	font-size: 120% ;
}
dt {
	font-weight: bold ;
	margin-bottom: .1em ;
}
dl {
	margin-top: .2em ;
	margin-bottom: .5em ;
}
dd {
	line-height: 1.5em ;
	margin-left: 2em ;
	margin-bottom: .1em ;
}
ul {
	line-height: 1.5em ;
	list-style-type: square ;
	list-style-image: url(https://www.linux.org.ru/wiki/images/bullet.gif) ;
	margin: .3em 0 0 1.5em ;
	padding: 0 ;
}
ol {
	line-height: 1.5em ;
	margin: .3em 0 0 3.2em ;
	padding: 0 ;
	list-style-image: none ;
}
li {
	margin-bottom: .1em ;
}
textarea {
	width: 99% ;
}
fieldset {
	padding: 0 7px 7px ;
	border: 1px solid #D8D8E7 ;
	margin-top: 10px ;
}
legend {
	padding: 0.5em ;
	font-weight: bold ;
}
.red {
	font-weight: bold ;
	color: #f00 ;
}
.green {
	font-weight: bold ;
	color: #090 ;
}
div.message {
	margin: 1.5em 0 1.5em 0 ;
	padding: 0 5px ;
}
div.submenu {
	margin: 1.0em 6em 0.8em 6em ;
	text-align: center ;
}
.clear {
	clear: both ;
}
.hidden {
	display: none ;
}

/* global positioning */

#wiki-page {
	font-size: 100% ;
	color: black ;
	padding: 0 5px ;
}
#wiki-navigation {
	position: absolute ;
	top: 5px ;
	left: 5px ;
	font-size: 90% ;
	padding: 5px 5px 5px 0 ;
	vertical-align: top ;
	width: 180px ;
}
#wiki-content {
	position: relative ;
	margin-left: 185px ;
	font-size: 100% ;
	vertical-align: top ;
}
#wiki-footer {
	color: gray ;
	font-size: 80% ;
	margin-left: 185px ;
	padding: 15px 10px ;
}

/* main content area */

#contents {
	background: #272C2D ;
	padding: 14px ;
	border: 1px solid #272C2D ;
	border-radius: 1em ;
	color: #BABDB6 ;
	list-style-type: disc ;
	padding-bottom: 25px ;
	
}
#contents-header {
    background: #272C2D ;
	margin-bottom: 0.1em ;
}
#contentSub {
	color: #7d7d7d ;
	margin: 0 0 1em 1em ;
}

/* navbar elements */

#logo {
	border: 0 ;
	margin: 8px 0 0 0 ;
	text-align: center ;
	vertical-align: middle ;
}
.portlet {
	background: #272C2D ;
	border: 1px solid #272C2D ;
	border-radius: 1em ;
	font-size: 90% ;
	margin: 16px 0 8px ;
	overflow: hidden ;
	padding: 0 8px 8px ;
}
.portlet h5 {
	font-size: 90% ;
	font-weight: normal ;
	padding: 5px 0 0 ;
}
#nav-menu {
	border-bottom: 0 ;
	margin-top: 2px ;
	padding: 0 ;
}
#nav-menu hr {
	display: none ;
}
#nav-menu ul {
	padding: 8px ;
	list-style-type: none ;
	list-style-image: none ;
	border-bottom: 1px solid #D8D8E7 ;
	margin-left: 0 ;
}
#nav-search {
	text-align: center ;
}
#nav-search form {
	padding: 0 ;
	margin: 0 ;
}
#nav-search br {
	padding-top: 4px ;
}
#nav-search input {
	margin-top: 8px ;
	background-color: white ;
	border: 1px solid #D8D8E7 ;
}
#nav-search input[type="text"] {
	width: 150px ;
}

/* diff page */

#diff_heading {
	margin-top: 10px ;
	width: 98% ;
}
#diff_heading td {
	text-align: center ;
	vertical-align: top ;
	width: 50% ;
}
#left_revision, #right_revision {
	font-weight: bold ;
}
table#diff {
	table-layout: fixed ;
	width: 98% ;
}
table#diff col.diff-marker {
	width: 2% ;
}
table#diff col.diff-content {
	width: 48% ;
}
.diff-delete {
        color: black ;
	background: #eeee88 ;
	font-weight: bold ;
	padding: 0.1em ;
	overflow: hidden ;
}
.diff-add {
        color: black ;
	background: #88ee88 ;
	font-weight: bold ;
	padding: 0.1em ;
	overflow: hidden ;
}
.diff-unchanged {
	padding: 0.1em ;
	overflow: hidden ;
}
.diff-indicator {
	font-weight: bold ;
	padding: 0.1em ;
	text-align: right ;
	width: 7px ;
}
.diff-line {
	padding: 0.5em 0.1em 0.1em 0.1em ;
	font-weight: bold ;
}
.diff-entry {
	margin: 1px ;
	padding: 1px ;
}
.diff-change {
	font-weight: bold ;
	color: red ;
}

/* topic elements */

#toc {
	border: 1px solid #D8D8E7 ;
	margin: 0.2em auto 0.2em 0em ;
	padding: 0.5em 0.8em 0.5em 0.8em ;
	background: #272C2D ;
}
#toc ul {
	list-style-type: none ;
	list-style-image: none ;
	margin-left: 0 ;
}
#toc ul ul {
	margin-left: 1em ;
}
#category-index {
	border: 1px solid #aaa ;
	padding: 10px 5px 10px 5px ;
	margin-top: 1em ;
	clear: both ;
}
#topic-file-download {
	border: 1px solid #aaa ;
	background-color: #f9f9f9 ;
	padding: 10px 5px 10px 5px ;
	margin-top: 1em ;
	clear: both ;
}

/* tab menu */

#tab-menu {
margin-left: 1em ;
}

.tab-menu a {
color: #FCAF3E ;
}

ul.tab-menu {
	display: inline-block ;
	list-style: inherit ;
	list-style-type: none ;
}
ul.tab-menu li {
	color: #AAAAAA ;
	background-color:  #272C2D ;
	border: 1px solid #171B2D ;
	float: left ;
	border-bottom: none ;
	min-width: 40px ;
	text-align: center ;
}
ul.tab-menu li.active {
	margin-bottom: -1px ;
	border-bottom: 1px solid #272C2D ;
	font-weight: bold ;
}
ul.tab-menu li.active a:hover {
	border-bottom: none ;
}
ul#tab_menu {
	padding: 0 ;
	margin: 0 ;
	margin-left: 6px ;
}
ul#tab_menu li {
	padding: 0 0.5em ;
	margin: 0 ;
	margin-right: 4px ;
	font-size: 85% ;
}
ul#tab_menu li.active {
	font-size: 90% ;
}
ul#tab_submenu {
	margin: 10px 0 0 ;
}
ul#tab_submenu li {
	background-color: #f9f9f9 ;
	padding: 5px 7px ;
	margin: 0 0 0 8px ;
/*	-moz-border-radius-topleft: 5px ;*/
	-webkit-border-top-left-radius: 5px ;
/*	-moz-border-radius-topright: 5px ;*/
	-webkit-border-top-right-radius: 5px ;
}
ul#tab_submenu li.active {
	background-color: #fff ;
}
.submenu-tab-content {
	background: white ;
	padding: 14px ;
	padding-bottom: 25px ;
	border: 1px solid #D8D8E7 ;
}

/* user menu */

#user-menu {
	float: none ;
	margin-bottom: 10px ;
	background-color: #272C2D ;
	padding-bottom: 1em ;
	padding-top: 1em ;
	padding-left: 0 ;
	padding-right: 0 ;
	width:100% ;
	margin-right: 2em ;
	text-align: center ;
	border-radius: 10px ;
	margin-bottom: 20px ;
}

#user-menu li {
	display: inline ;
	list-style-type: none ;
	list-style-image: none ;
	padding: 0 0.8em ;
	font-size: 90% ;
}

#user-menu a {
 color: #E9B96E ;
 font-size: 1.2em ;
 text-decoration: none ;
}



/* footer */

#footer-logo {
	font-size: 0.9em ;
	padding-top: 10px ;
	text-align: right ;
}
#footer-custom {
	font-size: 0.9em ;
	text-align: center ;
}

/* recent changes, user contributions, history */

li.minorchange {
	color: #777777 ;
}

li.deletechange {
	color: #770000 ;
}
li.importchange {
	color: #007777 ;
}
li.movechange {
	color: #777777 ;
}
li.undeletechange {
	color: #007777 ;
}
span.edit-comment {
	font-style: italic ;
}
span.section-link {
	color: #999 ;
}
span.section-link a {
	color: #333 ;
}

/* topic pages */

#content-article {
	padding: 10px 0 10px 0 ;
}
#content-article pre {
	overflow: hidden ;
}
.editsection {
	font-size: 67% ;
	float: right ;
	margin-left: 5px ;
	position: relative ;
}
#shared-image-message {
	border: 1px solid red ;
	font-size: 125% ;
	font-weight: bold ;
	margin: 10px 50px ;
	padding: 10px ;
	text-align:center ;
}

/* images */

img.wikiimg {
	border: 0 ;
}
a.wikiimg {
	border: 0 ;
}
img.thumbborder {
	border: 1px solid #ddd ;
}
div.thumbcaption {
	text-align:left ;
	border: none ;
	font-size:94% ;
	line-height:1.4em ;
	padding:3px ;
}
div.floatleft {
	border: none ;
	clear:left ;
	float:left ;
	margin: 0 0.5em 0.5em 0 ;
	position:relative ;
}
div.floatright {
	border: none ;
	clear:right ;
	float:right ;
	margin: 0 0 0.5em 0.5em ;
	position:relative ;
}
div.center {
	text-align:center ;
	width:100% ;
}
div.center * {
	margin-left:auto ;
	margin-right:auto ;
}
div.tright {
	border: none ;
	margin: 0.5em 0 0.8em 1.4em ;
	clear:right ;
	float:right ;
}
div.thumb {
	border: none ;
	margin-bottom:0.5em ;
	width:auto ;
}
div.thumbinner  {
	background-color:#f9f9f9 ;
	border: 1px solid #ccc ;
	font-size:94% ;
	overflow:hidden ;
	padding: 3px ;
	text-align:center ;
	min-width:10px ;
}
table.gallery {
	border: 1px solid #ccc ;
	margin: 2px ;
	padding: 2px ;
	background-color: white ;
}
table.gallery caption {
	font-weight: bold ;
	padding-bottom: 5px ;
}
table.gallery td {
	background-color: #f9f9f9 ;
	border: solid 2px white ;
	text-align: center ;
	vertical-align: top ;
}
table.gallery-cat td {
	vertical-align: middle ;
	padding: 2px ;
}
img.gallery {
	border: 1px solid #bbbbbb ;
	padding: 0.3em ;
}
div.gallerytext {
	font-size: 94% ;
	overflow: hidden ;
	padding: 2px 4px
}
div.gallerytext p {
	line-height: normal ;
	margin: auto ;
}
div.gallerybox {
	margin: 2px ;
}
table.gallery-cat div.gallerybox {
	width: 155px ;
}
#edit-toolbar {
	margin: 3px 0 ;
}
.toolbar-group {
	/*-moz-border-radius:5px ;*/
	-webkit-border-radius: 5px ;
	background-color: #e9e9e9 ;
	border-radius: 5px ;
	display: inline-block ;
	margin: 3px 5px 3px 0 ;
	padding-left: 3px ;
}
.toolbar-button a {
	border-radius: 3px ;
	cursor: default ;
	display: inline-block ;
	outline: none ;
	padding: 2px 3px ;
}
.toolbar-button {
	border-right: 1px solid #fff ;
	cursor: pointer ;
	display: inline-block ;
	padding: 2px 2px 2px 0 ;
	/*-moz-border-radius: 3px ;*/
	-webkit-border-radius: 3px ;
}
.toolbar-button a:hover, .toolbar-button a:focus, .toolbar-button a:active {
	background-color: #bfd1ff ;
	border-bottom: none ;
}
.toolbar-icon {
	background: url(https://www.linux.org.ru/wiki/images/toolbar-icons.png) 0 0 no-repeat ;
	cursor: default ;
	display: inline-block ;
	height: 22px ;
	width: 22px ;
}
#button-bold .toolbar-icon {
	background-position: 0 -66px ;
}
#button-italic .toolbar-icon {
	background-position: 0 -88px ;
}
#button-underline .toolbar-icon {
	background-position: 0 -110px ;
}
#button-strike .toolbar-icon {
	background-position: 0 -132px ;
}
#button-subscript .toolbar-icon {
	background-position: 0 -154px ;
}
#button-superscript .toolbar-icon {
	background-position: 0 -176px ;
}
#button-nowiki .toolbar-icon {
	background-position: 0 0 ;
}
#button-orderedlist .toolbar-icon {
	background-position: 0 -198px ;
}
#button-unorderedlist .toolbar-icon {
	background-position: 0 -220px ;
}
#button-indent .toolbar-icon {
	background-position: 0 -242px ;
}
#button-wikilink .toolbar-icon {
	background-position: 0 -264px ;
}
#button-link .toolbar-icon {
	background-position: 0 -22px ;
}
#button-image .toolbar-icon {
	background-position: 0 -286px ;
}
#button-signature .toolbar-icon {
	background-position: 0 -44px ;
}
#button-hr .toolbar-icon {
	background-position: 0 -308px ;
}
#button-heading1 .toolbar-icon {
	background-position: 0 -330px ;
}
#button-heading2 .toolbar-icon {
	background-position: 0 -352px ;
}
#button-heading3 .toolbar-icon {
	background-position: 0 -374px ;
}
table.categories {
	width: 100% ;
}
table.categories td {
	vertical-align: top ;
	width: 33% ;
}

/* search results */

.highlight {
	background:yellow ;
}
.searchresult {
	background-color: #dcdcdc ;
	margin: 1em 0 0 0 ;
}
.searchsummary {
	margin: 0.5em 0 0.7em 0 ;
}
#searchpower {
	font-size: 90% ;
	font-style: italic ;
	margin: 1em 0 1em 0 ;
}
#searchhints {
	margin: 1.5em 0 1.5em 0 ;
}

/* form styles */

.formhelp {
	font-size: 90% ;
	color: #5f5f5f ;
	clear: both ;
}
.darkbg {
	background-color: #d8d8e7 ;
}
.translationElement {
	float: left ;
	vertical-align: top ;
	padding: 3px ;
	width: 49% ;
	overflow: hidden ;
}
textarea.translation {
	width: 100% ;
	overflow:auto ;
	height:5em ;
}
textarea.medium {
	width: 400px ;
	height: 60px ;
}
table.wiki-admin {
	border: 1px solid #fff ;
	border-collapse: collapse ;
	border-spacing: 0 ;
	margin: 0 auto ;
	width: 99% ;
}
table.wiki-admin th {
	background-color: #d8d8e7 ;
	border: 1px solid #fff ;
	text-align: center ;
	padding: 5px ;
}
table.wiki-admin th.first {
	width: 250px ;
}
table.wiki-admin td {
	border-bottom: 1px solid #fff ;
	padding: 5px ;
}
.noborder {
	border: none ;
}
.center {
	text-align: center ;
}
div.row {
	clear: right ;
	padding: 5px 7px ;
}
.admin div.row {
	background-color: #e9e9e9 ;
	border-top: 1px solid #fff ;
}
div.row span {
	display: inline-block ;
	vertical-align: top ;
	text-align: left ;
	padding-left: 5px ;
}
div.row label {
	display: inline-block ;
	text-align: left ;
	width: 240px ;
	font-weight: bold ;
}
div.row span.form-button {
	margin-left: 240px ;
}
div.row .formhelp {
	padding-top: 3px ;
}
div.rowhelp {
	border: 1px solid #e9e9e9 ;
	margin: 10px ;
	padding: 5px 10px ;
}
.translationElement label {
	vertical-align: top ;
	padding: 3px ;
	width: auto ;
	overflow: hidden ;
}
div.row label.nonblock {
	display: inherit ;
	text-align: inherit ;
	width: auto ;
	font-weight: inherit ;
}
div.callout {
	border: 1px solid #e9e9e9 ;
	margin: 10px ;
	padding: 10px ;
}
div.callout input {
	margin-right: 20px ;
}
.expander {
	padding: 5px ;
}
.expander-open {
	height: auto ;
}
.expander > label {
	cursor: pointer ;
	font-weight: bold ;
}
.expander > label:before {
	content:url("https://www.linux.org.ru/wiki/images/arrow-right.png") ;
}
.expander-open > label:before {
	content:url("https://www.linux.org.ru/wiki/images/arrow-down.png") ;
}
.expander > ul {
	display: none ;
	margin-top: 10px ;
}
.expander-open > ul {
	display: block ;
}
.captcha {
	padding: 5px 0 ;
}
.captcha-label {
	padding: 5px 0 ;
}
#manage div.row label, #move div.row label, #register div.row label {
	width: 180px ;
}
#manage div.row span.form-button, #move div.row span.form-button, #register div.row span.form-button {
	margin-left: 180px ;
}
#translation div.row label {
	font-weight: normal ;
}
#login div.row label {
	width: 100px ;
}
#login div.row span.form-button {
	margin-left: 100px ;
}
}
