css:
@-moz-document domain("www.textnow.com") {
html {
  background:#050505 url(http://i.imgur.com/iQiT2Yo.png)!important;
  color: #e5e5e5;
}

a, .footerLinks a {
  color: #A7C7FA;
  text-shadow: 0.1em 0.1em 0.2em black!important;
  text-decoration: none!important;
  -webkit-transition:color .5s ease-in!important;  
  -moz-transition:color .5s ease-in!important;  
  -o-transition:color .5s ease-in!important;  
  transition:color .5s ease-in!important; 
}

a:hover {
  color: #e5e5e5;
}

.chat .messages, .group .messages {
  background:#262626 url(http://i.imgur.com/iQiT2Yo.png)!important;
  color: #e5e5e5;
}

#sidebar {
  border-left: none;
  border-bottom: none;
  border-right: 1px solid #181818;
}

.footer-wrapper {
  background:#181818 url(http://i.imgur.com/iQiT2Yo.png)!important;
  color: #e5e5e5;
}

.recent-list {
  background:#262626 url(http://i.imgur.com/iQiT2Yo.png)!important;
}

.chat .input, .group .input {
  background:#181818 url(http://i.imgur.com/iQiT2Yo.png)!important;
}

.chat .profile-toolbar, .group .profile-toolbar {
  background:#181818 url(http://i.imgur.com/iQiT2Yo.png)!important;
  border-bottom: 1px solid #303030;
  color: #e5e5e5;
}

.main-view .content {
  color: #e5e5e5;
  background:#181818 url(http://i.imgur.com/iQiT2Yo.png)!important;
  border-right: none;
  border-bottom: none;
}
    
.container {
  background: none;
}
  
.chat .outgoing, .group .outgoing {
  border: 1px solid #303030;
  color: #e5e5e5;
  text-shadow: 0.1em 0.1em 0.3em black;
  background:#181818 url(http://i.imgur.com/iQiT2Yo.png)!important;
}

.chat .incoming, .group .incoming {
  border: 1px solid #303030;
  color: #e5e5e5;
  text-shadow: 0.1em 0.1em 0.3em black;
  background:#1F1F1F url(http://i.imgur.com/iQiT2Yo.png)!important;
}
  
.chat li .date, .group li .date {
  color: #A7C7FA;
}

.recent-list li.header {
  color: #e5e5e5;
  border-bottom: 1px solid #303030;
}
  
.recent-list li.current {
  color: #e5e5e5;
  background:#181818 url(http://i.imgur.com/iQiT2Yo.png)!important;
  border-top: 1px solid #303030;
  border-bottom: 1px solid #303030;
}
  
.recent-list li.current .name {
  color: #e5e5e5;
}

.chat li .name, .group li .name {
  text-transform: capitalize;
}

.chat .input #text-inner, .group .input #text-inner {
  color: #e5e5e5;
  background:#212121 url(http://i.imgur.com/iQiT2Yo.png)!important;
  border: 1px solid #303030;
}

.chat .input button, .group .input button {
  color: #e5e5e5;
  background:#262626 url(http://i.imgur.com/iQiT2Yo.png)!important;
}

.main-view>header .buttons button {
  color: #e5e5e5;
  background:#181818 url(http://i.imgur.com/iQiT2Yo.png)!important;
  -webkit-box-shadow: none;
  -moz-box-shadow: none;
  box-shadow: none;
  border-bottom: 1px solid #303030;
}
  
.main-view>header .profile {
  color: #e5e5e5;
  background:#181818 url(http://i.imgur.com/iQiT2Yo.png)!important;
  border-left: none;
  -webkit-box-shadow: none;
  -moz-box-shadow: none;
  box-shadow: none;
}
  
.main-view>header {
  color: #e5e5e5;
  background:#181818 url(http://i.imgur.com/iQiT2Yo.png)!important;
  border-bottom: 1px solid #303030;
  border-top: none;
  border-right: none;
  border-left: none;
}

.main-view>header .buttons button {
  border-right: none;
}

.chat .input textarea, .group .input textarea {
  color: #e5e5e5;
}

#tnDialogContainer .dialog h1, .nifty-modal h1 {
  color: #e5e5e5;
  background:#181818 url(http://i.imgur.com/iQiT2Yo.png)!important;
}

#tnDialogContainer .dialog form .content, .nifty-modal form .content {
  color: #e5e5e5;
  background:#262626 url(http://i.imgur.com/iQiT2Yo.png)!important;
}

#tnDialogContainer .dialog .buttons, .nifty-modal .buttons {
  color: #e5e5e5;
  background:#181818 url(http://i.imgur.com/iQiT2Yo.png)!important;
  border-top: 1px dashed #ccd5df;
}

.tndialog, #tnDialogContainer .dialog {
  color: #e5e5e5;
  background:#181818 url(http://i.imgur.com/iQiT2Yo.png)!important;
  -moz-box-shadow: none;
  -webkit-box-shadow: none;
  box-shadow: none;
  padding-bottom: 20px;
}
  
.form_button_primary, .nifty-content button.ok, .nifty-content button.yes {
  border: 1px solid #303030;
  color: #e5e5e5;
  background:#181818 url(http://i.imgur.com/iQiT2Yo.png)!important;
  background-image: none;
  height: 42px;
}
    
.form_button, .nifty-content button {
  border: 1px solid #303030;
  color: #e5e5e5;
  background:#181818 url(http://i.imgur.com/iQiT2Yo.png)!important;
  background-image: none;
  height: 42px;
}
      
.recent-list li .name {
  color: #e5e5e5;
}
        
.recent-list li .message {
  color: #ccc;
}
          
.recent-list li .date {
  color: #A7C7FA;
}

.nifty-content h3 {
  color: #e5e5e5;
  background:#181818 url(http://i.imgur.com/iQiT2Yo.png)!important;
}

.nifty-content .nifty-inner {
  color: #e5e5e5;
  background:#262626 url(http://i.imgur.com/iQiT2Yo.png)!important;
}
}
