@-moz-document url-prefix("http://www.tumblr.com/"), url("http://www.tumblr.com/dashboard"), url("http://www.tumblr.com/likes"), url("http://www.tumblr.com/following"), url("http://www.tumblr.com/inbox"), url("http://www.tumblr.com/spotlight/culture"), url("https://www.tumblr.com/register"), url("https://www.tumblr.com/login"), url("http://www.tumblr.com/explore"), url("https://www.tumblr.com/lookup"), url("https://www.tumblr.com/preferences"), url("http://www.tumblr.com/help"), url-prefix("http://www.tumblr.com/blog/"), url-prefix("http://www.tumblr.com/tagged/"), url-prefix("http://www.tumblr.com/following") , url-prefix("https://www.tumblr.com/"), url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/likes"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/spotlight/culture"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/help"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/following") {
body {
   background:url("http://a1star.com/images/00-star-space-hubble-tile5.jpg")no-repeat fixed  !important;
   background-color: #A93A4A !important;
   background-position: 0% 60% !important;
   background-size: 100% 100%  !important;
 }

div.l-header .tab.iconic a{
  background-color: #A93A4A !important;
  border-radius: 60px !important;  
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {  
   font-family: Arial; color: #660e23!important; 
   background: rgba(252, 234, 238, .4) !important;
}


.notification  {
  	background: #dbcccf !important; border: none !important; 
	color: #607fb5 !important; font-size: 11px !important;
  }

.notification :link, .notification :active, .notification :visited {
    color: #81a5e3 !important;
    text-decoration: none !important;
    border: none !important;
  }

.new_post_label .new_post_label_icon {
background-image: url('http://img222.imageshack.us/img222/8859/fifib.png') !important;
background-size: 520px 84px !important;
margin-top: 0px !important;
margin-right: 15px !important;
opacity: 1 !important;
}

div.l-content #new_post .new_post_label {
color: #000000 !important;
line-height: 1.4em;
font-weight: bold !important;
font-size: .9em !important;

}

#right_column .controls_section li a,

#right_column .controls_section li a .count {

  color: #ffffff !important; 
}

#logo {
height: 0 !important;
width: 0 !important;
padding-left: 215px !important;
padding-top: 65px !important;
background: url('http://i44.tinypic.com/28w311d.png') no-repeat !important;
}

.controls_section li .posts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.posts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .posts:before {
  content: "Stars" !important;
}.controls_section li .followers:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.followers  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .followers:before {
  content: "Phantasms" !important;
}.controls_section li .drafts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.drafts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .drafts:before {
  content: "Maybes" !important;
}.controls_section li .queue:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.queue  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .queue:before {
  content: "Ccapac Apu" !important;
}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  margin-top: 0px !important;

  border-bottom: solid 11px rgba(0, 0, 0, 0.4) !important;

  border-right: 11px solid transparent !important;

  border-left: 11px solid transparent !important;

}

.return_to_top_icon{
background: url('http://media.tumblr.com/74e48d9da9dd1227097445fc43782313/tumblr_inline_mg55ks5noN1rqr0uv.png') no-repeat transparent !important;
width: 100px !important;
height: 150px !important}
}
