css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wykop.pl") {

.cookie-alert, #alert, #social_widget, #fixedBox,
#dying-links-box, aside.fleft
  { display: none !important; }

#body-con, .margintop15, .margintop20, .margintop30
  { margin-top: 0 !important; }

/*
 * Mniejszy górny pasek z miniaturkami.
 */
#slidesContainer, #slideshow
  { height: 190px !important; }
#slidesContainer img
  { width: 150px !important; height: 130px !important; }
#slidesContainer div.slidebox
  { width: 170px !important; }
.large
  { font-size: 10px !important; line-height: 12px !important; }
span.overlabel.full
  { background: rgba(0, 0, 0, 0.4) !important; }
#scrolling /* górny gradient */
  { display: none !important; }

/*
 * Strona zeskalowana do wielkości okna.
 */
.wrapper
  { min-width: 0 !important; max-width: none !important;
    padding: 0 !important; }
.scale
  { margin-right: 0 !important; }
section.fleft
  { margin-top: 50px !important; }


/*
 * Prostsze ikony liczników.
 */
.icon.diggcount, .icon.diggcount2 {
    line-height: 20px !important;
    width: 24px !important;
    height: 20px !important;
    background: none !important;
}
.diggit { width: 24px !important; }
.diggit .action { display: none !important; }
.cff5917 { color: #EB9 !important; }

}
