css:
@-moz-document domain("quake.ingame.de") {
/*
	Allgemein
*/
.pagination span a,
.actionbuttons .group .button,
.navpopupmenu.popupmenu.nohovermenu a.textcontrol,
.navpopupmenu a.textcontrol,
.navpopupmenu a.popupctrl,
.navpopupmenu.popupmenu.nohovermenu a.textcontrol,
.navpopupmenu.popupmenu.nohovermenu a.popupctrl,
.toolsmenu {
  border: none !important;
}
.above_threadlist a.newcontent_textcontrol,
.below_threadlist a.newcontent_textcontrol,
.navpopupmenu a {
  background-color: #172126 !important;
  background-image: none !important;
}
.above_threadlist a.newcontent_textcontrol:hover,
.below_threadlist a.newcontent_textcontrol:hover,
.navpopupmenu a:hover {
  background-color: #466371 !important;
}
.announcement dt {
	font-weight: normal !important;
}
.announcements .announcerow {
  box-shadow: none !important;
}

/*
	√úbersicht
*/
.threadtitle .title {
  font-weight: normal !important;
  color: #C9992F !important;
}
.new .threadtitle .title {
    color: #FFB818 !important;
}
.threadtitle .gotonewpost {
  vertical-align: bottom !important;
}
.threadtitle > img {
	display: none !important;
}
.threadmeta .label a {
      font-weight: normal !important;
}
.threadmeta {
    margin-left: 0 !important;
}
.threadstats li a {
	font-weight: normal !important;
}
.threadinfo .threadstatus {
  display: none !important;
}
.threadinfo {
	padding-left: 4px !important;
}
.threadbit .alt {
	height: auto !important;
}
.threadlastpost {
  padding: 4px !important;
}
.threadlastpost .username {
  font-weight: normal !important;
}
.above_threadlist .newcontent_textcontrol,
.below_threadlist .newcontent_textcontrol {
  box-shadow: none !important;
  border: none !important;
  border-radius: 0px !important;
}
.threadlisthead,
.forumhead {
  border: none !important;
  border-radius: 0px !important;
}
.threadbit .nonsticky ,
.sticky,
.forumbit_post .forumrow {
  border-left-width: 0px !important;
}

/*
	Thread
*/
.postbody,
.postbitlegacy,
.posthead,
.blockhead,
.blockbody,
.bbcode_quote {
  border: none !important;
}
.bbcode_quote {
    border-radius: 0px !important;
}
.postbody .postrow .title {
  display: none !important;
}
.posthead .nodecontrols a,
.postbitlegacy .postfoot a {
	font-weight: normal !important;
}
.above_postlist .newcontent_textcontrol,
.below_postlist .newcontent_textcontrol {
  box-shadow: none !important;
  border: none !important;
  border-radius: 0px !important;
}
.posthead {
  margin-left: 0px !important;
}
.postbitlegacy .postfoot .textcontrols span.seperator {
  display: none !important;
}
.hasad {
  margin-right: 0px !important;
}

/*
	ckeditor
*/
.cke_skin_kama {
  border: none !important;
}
.cke_wrapper {
  border-radius: 0px !important;
  background-color: #172126 !important;
}
#cke_contents_vB_Editor_QR_editor textarea {
    resize: both !important;
}
.cke_bottom a {
  color: #fff !important;
  opacity: 1 !important;
}
.cke_bottom .cke_button {
  background: url(http://quake.ingame.de/forum/images/inquake/Leisten/nav_19px2.png) repeat-x !important;
  padding: 2.5px 5px !important;
}

/*
	Suche
*/
#globalsearch input[name="query"] {
  border-radius: 0 !important;
}
}
