css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sceper.ws") {
    body {
        background: #000000 !important;
        color: #999999 !important;
    }
    
    #from-categories {
        background: #222222 !important;
        width: 1089px;
    }
    
    .recent-cat.first {
        background: #111111 !important;
    }
    
    .meta-info {
        
    }
    
    .recent-cat {
    background: #111111 !important;
    }
    
    .recent-cat.last .entry {
        border-right: 1px solid #111111 !important;
        border-bottom: 1px solid #111111 !important;
    }
    
    #sidebar {
        float: left;
        margin-top: -48px !important;
        width: 302px;
    }

    body#home #sidebar {
        float: left;
        margin-top: 1px !important;
        width: 289px;
    }

    div#top-shadow {
        background: url("http://static.sceper.ws/images/SceneRLSv4/index-shadow.png") repeat-x scroll 0 0 transparent;
        height: 26px !important;
        left: 0;
        position: absolute;
        top: 0;
        width: 100%;
    }

    .top.last {
        border-right: 1px solid #111 !important;
        width: 270px;
    }
    
    .recent-cat .entry {
        border-bottom: 1px solid #111111 !important;
        border-right: 1px solid #111111 !important;
        padding-left: 19px !important;
        padding-right: 19px;
        padding-top: 14px;
        position: relative;
        width: 232px;
    }
    
    .entry .orange div.title {
        margin-left: 5px !important;
    }
    
    .recent-cat .entry.orange .title {
    background: url("http://static.sceper.ws/images/SceneRLSv4/title_orange.png") no-repeat scroll left bottom transparent;
    left: 0px !important;
    padding-bottom: 10px;
    position: absolute;
    top: -120px;
    }
    
    .recent-cat .entry.orange h3 {
        background: none repeat scroll 0 0 #829A78;
        text-shadow: none !important;
        width: 207px;
    }
    .recent-cat .entry.green .title {
        background: url("http://static.sceper.ws/images/SceneRLSv4/title_green.png") no-repeat scroll left bottom transparent;
        left: 0px !important;
        padding-bottom: 10px;
        position: absolute;
        top: -120px;
    }
    .recent-cat .entry.green h3 {
        background: none repeat scroll 0 0 #121F10;
        text-shadow: none !important;
        width: 207px;
    }
    .recent-cat .entry.light-blue .title {
        background: url("http://static.sceper.ws/images/SceneRLSv4/title_light-blue.png") no-repeat scroll left bottom transparent;
        left: 0px !important;
        padding-bottom: 10px;
        position: absolute;
        top: -120px;
    }
    .recent-cat .entry.light-blue h3 {
        background: none repeat scroll 0 0 #A5BB97;
        text-shadow: none !important;
        width: 207px;
    }
    .recent-cat .entry.blue .title {
        background: url("http://static.sceper.ws/images/SceneRLSv4/title_blue.png") no-repeat scroll left bottom transparent;
        left: 0px  !important;
        padding-bottom: 10px;
        position: absolute;
        top: -120px;
    }
    .recent-cat .entry.blue h3 {
        background: none repeat scroll 0 0 #222D20;
        text-shadow: none !important;
        width: 207px;
    }  
    #from-categories .clearfix {
        background: #222222 !important;
        width: 1089px;
    }
    
    #breadcrumbs {
        background: none repeat scroll 0 0 transparent !important;
        text-shadow: 1px 1px 1px #111111 !important;
        height: 20px !important;
        top: 0px !important;
        padding-top: 16px !important;
    }
    #content {
        background: #111111 !important;
    }
    
    #content-single {
        background: #111111 !important;
    }
    
    #main-content-wrap {
        background: #222222 !important;
    }
    
    #main-content {
        background: #161616 !important;
    }
    
    #main-content .clearfix {
        background: #181818 !important;
    }
    
    #recent-posts {
        padding-top: 10px !important;
        background: #181818 !important;
        width: 780px !important;
    }
    
    #recent-posts .clearfix{
        background: #181818 !important;
        padding-left: 20px !important;
        padding-bottom: 10px !important;
    }
    
    .title {
        background: #181818 !important;
    }
    
    *.title {
        text-shadow: none !important;
    }
    
    .title a {
        background: #181818 !important;
        padding-left: 0px !important;
    }
    
    h1 a, h2 a, h3 a, h4 a, h5 a, h6 a {
        text-shadow: none !important;
    }
    
    h2.title a {
        color: #C26700 !important;
        text-shadow: none !important;
    }
    
    h2.title a:hover {
        color: #dd7700 !important;
    }
    
    h4.title a {
        color: #666666 !important;
        text-shadow: none !important;
    }
    #sidebar {
        background: #333 !important;
        color: #ccc !important;
    }
    
    #sidebar div.widget {
        background: #222222 !important;
        float: left;
        margin-left: 1px;
        padding-bottom: 15px;
        width: 259px;
    }

    #sidebar h4.widgettitle {
        background: #222222 !important;
        color: #6F6F6F;
        font-family: Georgia,serif;
        font-size: 14px;
        font-style: normal !important;
        padding-bottom: 0;
        padding-left: 2px;
        text-align: center;
        text-shadow: 1px 1px 1px #111111;
    }
    .widgettitle span {
        background: #272727 !important;
        text-shadow: 1px 1px 1px #111111 !important;
        border-top: 1px solid #333333 !important;
        border-bottom: 1px solid #171717 !important;
    }
    
    #sidebar .widget ul li {
        background-image: none !important;
        color: #262626;
        padding: 3px 0 6px 18px;
    }
    
    #recent-posts .entry .post-meta p {
        color: #AFAFAF;
        float: left;
        font-family: Georgia,serif;
        font-size: 12px;
        font-style: italic;
        padding-bottom: 9px !important;
        padding-left: 14px !important;
        padding-top: 4px !important;
        text-shadow: 1px 1px 1px #111111 !important;
        width: 768px !important;
    }
    
    #recent-posts .entry .title a {
        margin-top: 10px !important;
    }
    
    #recent-posts .entry .post-meta {
        background: url("http://static.sceper.ws/images/SceneRLSv4/post_bg.png") repeat-x scroll left bottom #EFEFEF;
        margin-left: -10px !important;
        padding-left: 0px !important;
        margin-top: -10px !important;
        position: relative;
        width: 768px !important;
        overflow: hidden !important;
    }
    
    #recent-posts .entry .post-meta a.readmore {
        background: -moz-linear-gradient(center top , #222222 0px, #111111 100%) repeat scroll 0 0 padding-box transparent !important;
        display: block;
        float: right;
        height: 29px;
        margin-top: 0px !important;
        margin-right: 335px !important;
        width:90px !important;
    }
    
    strong {
        color: #666666 !important;
    }
    
    .meta, .metab {
        color: #dd7700 !important;
        border-bottom: 1px solid #553300 !important;
        clear: both;
        display: block;
        float: left;
        font-size: 10px;
        margin-bottom: 7px;
        padding: 2px 0 1px;
        width: 758px !important;
    }
    a:link,a:visited {
        color: #C26700 !important;
    }
    
    a:hover {
        color: #ff9900 !important;
    }
    
    .entry-content {
        margin-left: -20px !important;
    }
    
    .entry-content clearfix .meta p {
        color: #aa6600 !important;
    }
    #content-bottom {
        background: -moz-linear-gradient(center bottom , #222222 2px, #111111 100%) repeat scroll 0 0 padding-box transparent !important;
        height: 15px !important;
    }
    
    #content-bottom .clearfix {
        background: #000000 !important;
        height: 0px;
    }
    
    p#copyright {
        background: none !important;
        color: #707070;
        float: left;
        font-size: 11px;
        padding-top: 16px !important;
    }
    .clearfix p span span {
        color: #999999 !important;
    }
    
    #comment-wrap {
        margin-left: 14px;
        padding-top: 10px !important;
    }
    
    ol.commentlist {
        list-style: none outside none;
        margin: 0 0 0 -2px !important;
        padding-top: 10px;
    }
    input[type="text"], input.text, input.title, textarea, select {
        outline: none !important;
        color: #bb6600 !important;
        padding: 4px !important;
        background-color: #333333 !important;
        box-shadow: none !important;
        -webkit-box-shadow: none !important;
        border-color: #111111 !important;
    }
    
    .post-meta {
        width: 730px !important;
        
    }
    
    input[type="text"]:focus, input.text:focus, input.title:focus, textarea:focus, select:focus {
        border-color: #bb6600 !important;
        color: #bbb !important;
        background-color: #333333 !important;
    }
    
    .wp-pagenavi {
        padding: 36px 30px 27px 50px !important;
    }
    
    .comment-wrap {
        float: left;
        margin-left: 0px !important;
        padding-bottom: 22px;
        padding-right: 52px !important;
        position: relative;
    }
    
    .comment-body {
        border-bottom: 1px solid #D8D8D8;
        padding: 0 0 0 17px;
    }
    .commentlist li.comment {
        margin: 15px 0;
        padding: 7px 0px 0 0px !important;
        overflow-x: hidden !important;
    }
    ol.commentlist {
        list-style: none outside none;
        margin: 0 0 0 -37px;
        padding-top: 10px;
        padding-right: 55px !important;
    }
    span.fn, span.fn a {
        color: #FF940A !important;
        font-weight: bold;
    }
    #recent-posts .entry h1.title, h3#comments {
        color: #dd7700 !important;
    }
    .wp-pagenavi a, .wp-pagenavi a:link, .wp-pagenavi a:visited {
        background: -moz-linear-gradient(center top , #222222 0px, #111111 100%) repeat scroll 0 0 padding-box transparent !important;
        border: 1px dotted #7D3301 !important;
        color: #C26700 !important;
        font-weight: normal !important;
        margin: 1px 5px !important;
        padding: 11px 15px 11px 14px !important;
        text-align: center;
        text-shadow: 1px 1px 1px #111111 !important;
    }
    .wp-pagenavi span.current, .wp-pagenavi span.extend, .wp-pagenavi a:active {
        background: -moz-linear-gradient(center top , #111111 0px, #171717 100%) repeat scroll 0 0 padding-box transparent !important;
        border: 1px dotted #7D3301 !important;
        color: #FFFFFF !important;
        font-weight: normal !important;
        margin: 1px 5px !important;
        padding: 11px 15px 11px 14px !important;
        text-align: center;
        text-shadow: 1px 1px 1px #1591BA;
    }
    .wp-pagenavi a:hover {
        background: -moz-linear-gradient(center top , #111111 0px, #171717 100%) repeat scroll 0 0 padding-box transparent !important;
        border: 1px dotted #A64302 !important;
        color: #ff9900 !important;
        font-weight: normal !important;
        margin: 1px 5px !important;
        padding: 11px 15px 11px 14px !important;
        text-align: center;
        text-shadow: 2px 2px 2px #C26700 !important;
    }
    #recent-posts .entry .post-meta a.readmore span {
        color: #cccccc !important;
        line-height: 29px;
        padding-left: 14px;
        padding-right: 14px;
        text-shadow: 1px 1px 1px #111111;
    }
    div.container {
        background: none !important;
    }
    
    .widgetcontent .xoxo .blogroll li {
        color: #cccccc !important;
    }
    #sidebar .widget ul li {
        margin-bottom: 5px;
        color: #888888 !important;
    }
}
