css:
@-moz-document url-prefix("https://www.drupal.org") {
#drupalorg-site-status {
    display: none;
}
#banner {
    display: none;
}
#page {
    min-width: 1200px !important;
}
.container-12 {
    min-width: 1200px !important;
}
#banner {
    display: none;
}
#header {
    min-width: 1200px !important;
    background: #56B3E6 !important;
}
#nav-masthead {
    margin-top: 20px !important;
}
#nav-masthead ul li a {
    background: #0678be;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}
#block-drupalorg-membership-campaign {
    display: none;
}
#nav-header {
    display: none;
}
#header-left {
    display: none;
}

#banner {
    display: none;
}

#edit-search-block-form--2 {
    display: none;
}

div#search-theme-form-advanced fieldset#edit-advanced.collapsible legend span.fieldset-legend a.fieldset-title {
    display: none;
}

#header-screen {
    display: none;
}

#footer {
    display: none;
}

html.js fieldset:not(.filter-wrapper):not(.vertical-tabs-pane):not(#edit-advanced).collapsible legend {
  width: 300px;
}

#header-content {
    position: absolute;
    margin-left: 982px;
}
#header-right {
	padding-top: 0;
}
#header-right-inner {
    float: right !important
}
#header input[type="text"] {
    background-color: #9dd1ee;
    padding: 0 !important;
    border: 2px solid #3ca8e4 !important;
    border-radius: 1px;
    color: #fff;
    width: 200px;
    height: 30px;
    font-size: 16px;
}

#edit-search-block-form--2 > input {
    padding-left: 5px;
}
#header input[type="text"]::-webkit-input-placeholder {
    color: #91c9e8;
}
#header #search-block-form .form-submit {
	display: none;
}
.block-search {
	padding-top: 10px;
}
#header input[type="submit"] {
    font-size: 0.85em;
    margin-top: 0.13em;
    padding: 0.22em 1em;
    background: #0D7DC1;
    border-radius: 1px;
}
input[name*="search"] {
    background: none;
}
.comment {
    margin-right: -400px;
}
.comment .content {
    margin: 0 0 0 50px !important;
}
.node-type-project-issue #content h3.comment-title {
    padding-top: 10px !important;
}
.node-project-issue {
    min-height: 700px !important;
    /* HACK */
}
#block-project-issue-issue-metadata .field-label-inline {
    line-height: 2.8em;
}
#block-project-issue-issue-metadata .last-update {
    padding: 0 0 2em;
    font-size: 85%;
    border-bottom: 20px solid #fff;
}
.update-link {
    padding: 1em;
}
.field-issue-status-14 {
    font-size: 95%;
}
td.extended-file-field-table-filename span.file img.file-icon {
    display: none;
}
td.extended-file-field-table-filename .file .file-icon,
tr.pift-file-info .file .file-icon {
    display: none;
}
div#page-title {
    display: none;
}
#page-title-tools {
    display: none;
}
/* Dreditor fixes */

a.dreditor-button {
    background: #0D7DC1 !important;
    border-bottom-left-radius: 2px !important;
    border-bottom-right-radius: 2px !important;
    border-top-left-radius: 2px !important;
    border-top-right-radius: 2px !important;
    color: #FFFFFF !important;
    font-family: 'Lucida Grande', 'DejaVu Sans', 'Bitstream Vera Sans', Verdana, Arial, sans-serif !important;
    font-size: 12px !important;
    padding: 0.25em 0.8em !important;
    text-decoration: none !important;
    text-transform: none !important;
    box-shadow: none !important;
    font-weight: normal !important;
    border: none !important;
}
button.dreditor-button {
    background: none !important;
    color: #668A19 !important;
    display: block !important;
    margin: 0 !important;
    padding: 0.231em 0.615em !important;
    text-decoration: none !important;
    text-transform: none !important;
    box-shadow: none !important;
    font-weight: normal !important;
    border: none !important;
    font-family: inherit !important;
    font-size: inherit !important;
}
.node-type-project-issue .block-project-issue .flag-wrapper {
    background-color: #F6F6F2;
    padding: 2em 0;
    text-align: center;
}
.flag-project-issue-follow a.flag {
    background: #CCC;
    border: none !important;
    color: #222;
}
.flag-project-issue-follow a.unflag-action {
    background-color: #999999;
    border: none !important;
    color: #fff;
}
a.flag:hover,
a.flag:focus,
a.action-button:focus,
a.action-button:hover {
    border: none !important;
    background: #8aaf3c !important;
}
#project-issue-jumplinks h3 {
    text-align: left;
    font-size: 1.1em;
}
#aside #project-issue-jumplinks .item-list li {
    text-align: left;
    font-size: 1.1em;
}

/* From the_sun his styling */
#content .comment { position: relative; }
#content .comment .links.inline { position: absolute; top: 1.35ex; right: 1.5em; text-transform: capitalize; }
#content .comment .links.inline a { opacity: 0; transition: opacity 0.5s; font-weight: normal !important; }
#content .comment:hover .links.inline a, #content .comment .links.inline a:focus { opacity: 1; color: #0678BE; }
#content .comment .permalink-wrapper .permalink {font-size: 13px; }

div.codeblock {
    background: #fcfcfb
}

code, pre {
    font-size: 13px !important;
    line-height: 130% !important;
    font-family: "Courier New","Bitstream Vera Sans Mono",Monaco,"Lucida Console",monospace !important;
    background-color: #fcfcfb;
}
}
