css:
/*====================================================
  itpro.nikkeibp.co.jp 日経IT Pro liquid ja jp 2013/11
  update 2015/05/27
  ====================================================*/

@-moz-document regexp('http://itpro\\.nikkeibp\\.co\\.jp/(?!(active|article/Active)/).*') {

* {font-family: none !important;}

#wrapper {width: auto !important; border: none !important;}

/*---- header ---*/
#header {width: auto !important;}
[class^="gnav-"] ul {width: auto !important;}
#localHeader {position: static !important; width: auto !important; height: auto !important;}
#localHeader div, #localHeader a
  {position: static !important; display: inline-block !important; width: auto !important;} /* 2015/05/26 */
#localHeader .nc {margin: 0 !important; padding: 0 !important;} /* 2015/05/26 */
#localHeader .logo a {width: 68px !important; height: 30px !important; background-size: contain;}
#localHeader #infoNews {background: yellow;} /* 2015/05/26 */
#localNaviWrapper {width: auto !important;}


/*--- top page contents ---*/
#topContents .leftContents {width: 100% !important; margin: 0 -300px 0 0 !important;}
#topContents .leftContents>div
 {width: auto !important; margin: 0 300px 0 0 !important;}
#pickupArea h2 {width: auto !important;}
#pickupArea ul {position: relative; z-index: 1; padding: 0 !important;}
#pickupArea #rotationView
 {width: 100% !important; margin: 0 -271px 0 0 !important;}
#pickupArea #rotationView * {width: auto !important;}
#pickupArea #rotationView>* {margin: 0 271px 0 0 !important;}

/*--- topic contents ---*/
#contentsWrapper {margin: 0 !important;}
#contents {width: 100% !important; margin: 0 0 0 -125px !important;}
#contents>#mainContent  {width: 100% !important; margin: 0 -300px 0 0 !important;}
#contents>#mainContent>div {
 width: auto !important; margin: 0 300px 0 125px !important; border: 1px solid gray; padding: 0 0.5em !important;
 } /* 2015/05/26 */
#contents>#mainContent>div div {width: auto !important;}


/*--- footer ---*/
#footer {width: auto !important; padding: 0 !important; border: none !important;}
#footer .logo_box {display: none !important;}
#footer .bottom_link {text-align: left !important;}
#footer .bottom_link br {display: none !important;}
#footer #copy_box {margin: 0 !important; padding: 0 !important;}
#footer #copy_box .copyright {width: auto !important; margin: 0 !important;}
#footer #copy_box .copyright br {display: none !important;}
}
