css:
@-moz-document domain("tweetdeck.twitter.com") {
  * {
    border-radius: 0px !important;
  }
  html, .tweet-body {line-height: 1;}
  .compose-account {
    margin: 0px 2px !important;
  }
  .compose-accounts {margin: 5px 0px !important;}
  .compose-text{
    height: 74px;
    color: #A3A3A3 !important;
    background-color: #222426 !important;
    width: 220px !important;
    word-wrap: break-word;
  }
  .compose-reply-tweet {
    width: 210px !important;
    padding: 5px !important;
  }
  .scroll-styled-v {overflow-y: hidden !important;}
  .scroll-styled-v:hover {overflow-y: auto !important;}
  .scroll-styled-h {overflow-x: hidden !important;}
  .compose-title {padding: 0px !important;}
  .compose-header {height: auto !important;}
  .label {display: none;}
  .full-width {
    width: auto !important;
  }
  .character-count {
    font-size: 13px !important;
  }
  .character-count:disabled {
    width: 22px !important;
  }
  .compose-text-container {
    background: transparent !important;
  }
  .compose-media-bar-holder{
    background-color: #A3A3A3 !important;
  }
  .tweet, .tweet-detail, .compose-text {font-size: 10px !important;}
  .column-title, .compose-title-text {font-size: 17px !important;}
  .column-drag-handle{top: 0px !important;}
  .column-header, .column-header-temp {
    line-height: 30px !important;
    height: 35px !important;
    text-align: center;
  }
  .column-content {top: 35px !important;}
  .scroll-styled-v {overflow-y: hidden !important;}
  .scroll-styled-v:hover {overflow-y: auto;}
  .avatar{border-radius: 5px !important;}
  .column{margin: 0px !important;}
  div.app-columns {padding: 0px !important;}
  .txt-base-smallest {font-size: 10px !important;}
  .is-condensed .app-header {width: 30px !important;}
  .app-header-inner {
    width: 30px !important;
    padding: 0px !important;
  }
  .tweet-button {
    width: 30px !important;
    height: 30px !important;
    text-align: center !important;
  }
  .icon-compose {font-size: 25px !important;}
  .app-search-fake {
    width: 30px !important;
    height: 30px !important;
  }
  .is-condensed .popover-position-rt {
    left: 35px !important;
  }
  .column-navigator {
    top: 75px !important;
    width: 30px;
  }
  .column-nav-item {
    height: 25px !important;
  }
  .column-nav-link {
    font-size: 10px !important;
  }
  .icon-large {
    height: 20px !important;
    width: 20px !important;
    font-size: 18px !important;
  }
  .app-title {
    display: none;
  }
  .app-navigator {
    bottom: 0px !important;
    width: 30px;
  }
  .margin-bm{
    margin: 0px !important;
  }
  .is-condensed .app-content {
    left: 30px !important;
  }
  .compose-media-bar-thumb {
    max-width: 200px;
  }
  .tooltip {
    display: none !important;
  }
  .padding-hl {
    padding: 0px 5px !important;
  }
  .size48 {
    width: 32px !important;
    height: 32px !important;
  }
  .padding-vm {
    padding: 0px 2px !important;
  }
  .btn-extra-height {
    padding: 0px 2px !important;
  }
  .compose {
    background-color: rgb(48, 50, 54) !important;
  }
}
