css:
@-moz-document url-prefix("http://www.gremln.com/UserPages/ScheduledMessages.aspx") {
/*menu bar blue*/

#wrapper #navwrapper_authenticated,#ctl00_cntrlHeaderBarAuth_img_message_button,.tools_auth,.tools_link
{background:-webkit-linear-gradient(top, #133556 0%, #169 50%, #004066 51%, #102a44 100%)!important;}

.tools_auth {background: none!important;}

#navwrapper_authenticated {border-bottom: solid 1px #999;border-top: #000 !important;border-width:1px 0px 1px 0px !important;border-style:solid !important;}
.tools_auth a{border-bottom: solid 1px #999;border-width:0px 0px 1px 0px !important;border-style:solid !important;}

.menu_auth ul li:hover{border: 0px solid #999 !important;}
.menu_auth ul li:hover a {color:#000;}
#navwrapper_authenticated .tools_link:hover {color:#fff !important;}








/*background gray color*/
#submenu_wrapper {background:-webkit-linear-gradient(top, #444, #222)!important;}
#content_app{background:-webkit-linear-gradient(top, #444, #101010)!important;}

/*dashboard menu*/
#submenu {border: solid 1px #999;background:-webkit-linear-gradient(top, #133556 0%, #169 50%, #004066 51%, #102a44 100%)!important;}
#submenu a{color:#fff !important;}

/*bulk posts*/
#posts .ActionBtn {border: solid 1px #999;background:-webkit-linear-gradient(top, #133556 0%, #169 50%, #004066 51%, #102a44 100%)!important;}

/*buttons*/
#help .helpcollapsed,#helpexpanded,.aspbutton,.aspimgbtn {border: solid 1px #999 !important;background:-webkit-linear-gradient(top, #133556 0%, #169 50%, #004066 51%, #102a44 100%)!important;}

/*drop down menus*/
.highlighteddiv_app .dropdown{border: solid 1px #fff;background:#444!important;}
#posts .dropdown {color:#fff;}

.highlighteddiv_app {border: solid 1px #ccc !important;background:-webkit-linear-gradient(top, #eee 0%, #eee 100%)!important;color:#333;border-radius:10px !important;}
#posts .textinput {border: solid 1px #444 !important;background:-webkit-linear-gradient(top, #555 0%, #444 100%)!important;color:#fff;}



/*posts and schedualed posts*/
#ctl00_ContentBody_cntrlScheduledMessages_Rg_ScheduledMessages {}
#ctl00_ContentBody_cntrlScheduledMessages_Rg_ScheduledMessages_ctl00 {border-radius:4px;border: solid 1px #999 !important;background:-webkit-linear-gradient(top, #133556 0%, #169 50%, #004066 51%, #102a44 100%)!important;
}
.RadGrid_Office2007 {border-bottom: solid 1px #777;
border-left: solid 1px #777;
border-right: solid 1px #777;
}
.RadGrid_Office2007 .rgHeader,.RadGrid_Office2007 .rgHeader a {color:#fff;}

.RadGrid_Office2007 .rgRow td {border-color: #777 !important;}



}

@-moz-document url("http://www.gremln.com/Dashboard.aspx") {
/*top bar*/
#ctl00_cntrlHeaderBarAuthenticated_img_message_button,.tools_link,#navwrapper_authenticated
{background:-webkit-linear-gradient(top, #133556 0%, #169 50%, #004066 51%, #102a44 100%)!important;}

#dashboard_content .dashtabbed{background-color:rgba(0,0,0,0.75)!important;}

.tools_auth {background: none!important;}
#navwrapper_authenticated {border-width:1px 0px 1px 0px !important;border-color:#000 !important;border-style:solid;}
#navwrapper_authenticated {border-top: #000 !important;border-width:1px 0px 0px 0px !important;border-style:solid !important;}
#dashboard_content {border-top: #000 !important;border-width:1px 0px 0px 0px !important;border-style:solid !important;}

/*font color*/
.twittergrid .post h1 a {color:#37a !important;}
.twittergrid .post a {color:#37a !important;}
#dashboard_panel_container .tweetactions a {color:#999 !important;}
#dashboard_panel_container .facebookgrid .post h1 a {color:#37a !important;}
#dashboard_panel_container .facebookgrid .post a {color:#37a !important;}
#dashboard_panel_container .facebookgrid .post .subpost .subposttxt h1 {color:#37a !important;}

/*buttons*/
#dashboard_controls .DashCntrlBtn {border: solid 1px #999;background:-webkit-linear-gradient(top, #133556 0%, #169 50%, #004066 51%, #102a44 100%)!important;color:#fff}

.menu_auth ul li:hover{border: 0px solid #999 !important;}
.menu_auth ul li:hover a {color:#000;}
#navwrapper_authenticated .tools_link:hover {color:#fff !important;}
}

@-moz-document domain("gremln.com") {
/********Post's Popup********/
#cntrlMessageFormPopup_ajaxPanel2 .highlighteddivsub_app_popup {border: solid 1px #444 !important;background:-webkit-linear-gradient(top, #888 0%, #444 100%)!important;color:#555 !important;}

#dashmsgpopup label {color:#222;font-size:14px;}

.aspbutton,.asplinks{border-radius:4px;border: solid 1px #999 !important;background:-webkit-linear-gradient(top, #133556 0%, #169 50%, #004066 51%, #102a44 100%)!important;}

#fancybox-overlay {border-radius:10px;background-color: rgb(30, 30, 30) !important; opacity: 0.7 !important;}

#dashmsgpopup {background:-webkit-linear-gradient(top, #ccc 0%, #333 100%)!important;color:#555 !important;}
#fancybox-wrap #fancybox-outer {border-radius:10px;border: solid 1px #999 !important;background:-webkit-linear-gradient(top, #555 0%, #444 100%)!important;color:#555 !important;}
#fancybox-content {border-radius:8px;border: 0px solid #333;}

#cntrlMessageFormPopup_pnlMessageForm #cntrlMessageFormPopup_TextMessage{border: solid #999 !important;border-width:1px 1px 1px 0px !important;background:-webkit-linear-gradient(top, #aaa 0%, #777 100%)!important;color:#333;}
}
