/* facebook privacy reminder - by pfeiffer stylez customizing */
/* http://userstyles.org/styles/94760/facebook-privacy-reminder */

@-moz-document domain("facebook.com") {
.fbPrivacyAudienceIndicator[aria-label~="Public"] i.img,
.uiButton[aria-label~="Public"] i.img,
._42ft[aria-label~="Public"] i.img:not(._3-99),
._43_1[aria-label~="Public"] ._1lbg,

.fbPrivacyAudienceIndicator[aria-label~="Öffentlich"] i.img,
.uiButton[aria-label~="Öffentlich"] i.img,
._42ft[aria-label~="Öffentlich"] i.img:not(._3-99),
._43_1[aria-label~="Öffentlich"] ._1lbg,

.fbPrivacyAudienceIndicator[aria-label~="Público"] i.img,
.uiButton[aria-label~="Público"] i.img,
._42ft[aria-label~="Público"] i.img:not(._3-99),
._43_1[aria-label~="Público"] ._1lbg,

div[id*="PageComposerPagelet"] .fbComposerAudienceTourContext i {
border-radius: 50% !important;
border: 1px solid #ff0000 !important;
box-shadow: 0px 0px 3px #ff0000 !important;
opacity: 1 !important;
}
}
