css:
@-moz-document domain(forums.undergroundhiphop.com) {
body {
	background: #222;
	color: #777;
}

td.s {
	background: #444;
	color: #eee;
}

a,
a font {
	color: #5cf !important;
  	text-decoration: none;
}

a:visited {
	color: #4d88a2;
  	color: #bbb !important;
}

a:hover,
a:hover font {
	color: #fff !important;
}


td[width="18%"] {
	background: #333;
 	color: #ccc;
}

td[bgcolor="#DEDFDF"],
tr[bgcolor="#DEDFDF"] {
	background: #393939;
}

td[bgcolor="#F7F7F7"],
tr[bgcolor="#F7F7F7"] {
	background: #444;
}

td[bgcolor="#DEDFDF"] td.s {
	background: none;
  	color: #fc7;
}

td.b font[color="#000080"] {
	color: #ff0;
}

textarea {
	background: #888;
  	border: 1px solid #aaa;
}

td.b {
	color: #fff;
}

td.b b {
	color: #fc7;
}

td.b a b {
	color: #ccc;
}

font[color="red"] {
	color: #f55;
  	font-size: 10px;
}

font[color="#000080"] {
	color: #bb7;
}

hr {
	color: #555;
  	background: #555;
	border: 0;
	height: 1px;
}

tr[bgcolor="#ffffff"] {
	background: #111;
}

tr[bgcolor="#F1F1F1"] {
	background: #222;
}

td[bgcolor="#F1F1F1"] {
	background: #111;
}
}
