css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
body {
   background:url("http://i.imgur.com/nMRg87u.jpg") no-repeat fixed !important;
   background-color: rgb(100,100,100) !important;
   background-position: center !important;
   background-size: cover  !important;
 }

#logo {
height: 45px !important;
width: 196px !important;
}

#logo > .logo_anchor {
background: url('http://i.imgur.com/8Z3aNvK.png') no-repeat !important;
}


div.l-header .tab.iconic > a {
background-image: url('http://i.imgur.com/SaEjU3c.png') !important;
}

div.l-header .selection_nipple {
border-bottom: 12px solid rgba(50, 50, 50, .4) !important;
}

#xkit-control > a {
background-image: url('http://i.imgur.com/jtY44KR.png') !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {  
   font-family: Arial; color: #222 !important; 
   background: rgba(50, 50, 50, .4) !important;
}

.mceContentBody {background: white !important}

.chrome.blue {
border-color: #737373 !important;
background: -webkit-linear-gradient(top,#bababa 0,#999 100%) !important;
}

.split .chrome {
text-shadow: 0 1px 0 rgba(0,0,0,0) !important;
}
    
.split .chrome.options:after {
box-shadow: 0 1px rgba(0,0,0,0) !important;
}

.controls_section li .posts:before {
position: absolute !important;
height: 30px !important;
line-height: 28px !important;
width: 100px !important;
font: inherit !important;
}

.post_permalink {
background: url('http://i.imgur.com/DSITYKs.png') !important;
width: 17px !important;
height: 17px !important;
display: inline !important;
}

.post_permalink:hover {
background: url('http://i.imgur.com/x38ZTAS.png') !important;
width: 17px !important;
height: 17px !important;
display: inline !important;
}

.post_full .post_permalink:before {
border-right: 16px transparent !important;
}

.link_button:active {
border-color: 1px transparent !important;
background-color: #ddd !important;
}
    
.link_button .link_title {
font-size: inherit !important;
line-height: 20px !important;
}

.post_footer {
background-color: transparent !important;
}

.plexi.color {
background: rgba(100, 100, 100, 0.7) !important;
}

#glass_overlay.show {
display: none !important;
}

#right_column .controls_section li a,
#right_column .controls_section li a .count {
color: #e6e6e6 !important; 
}

#right_column {
  background-color: rgba(100, 100, 100, 0) !important;
}

div#right_column:after {
  background: none !important;
}

.open_blog { 
line-height: 30px !important;
height: 30px !Important;
}

.blog_menu .selected_blog #open_blog_link {
font-family: Arial !important;
font-size: inherit !important;
}

.controls_section li.section_header {
border-bottom: transparent !important;
display: none !important;
}

.controls_section.user_list li a:before {
background: transparent !important;
border-right: 5px transparent !important;
}
      
div.ui_follow:hover, .controls_section li .follow:hover {
background: rgba(0,0,0,.3) !important;
}

div.ui_follow, .controls_section li div.follow {
color: #e6e6e6 !important;
line-height: 8px !important;
font-size: 24px !important;
}

.search_form_row {
background-color: rgba(0,0,0,.3) !important;
}

#pagination a {
background-color: rgba(0,0,0,0) !important;
color: #e6e6e6 !important;
}

#dashboard_controls a, #dashboard_controls .control {
color: #e6e6e6 !important;
text-shadow: none;
}

.footer_links a {
color: #e6e6e6 !important;
}
}
