/*
    
    Theme Name: FunFile Dark Surface

    Theme Creator: Adam-J-Frost.
    
    Updated: 2020/01/06

    Created: 2013/11/04
    
    Description: This is a Dark Style for FunFile.

*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("funfile.org") {
body {
	background-image: url("https://i.ibb.co/CMvhC2k/bg.jpg") !important;
	background-position: center center;
	background-repeat: no-repeat;
	background-attachment: fixed;
	background-size: cover;
	background-color: #000000;
	font-family: arial, sans-serif !important;
	font-size: 11px !important;
	color: #8a8a8a !important;
}

.head_banner {
	background: url("https://i.ibb.co/vX0tdxp/header.png") no-repeat scroll 0 0 transparent !important;
	border-radius: 15px !important;
}

.ugroup_75 {
	color: #8a8a8a !important;
}

.ugroup_75:hover {
	color: white !important;
}

font {
	color: #8a8a8a !important;
}

.mainframe td {
	padding: 0px !important;
}

.l_side {
	background: transparent !important;
}

.r_side {
	background: transparent !important;
}

.mf_tr {
	background: transparent !important;
	background-color: rgba(0, 0, 0, 0.6) !important;
	border-top-right-radius: 15px !important;
}

.mf_tl {
	background: transparent !important;
	background-color: rgba(0, 0, 0, 0.6) !important;
	border-top-left-radius: 15px !important;
}

.mf_mr {
	background: transparent !important;
	background-color: rgba(0, 0, 0, 0.6) !important;
}

.mf_tm {
	background: transparent !important;
	background-color: rgba(0, 0, 0, 0.6) !important;
}

.mf_ml {
	background: transparent !important;
	background-color: rgba(0, 0, 0, 0.6) !important;
}

.mf_bl {
	background: transparent !important;
	background-color: rgba(0, 0, 0, 0.6) !important;
	border-bottom-left-radius: 15px !important;
}

.mf_br {
	background: transparent !important;
	background-color: rgba(0, 0, 0, 0.6) !important;
	border-bottom-right-radius: 15px !important;
}

.mf_bm {
	background: transparent !important;
	background-color: rgba(0, 0, 0, 0.6) !important;
}

.mb_top {
	background: transparent !important;
	background-color: rgba(0, 0, 0, 0.6) !important;
	border-top-left-radius: 15px !important;
	border-top-right-radius: 15px !important;
}

.mb_middle {
	background: transparent !important;
	background-color: rgba(0, 0, 0, 0.6) !important;
}

.mb_bottoml {
	background: transparent !important;
	background-color: rgba(0, 0, 0, 0.6) !important;
	border-bottom-left-radius: 15px !important;
	border-bottom-right-radius: 15px !important;
}

.mb_bottomr {
	background: transparent !important;
	background-color: rgba(0, 0, 0, 0.6) !important;
	border-bottom-left-radius: 15px !important;
	border-bottom-right-radius: 15px !important;
}

.donate {
	background: url("https://i.ibb.co/MMcBG3G/donate0.png") no-repeat scroll 0 0 transparent !important;
}

.idonate1 {
	background: url("https://i.ibb.co/vYHkh2K/donate1.png") no-repeat scroll center center transparent !important;
}

.idonate2 {
	background: url("https://i.ibb.co/xGftLKH/donate2.png") no-repeat scroll center center transparent !important;
}

.idonate3 {
	background: url("https://i.ibb.co/L8Hvj7v/donate3.png") no-repeat scroll center center transparent !important;
}

.idonate4 {
	background: url("https://i.ibb.co/QjLxdCh/donate4.png") no-repeat scroll center center transparent !important;
}

.btn {
	background: url("https://i.ibb.co/WnnB3fF/btn.png") repeat-x scroll 0 0 transparent !important;
	color: #8a8a8a !important;
	border-radius: 15px !important;
	padding-bottom: 19px !important;
}

.btn:hover {
	background: url("https://i.ibb.co/n63cKNQ/btn-hover.png") repeat-x scroll 0 0 transparent !important;
	color: white !important;
	border-radius: 15px !important;
}

h1,
h2 {
	color: #8a8a8a !important;
}

.menubox {
	font-size: 0.95em !important;
	margin-bottom: 7px !important;
}

.mf_content {
	background-color: rgba(0, 0, 0, 0.6) !important;
}

.rowhead {
	background-color: transparent !important;
}

.row1 {
	background-color: transparent !important;
}

.row2 {
	background-color: transparent !important;
}

.row3 {
	background: transparent !important;
	border-bottom: none !important;
}

.ys-inverse {
	background-color: transparent !important;
}

.ys-post-timestamp {
	color: #8E39FF !important;
}

#ys-posts {
	background-color: transparent !important;
}

.ys-posts {
	background-color: rgba(0, 0, 0, 0.6) !important;
}

#yshout .ys-post-nickname {
	color: #8a8a8a !important;
	background-color: transparent !important;
}

#ysout,
.ys-post-actions {
	background-color: transparent !important;
}

#yshout .ys-post {
	border-bottom: none !important;
	border: none !important;
	border-radius: 15px !important;
	margin-top: 5px !important;
	padding: 7px !important;
}

#yshout .ys-info-inline {
	border-top: none !important;
}

#yshout .ys-post-message {
	color: #8a8a8a !important;
}

td.colhead {
	background-color: transparent !important;
	border-bottom: none !important;
	padding-bottom: 5px !important;
}

td.rowhead {
	border-right: 1px solid #3a3a3a !important;
	padding-right: 5px !important;
}

a,
a:link,
a:active,
a:visited {
	color: #8a8a8a !important;
}

a,
a:link:hover {
	color: white !important;
	-webkit-transition: color .2s;
	-moz-transition: color .2s;
	-o-transition: color .2s;
	transition: color .2s;
}

#clock {
	color: #8a8a8a !important;
}

.credits {
	color: #8a8a8a !important;
	font-size: 0.8em !important;
}

.mb_title {
	color: white !important;
	margin-left: 14px !important;
	margin-right: 12px !important;
}

#myminicity_content {
	background: none repeat scroll 0 0 #1a1a1a !important;
}

#myminicity_state_table {
	color: #8a8a8a !important;
	font-size: 12px;
}

a:link .tenpic,
a:visited .tenpic {
	background-color: #8a8a8a !important;
	border: 0 !important;
	border-radius: 15px !important;
}

a:link:hover .tenpic:hover,
a:visited .tenpic:hover {
	background-color: white !important;
	border: 0 !important;
	-webkit-transition: background-color .3s;
	-moz-transition: background-color .3s;
	-o-transition: background-color .3s;
	transition: background-color .3s;
}

input {
	background-color: rgba(0, 0, 0, 0.4) !important;
	color: #8a8a8a !important;
	border-style: solid !important;
	border-radius: 10px !important;
	padding: 5px !important;
}

textarea {
	background-color: rgba(0, 0, 0, 0.4) !important;
	color: #8a8a8a !important;
	border-style: solid !important;
	border-radius: 10px !important;
	padding: 5px !important;
}

select {
	background-color: rgba(0, 0, 0, 0.4) !important;
	color: #8a8a8a !important;
	border-style: solid !important;
	border-radius: 10px !important;
	padding: 5px !important;
}

option {
	background-color: rgba(0, 0, 0, 0.4) !important;
	color: #8a8a8a !important;
	border-radius: 10px !important;
	padding: 5px !important;
}

#suggestions {
	background-color: rgba(0, 0, 0, 1) !important;
	color: #8a8a8a !important;
	border-radius: 10px !important;
	padding: 5px !important;
}

#last21pic {
	border: 2px solid #3a3a3a !important;
	border-radius: 15px !important;
	max-height: 215px !important;
	max-width: 212px !important;
}

#moviepic {
	border: 2px solid #3a3a3a !important;
	border-radius: 15px !important;
	max-height: 230px !important;
	max-width: 212px !important;
}

#tvpic {
	border: 2px solid #3a3a3a !important;
	border-radius: 15px !important;
	max-height: 212px !important;
	max-width: 212px !important;
}

#musicpic {
	border: 2px solid #3a3a3a !important;
	border-radius: 15px !important;
	max-height: 230px !important;
	max-width: 212px !important;
}

#gamespic {
	border: 2px solid #3a3a3a !important;
	border-radius: 15px !important;
	max-height: 245px !important;
	max-width: 212px !important;
}

#appspic {
	border: 2px solid #3a3a3a !important;
	border-radius: 15px !important;
	max-height: 230px !important;
	max-width: 212px !important;
}

#portablepic {
	border: 2px solid #3a3a3a !important;
	border-radius: 15px !important;
	max-height: 245px !important;
	max-width: 212px !important;
}

#0daypic {
	border: 2px solid #3a3a3a !important;
	border-radius: 15px !important;
	max-height: 245px !important;
	max-width: 212px !important;
}
.credits::before {
    content: 'FunFile Redesign by Adam J Frost | Original Design:' !important;
    color: #000000 !important;
}
}
