css:
@-moz-document domain("loading.se") {
/* 
NYA loading.se remake av lassekongo83
Testad i Opera, Chrome
Version 0.4.4
*/

/* Plockar bort en del grejer samt fixar bredden */
#site_wrapper { max-width: 998px !important; width: 998px !important; } /* Bredden på huvud-kolumnen */
#main_col  { max-width: 970px !important; width: 970px !important; padding-left: 7px !important; }
#sec_col, #header_news, #footer, #fb-root { display: none !important; } /* Detta censurerar vi tycker jag ;-) */
/* Plockar bort twitter och facebook knapparna */
a[class="tooltip social_icons twitter"], a[class="tooltip social_icons facebook"], .fblike { display: none !important; }
.theme_selector { display: none !important; } /* Behövs ej då vi kör med eget tema ;-) */
.ox_zone { display: none !important; }

/* Färger o form */
body { background-image: none !important; background-color: #DEE0E3 !important; font-family: arial, verdana, helvetica neue, helvetica, sans-serif !important; padding-top: 0 !important; }
* { text-shadow: none !important; } /* Tar bort skuggor från texten */

#site_wrapper, #content, #main_col { background-color: #f9f9f9 !important; }

#branding img {max-height: 30px !important;} /* Gör loggan lite mindre */

.forum_list,
.forum_threads_list {
	background-color: none !important;
	border: none !important;
}

tr[class="pagination"] td[colspan="7"] {
	border-bottom: 1px solid #e8e8e8 !important;
}

.forum_forum_tbr_td {
	background: none !important;
	border: none !important;
}

.forum_cat_tbr,
.forum_forum_tbr {
  	background: linear-gradient(#f0f0f0, #f0f0f0) !important;
	border: 1px solid #e8e8e8 !important;
	border-top: 0px !important;
}

.forum_cat_tbr.odd,
.forum_forum_tbr.odd  {
	background: linear-gradient(#f8f8f8, #f2f2f2) !important;
	border-top: 1px solid #e8e8e8 !important;
}

.forum_thread_nav {
	border-bottom-width: 0 !important;
}

td[class="forum_forum_tbr_td thread_modified"][width="150"] {
	text-align: right !important;
    border-right: 1px solid #e8e8e8 !important;
}

.glued {
  	background: linear-gradient(#e8e8e8, #e2e2e2) !important;
	border: 1px solid #d8d8d8 !important;
	border-radius: 0 !important;
}

/* Byter ut ikonerna som talar om om tråden är läst citerad etc */
img[src="/gfx/static_gfx/icon_pm_unread.png"] {
	height: 0 !important;
	width: 0 !important;
	padding-left: 12px !important;
	padding-top: 12px !important;
	background: linear-gradient(#849FC2, #6683B3) !important;
	border-radius: 18px !important;
}

img[src="/gfx/static_gfx/icon_pm_read.png"] {
	height: 0 !important;
	width: 0 !important;
	padding-left: 12px !important;
	padding-top: 12px !important;
	background: linear-gradient(#e2e2e2, #ccc) !important;
	border-radius: 18px !important;
}

img[src="/gfx/forum_allread_w.gif"] {
	height: 0 !important;
	width: 0 !important;
	padding-left: 8px !important;
	padding-top: 8px !important;
	background: linear-gradient(rgba(100,100,100,0.4), rgba(100,100,100,0.4)) !important;
	border-radius: 18px !important;
}

img[src="/gfx/forum_allread_q.gif"] {
	height: 0 !important;
	width: 0 !important;
	padding-left: 12px !important;
	padding-top: 12px !important;
	box-shadow:inset 0px 0px 0px 3px #ccc !important;
	background: none !important;
	border-radius: 18px !important;
}

img[src="/gfx/forum_unread_q.gif"] {
	height: 0 !important;
	width: 0 !important;
	padding-left: 12px !important;
	padding-top: 12px !important;
	box-shadow:inset 0px 0px 0px 3px #E2013D !important;
	background: none !important;
	border-radius: 18px !important;
}

img[src="/gfx/forum_unread_w.gif"] {
	height: 0 !important;
	width: 0 !important;
	padding-left: 8px !important;
	padding-top: 8px !important;
	background: linear-gradient(rgba(226,1,61,0.6), rgba(226,1,61,0.6)) !important;
	border-radius: 18px !important;
}

/* Fyller ut innehållet på andra sidor istället för bara på forumet */
.main_left_col { 
	width: 688px !important; 
	background: none !important;
}

/* Rundar kanterna längst ner */
#site_wrapper { 
	border-radius: 0px 0px 7px 7px !important;
	box-shadow: 0px 1px 2px 1px rgba(0,0,0,0.1) !important;
}

/* Lite gradients och sånt */
#header {
	margin-top: 24px !important;
	background: linear-gradient(#477094, #0A4070) !important;
}

#header .vert_sep { display: none !important; }

/* Avataren i headern */
#header .avatar { 
	background-color: #477094 !important; 
	border: none !important;
	border-radius: 2px !important;
}

#header .avatar img { 
	box-shadow: 0px 0px 3px black !important;  
}

.forum_thread_tbr .avatar img {
	width: 120px !important;
	height: 120px !important;
}

/* Användarens header */
#user_header {
	background-image: none !important;
	margin-right: -14px !important;
	margin-left: -14px !important;
	background: linear-gradient(#052038, #08365F) !important;
}

#user_header .frame {
	background-color: #0A3E6D !important;
	border: none !important;
	border-radius: 4px !important;
}

#user_header .frame .avatar { 
	box-shadow: 0px 0px 3px #000 !important;
	border-color: rgba(0, 0, 0, 0.5) !important;
}

#user_header .meta > span { color: #8EB4D3 !important; }

/* Den blåa menyn */
#site_navigation {
	position: fixed !important;
	top: 0px !important;
	height: 24px !important;
	width: 998px !important;
	z-index: 1000 !important;
	box-shadow: none !important;
	border-top: none !important;
	border-bottom: 1px solid rgba(0,0,0,0.4) !important;
	background: linear-gradient(#849FC2, #6683B3) !important;
}

.rgt .pictos { color: #7B95AA !important; } /* färgen på blogg, quote, pm knapparna */

#site_navigation > ul.nav { height: 20px !important; }
#site_navigation > ul.nav > li { height: 20px !important; }
#site_navigation > ul.nav > li div.hit_area { top: 10px !important; }

#site_navigation > ul.nav > li > a {
	position: relative !important;
	top: -10px !important;
	height: 20px !important;
}

#site_navigation ul.nav > li > a, ul.nav.dd > li > span { padding-top: 0 !important; }

#site_navigation div.repelled { 
	position: relative !important; 
	top: -8px !important;
}

/* Sub-menyn */
#site_navigation ul.nav.sub { 
	border-radius: 0px 0px 4px 4px !important;
	box-shadow: none !important;
	background-color: rgba(0, 0, 0, 0.9) !important;
}

#site_navigation ul.nav.dd > li:hover > a, ul.nav.dd > li:hover > span { 
	background-color: rgba(0, 0, 0, 0.9) !important;
	border-radius: 0px 0px 0px 0px !important;
	min-height: 24px !important;
	max-height: 24px !important;
}

/* Flytta högerkolumnen på huvudsidan längre upp */
.section_index .main_right_col {
	top: 0px !important;
	right: 4px !important;
	position: absolute !important;
}

/* sök grejen */
.g_search {
	background: none !important;
	border: none !important;
	border-radius: 0px !important;
	max-height: 24px !important;
	margin-top: 19px !important;
	margin-left: 300px !important;
}

.g_search input {
	max-height: 22px !important;
	border-radius: 2px !important;
	border: none !important;
	box-shadow: none !important;
	font: arial !important;
	font-weight: normal !important;
	margin-top: 2px !important;
	background-image: none !important;
	background: linear-gradient(rgba(255,255,255,0.8), rgba(255,255,255,1.0)) !important;
}

.g_search input:focus::-webkit-input-placeholder { opacity: 0 !important; }
.g_search input:focus::-moz-placeholder { color:transparent !important; }

/* bort med prickikorven */
.noise { background-image: none !important; }

/* Den blåa lilla titeln vid högerkolumnen på huvudsidan bland annat */
.related_box > h3 {
	background-image: none !important;
	box-shadow: 0px 3px 5px #000 !important;
	border-radius: 4px !important;
	background: linear-gradient(#4CC1EF, #3CAEEA) !important;
}

#main_col .related_box ul { padding-top: 3px !important; }

/* quote delen */
.forum_text_quote { 
	background-color: #E0E0E0 !important; 
	padding: 8px !important;
	border: 1px solid #CCCCCC !Important;
	background: linear-gradient(#E0E0E0, #DCDCDC) !important;
}

.quote { 
	background-color: #E0E0E0 !important; 
	padding: 8px !important;
	border: 1px solid #CCCCCC !Important;
	background: linear-gradient(#E0E0E0, #DCDCDC) !important;
}

.quote p, .forum_text_quote p {
	font-style: normal !important;
	line-height: 16px !important;
	color: #333 !important;
	font-size: 11px !important;
}

.forum_text_quote .author, .quote .author, .forum_text_quote .author:hover, .quote .author:hover {
	font-size: 12px !important;
	color: #222 !important;
}

.quote .quote_char, .forum_text_quote .quote_char { color: #ccc !important; }

/* visar vilken forumdel vi är i */
.forum_cat_sep {
	max-width: 986px !important;
	max-height: 34px !important;
	color: #fff !important;
	background: linear-gradient(#839EC1, #6582B2) !important;
  	box-shadow: 0 1px 2px 0 rgba(0,0,0,0.2) !important;
	border: none !important;
	border-radius: 2px !important;
}

span.forum_cat_name { border-right: 1px solid rgba(97,123,163,0.5) !important; }

.forum_bc_separator { display: none !important; }

.forum_cat_sep a:hover { color: #71C6F0 !important; }
.forum_cat_name, .forum_cat_desc, .forum_bc_separator { color: #fff !important; background: none !important; }
.forum_cat_name a, .forum_cat_desc a, .forum_bc_separator a { color: #fff !important; background: none !important; }

/* Grejen som visar vilken tråd-layout vi valt */
.forum_style_sel {
	background: none !important;
	border: none !important;
	color: #fff !important;
	max-height: 34px !important;
}

.forum_style_sel.active {
	background: none !important;
	border: none !important;
	background: linear-gradient(rgba(97,123,163,0.2), rgba(97,123,163,0.7)) !important;
	box-shadow:inset 0px 0px 2px 0px rgba(0,0,0,0.2) !important;
}
span.icon.f_threads {
	background-repeat: no-repeat !important;
	background-position: center !important;
	-webkit-filter: contrast(619%) brightness(619%) !important;
	-moz-filter: contrast(619%) brightness(619%) !important;
}

span.icon.f_cats {
	background-repeat: no-repeat !important;
	background-position: center !important;
	-webkit-filter: contrast(619%) brightness(619%) !important;
	-moz-filter: contrast(619%) brightness(619%) !important;
}

/* Knappen Ny tråd i en tråd */
#cat_id_1,
#cat_id_2,
#cat_id_3,
#cat_id_4,
#cat_id_5,
#cat_id_6,
#cat_id_7,
#cat_id_8,
#cat_id_9,
#cat_id_ {
	background: none !important;
	border: none !important;
	color: #fff !important;
}

#cat_id_1:hover,
#cat_id_2:hover,
#cat_id_3:hover,
#cat_id_4:hover,
#cat_id_5:hover,
#cat_id_6:hover,
#cat_id_7:hover,
#cat_id_8:hover,
#cat_id_9:hover,
#cat_id_:hover,
.forum_style_sel:hover {
	background-color: rgba(255,255,255,0.1) !important;
	border: none !important;
	color: #fff !important;
}

/* trådtiteln längst upp */
.forum_thread_info {
	background: #f2f2f2 !important;
}

.forum_thread_info h3 { color: #444 !important; }
.forum_thread_info { color: #999 !important; }

td[class="forum_thread_info"] {
	border-width: 1px 0 0 1px !important;
	border-style: solid !important;
	border-color: #e8e8e8 !important;
}

td[class="forum_thread_info"][align="right"] {
	border-width: 1px 1px 0 0 !important;
	border-style: solid !important;
	border-color: #e8e8e8 !important;
}

/* Knappar */

.forum_cat_sep .button {
	background: none !important;
	border-left: 1px solid #f0f0f0 !important;
	border-right: none !important;
	border-bottom: none !important;
	border-top: 1px solid #e8e8e8 !important;
	border-radius: 0px !important;
	box-shadow: none !important;
}

.forum_cat_sep .button:hover { 
	background: none !important;
	border-left: 1px solid #f0f0f0 !important;
	border-right: none !important;
	border-bottom: none !important;
	border-top: 1px solid #e8e8e8 !important;
	border-radius: 0px !important;
	box-shadow: none !important;
}

.button {
	border-radius: 3px !important;
	background-image: none !important;
	background: linear-gradient(#f7f7f7, #DCDCDC) !important;
	border-color: rgba(0,0,0,0.3) !important;
	box-shadow:inset 0px 1px 0px rgba(255,255,255,1.0), 0px 1px 2px rgba(0,0,0,0.2) !important;
}

.button.ok, .button.no { color: #333 !important; }
.button.ok:hover, .button.no:hover { color: #777 !important; }

.button:hover {
	border-radius: 3px !important;
	background-image: none !important;
	background: linear-gradient(#F5F5F5, #E8E8E8) !important;
	border-color: rgba(0,0,0,0.35) !important;
	box-shadow:inset 0px 1px 0px rgba(255,255,255,1.0), 0px 1px 2px rgba(0,0,0,0.3) !important;
}

.button:active {
	border-bottom-color: #CCCCCC !important;
	border-left-color: #AAAAAA !important;
	border-right-color: #AAAAAA !important;
	border-top-color: #888888 !important;
	box-shadow: inset 0px 1px 5px rgba(0, 0, 0, 0.251), 0px 1px 0px #FFFFFF !important;
	background: linear-gradient(#DCDCDC, #f7f7f7) !important;
}

#header #login_nav ul.nav.dd > li > a { /* inloggningsknappen */
	background-image: none !important;
	color: #fff !important;
	border: none !important;
	background-color: #7B95AA !important;
	background: linear-gradient(#8EB4D3, #7B95AA) !important;
	border-radius: 10px 10px 10px 10px !important;
}

#header #login_nav ul.nav.dd > li > a:hover {
	background-color: #8BA8BF !important;
	background: linear-gradient(#9CC5E7, #8BA8BF) !important;
	border-radius: 10px 10px 10px 10px !important;
}

/* Hover effekter på länkar och dylikt */
.forum_user_off:hover { color: #999999 !important; }
.forum_user_on:hover { color: #FF66AA !important; }
a.forum_forum_thread:hover { color: #888888 !important; }
.forum_text_url:hover, .forum_thread_tbr .action_link:hover, .action_link:hover, .back a:hover, .author:hover, h3 a:hover, .user_text_url:hover, a[class="action_link"][href="#comment"]:hover { color: #71C6F0 !important; }
.forum_thread_nav > a { color: #888 !important; }
.forum_thread_nav > a:hover { color: #bbb !important; }

/* Dold text ska vara tydligare samt visas när musen är över istället för att man ska behöva markera allt */
.forum_text_hidden1 {
	font-weight: bold !important;
	margin: 0 !important;
	color: #F02B2B !important;
}

span.forum_text_hidden2:hover {
	font-weight: normal !important;
	color: #fff !important;
}

/* Bredden på texten */
.forum_thread_text { width: auto !important; max-width: 753px !important; }

/* Fet text till länkar och lite andra fixar med fet-text och undertext. Måste kunna gör bättre men jag glömma hur. */
.forum_thread_text strong { font-weight: bold !important; }
.forum_thread_text u strong, .forum_thread_text u strong a, .forum_thread_text strong u, .forum_thread_text strong u a { text-decoration: underline !important; font-weight: bold !important; }
.forum_thread_text u a, .forum_thread_text u { text-decoration: underline !important; }

/* Text area (där man skriver sina inlägg) */
.thread_reply.light textarea { 
	background-color: #f4f4f4 !important;
	border-color: #ccc !important;
	max-width: 618px !important;
	width: 618px !important;
	border-radius: 0px !important;
}

.lform input[type="text"], .lform input[type="password"], .lform select, .lform textarea { 
	background-color: #f4f4f4 !important;
	border-color: #ccc !important;
	border-radius: 0px !important;
	font-family: arial, verdana, helvetica neue, helvetica, sans-serif !important;
}

.lform { border-radius: 0px !important; }

form[id="text"][class="lform"] {
	background-color: #f0f0f0 !important;
	border-color: #ccc !important;
}

#forum_id {
	height: 27px !important;
	width: 220px !important;
}

#subject {
	height: 24px !important;
	width: 500px !important;
	float: right !important;
}

#message {
	min-width: 630px !important;
	border-radius: 0px !important;
	background: linear-gradient(#f0f0f0, #f8f8f8) !important;
	box-shadow:inset 0px 1px 2px 0px rgba(0,0,0,0.1) !important;
}

/* gör inläggen lite mer kompakta samt separerar avatar-delen med en border  */
td[class="forum_thread_tbr_td"][width="160"] {
	width: 160px !important;
	min-width: 160px !important;
	border-right: 1px dotted #ccc !important;
}

.forum_thread_tbr_td {
	max-width: 750px !important;
}

.section_forum .forum_thread_nav { border-color: #ccc !important; }
.forum_cat_tbr td, .forum_forum_tbr td, .forum_thread_tbr_foot > td, .forum_thread_tbr > td { border: none !important; }

/* Färger och grejs på inläggen */

.forum_thread_list {
	border: 1px solid #e8e8e8 !important;
}

.forum_thread_tbr_foot.odd .forum_thread_tbr_td,
.forum_thread_tbr_foot .forum_thread_tbr_td {
	border-bottom: 1px solid #e8e8e8 !important;	
}

.forum_thread_tbr > td {
	background: none !important;
}

.forum_thread_tbr_head .forum_thread_tbr_td,
.forum_thread_tbr_foot .forum_thread_tbr_td,
.forum_thread_tbr {
	background: #f2f2f2 !important;
}

.forum_thread_tbr_head.odd .forum_thread_tbr_td,
.forum_thread_tbr_foot.odd .forum_thread_tbr_td,
.forum_thread_tbr.odd {
	background: none !important;
}

.forum_thread_tbr_head.odd,
.forum_thread_tbr_td {
	background: none !important;
}

/* Spelgrannar */
.match_bar_inner {
	background-image: none !important;
	background: linear-gradient(#C8E870, #9AD548) !important;
}

/* navigeringsknappar */
#user_nav li > a {
	background-image: none !important;
	background: linear-gradient(#D5D5D5, #B0B0B0) !important;
}

#user_nav li > a:hover {
	background-image: none !important;
	background-color: #D5D5D5 !important;
}

#user_nav li.active > a, #user_nav li.active > a:hover {
	background-image: none !important;
	background-color: #f9f9f9 !important;
}

/* fyller ut bloggtexten lite mera */
.blogg_text { width: 100% !important; }

.list_item.comment_item {
	border: 1px solid #e8e8e8 !important;
}
.list_item {
	border-bottom: 0px !important;
}

/* Kod-rutor i <pre> */
.forum_thread_text pre {
	background-color: #e8e8e8 !important;
    border: 1px solid #ccc !important;
	padding: 12px !important;
}

/* Större YouTube videos */
iframe[width="416"][height="312"], iframe[width="435"][height="357"], iframe.youtube-player {
	width: 640px !important;
	height: 360px !important;
}
}
