css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.ubuntu-fr.org"),domain("doc.ubuntu-fr.org") {

/*
Couleurs de référence:
#77216F : light aubergine
#5E2750 : medium aubergine
#2C001E : dark aubergine
#AEA79F : warm grey
#333333 : dark grey
*/

body { background-color: #f7f6f5 !important; background-image: none !important; font-family: "ubuntu",arial,sans-serif !important; font-size: .9em !important; }
a { color: #dd4814 !important; }
hr { border-width: 1px 0 0 !important; border-color: rgba(0,0,0,.2) !important; }

#page { border: 0 none !important; background-image: none !important; padding: 0 0 2em !important; }


/* BLOCS_PRINCIPAUX */
#navbar, #announce, #main, #footer, .block-inner { margin-left: 18em !important; }
html.hidemenu #navbar, html.hidemenu #main, html.hidemenu #references, html.hidemenu #announce, html.hidemenu #footer { margin-left: 0 !important; }
#navbar, #announce, #main, #footer, .welcome, #brdfooter { border-color: rgba(0,0,0,.1) rgba(0,0,0,.2) rgba(0,0,0,.3) rgba(0,0,0,.1) !important; }
#mainindex, #main, #mainviewforum, #mainviewindex { padding: 1em !important; }

#pagetitle,
#profile .blockmenu,
#announce{ background: -moz-linear-gradient(bottom,  #d05630 50%, #ce6748 100%) #d05630 !important; border-radius: 4px !important; }
#pagetitle a,
#profile .blockmenu,
#announce{ color: rgba(255,255,255,.8) !important; }


/* ANNONCE ORANGE */
#announce .hd { background: transparent !important; border-bottom: 0 none !important; padding: 0 .66em !important; }
#announce {  }
  #announce .hd h2 { border-bottom: 1px dotted rgba(0,0,0,.2); }
  #announce .box { padding: .66em 0 !important; }
  #announce a{ color: #fff !important; font-weight: bold !important; }

#main #navbar { margin-left: 0 !important; }


/* MESSAGES ACCUEIL */
#announce-welcome tr:hover, .welcome { background: none !important; }
  .welcome, .winner { padding: .6em 2em !important; }
  .welcome { border: 0 none !important; }
  .welcome, .welcome a { color: #772953 !important;}
    .welcome a { font-weight: bold !important; }
    .welcome p { margin: 0 !important; }
  .winner, #winner_inbox { background: none !important; border: 0 none !important; }
  .winner { padding-left: 0 !important; }
    #winner_inbox { -moz-box-sizing: border-box; padding: 1em !important; background: rgba(0,0,0,.05) !important; }
      #winner_inbox p { padding: 0 !important; }

.tclcon h3 a { text-decoration: none !important; text-transform: uppercase; font-size: .8em !important; }


/* FORUM */
#pagetitle,
#announce .hd h2,
.blocktable h2 { padding: .4em !important; text-align: center !important; color: rgba(255,255,255,.6); text-transform: uppercase; }

.blocktable, .blockpost h2, .blocktable h2 span, .blocktable h2, #quickpost h2, #postreview .blockpost .postleft dt, #punindex .blocktable h2 { background-image: none !important; }
  .blocktable h2 { -moz-box-sizing: border-box; background: #772953 !important; text-indent: 0 !important; height: auto !important; margin: 1.33em auto !important; }
    .blocktable h2 span { display: inline !important; padding: 0 !important; margin: 0 !important; }

  .blocktable .box { margin-top: 0 !important; }
    .blocktable .box th{ text-transform: uppercase !important; color: #333 !important; }
    .tcr{ padding-left: 0 !important; }
    tbody .tcl, tbody .tc1, tbody .tc2, tbody .tc3, tbody .tc4, tbody .tc5, tbody .tcr { border: 0 none !important; padding-top: .33em !important; padding-bottom: .33em !important; }
    #punindex .roweven, #punsearch .roweven, #vf .roweven { background: #efedeb !important; }
    .newtext, .pagestext { color: rgba(0,0,0,.5) !important; font-size: .8em !important; }    
#quickpost h2 { display: none; }

/* nav */
.inbox.crumbsplus { margin-bottom: 1em !important; }
.crumbsplus .pagelink { padding: 0 !important; margin: .5em 0 0 !important; }
.crumbsplus .pagelink a,#navbar p strong { padding: 0 .2em; }

/* messages */
.blockpost { min-height: 90px !important; position: relative !important; }
.blockpost { background: none transparent !important; }
.blockpost, .quotebox { border: 0 none !important; margin: 0 0 1em !important; }
    /* header */
.blockpost h2 { position: absolute !important; top: 1px !important; right: 1px !important; margin: 0 !important; padding: 0 .5em .25em !important; text-indent: 0 !important; height: 1em !important; font-size: 1em !important; background: rgba(0,0,0,.066) !important; border-radius: 0 0 0 4px; }
.blockpost h2, .blockpost h2 span { display: block !important; text-align: right !important; }
.blockpost h2 span.conr { background: none !important; padding: 0 !important; margin: 0 0 0 1em !important; color: #77216F !important; }
    /* avatar + message */
.blockpost .box { margin: 0 !important; padding: 0 !important; min-height: 0 !important; }
.postbody { padding: 0 !important; }
    /* avatar */
.postleft { position: absolute !important; }
#postreview .postleft { margin: 0 !important; }
.postleft, .postleft dl, .postleft dl dd { display: block !important; }
.postleft dl { position: relative !important; }
.postleft dt { position: absolute !important; top: 70px; padding: 0 !important; width: 60px !important; font-size: .8em; line-height: 1; text-align: center; }
#postreview .postleft dt, #punmoderate .postleft dt { margin: 0 !important; height: auto !important; }
#postreview .postleft dd { margin: 0 0 0 100px !important; }
#postreview .postleft dd span { padding: 0 !important; color: #77216F !important; font-size: .9em !important; }
.postleft strong { font-weight: normal !important; }
#punviewtopic .postleft dd, .postleft .postavatar { position: static !important; display: table-cell !important; width: 60px !important; height: 60px !important; margin: 0 !important; text-align: center; vertical-align: middle !important; background: rgba(0,0,0,.05) !important; border: 1px solid rgba(0,0,0,.15); }
    /* message */
.postright, .postfoot { margin: 0 0 0 80px !important; border: 1px solid #ccc; }
.postright {  padding: 1.33em !important; position: relative !important; border-bottom-width: 0; border-radius: 4px 4px 0 0 !important; }
#postreview .postright { border-bottom-width: 1px; border-radius: 4px !important; }
.postmsg { padding: 0 !important; min-height: 0 !important; font-size: 1.1em !important; line-height: 1.5 !important; }
.postmsg:after, .postmsg:before { right: 100%; border: solid transparent; content: " "; height: 0; width: 0; position: absolute; pointer-events: none; }
.postmsg:after { border-color: rgba(255, 255, 255, 0); 	border-right-color: #fff; border-width: 10px; top: 75px; margin-top: -10px; }
.postmsg:before { border-color: rgba(200, 200, 200, 0);	border-right-color: #ccc; border-width: 11px; top: 75px; margin-top: -11px; }
    /* message: quote */
.postedit em, cite { font-style: normal !important; color: #77216F !important; font-size: .9rem !important; }
cite { margin: 0 !important; }
.quotebox { min-height: 0 !important; background: none !important; border: 0 none !important; margin-left: 1.66em !important; padding: 0 0 0 .66em !important; border-radius: 0 !important; border-left: 4px solid #AEA79F !important; }
blockquote { margin: 0 !important; font-size: .9rem !important; color: #827c75 !important; }
blockquote p:last-child{ margin: 0 !important; }
    /* message:code */
.codebox { border-radius: 2px !important; border-color: rgba(0,0,0,.25) !important; font-size: .9rem !important; }
    /* footer */
.postfoot { border-top: 0 none; border-radius: 0 0 4px 4px; }
.postfootright { display: inline-block !important; }
.postfootright > ul { border-top: 1px dotted rgba(0,0,0,.1) !important; }

/* form réponse */
.blockform { margin: 2em 0 !important; padding: 1em !important; background: #eee !important; border: 0 none !important; border-radius: 4px !important; }
.blockform h2 { padding: 0 !important; margin: 0 0 1em !important; color: #77216F !important; text-transform: uppercase !important; }
.blockform .inform { margin: 0 !important; padding: 0 !important; }
.blockform .inform legend { display: none; padding: 0 !important; margin: 0 !important; color: #77216F !important; }
.blockform .infldset, #quickpost .infldset label  { padding: 0 !important; margin: 0 !important; }
.blockform textarea { background: #fff !important; border: 1px solid rgba(0,0,0,.2) !important; }
.blockform .bblinks { margin : 0 !important; font-size: .9em; }
.blockform .rbox { margin: 1em 0 0 !important; }
.blockform .buttons { margin: 1em 0 !important; }
.buttons input { padding: .33em .66em !important; min-width: 10em !important; border-width: 1px !important; border-style: solid !important; border-color: rgba(0,0,0,.1) rgba(0,0,0,.2) rgba(0,0,0,.2) rgba(0,0,0,.1) !important; }
.buttons input:hover { border-color: #77216F !important; color: #77216F !important; cursor: pointer !important;}

/*input, select, option, textarea { background-color: transparent !important; }*/

/* NAVIGATION A GAUCHE */
#navigation li.menu, #navigation li#active.menu { background: none !important; padding: .66em 0 !important; margin: 0 !important; border-width: 0 0 1px !important; border-style: dotted !important; border-color: rgba(0,0,0,.2) !important; border-radius: 0 !important; }
#navigation li.menu:hover, #navigation li#active.menu:hover { background-color: rgba(0,0,0,.05) !important; }
#navigation li.menu:last-child { border: 0 none !important; }
  #navigation a.title {  text-transform: uppercase !important; font-weight: bold !important; }
  #navigation #active a.title { border: 0 none !important; }
    #navigation a.title:hover span { background: none !important; }
  #navigation li.menu > ul { margin-left: 2em !important; }
    #navigation .cat { margin-top: 1em !important; }
      #navigation .cat h2 { background: none !important; border: 0 none !important; margin: 0 !important; padding: 0 !important; font-size: .8em !important; color: #444 !important; text-transform: uppercase !important; }
      #navigation .cat h2:before { display: none; }
      #navigation .cat a { padding: 0 !important; min-height: 0 !important; }
      #navigation .cat a:before { display: none; }
      #navigation .cat a:hover { font-weight: normal !important; text-decoration: underline !important; }


/* DOCUMENTATION */
#navbar #pagetitle { border-radius: 4px 4px 0 0 !important; }
#main h1:after { display: none !important; }
#main h1 { position: relative !important; left: 44px; display: inline-block !important; margin: 1em auto !important; padding: 0 !important; background: none !important; color: #772953 !important; font-weight: normal !important; }
#main h1:before { position: absolute !important; left: 0 !important; top:-4px !important; width: 32px; height: 32px; background: url(data:image/png;base64,) no-repeat center center !important; }
/*table { border: 1px solid rgba(0,0,0,.13) !important; }*/
.inline { width: 100%; }
  .inline, tr { background: none !important; }
  th:before, th:after { display: none !important; } 
  .inline th { background: #772953 !important; color: rgba(255,255,255,.6) !important; padding: .33em 1.33em !important; text-transform: lowercase !important; }
  .inline td { border: 0 none !important; background: rgba(0,0,0,.03) !important; padding: 1.33em !important; vertical-align: top !important; }
  .inline tr td:nth-child(odd) { background: rgba(0,0,0,.06) !important; }
}
