css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {

body {
background: url("http://farm9.static.flickr.com/8521/8516885357_b05ff2cf07_o.jpg") no-repeat 0 0;
background-attachment: fixed;
  background-size: 100%;
  color: #fff;
}

li#popover_blogs
{
  background: #000;
}


.message
{
  color: #fff;
  }

#tinymce {background: #ffffff !important;}
 
.post_full .post_notes_avatar .avatar_frame {
position: relative !important;
}
 
.dashboard_iframe {
background: none !important;
}

.post_title {
  color: #fff;
}

.note_item {
    background: none;
    opacity: .5;
}

#container {
  background-image: url("http://i40.tinypic.com/bjehrp.jpg");
  background-repeat: no-repeat;
  background-position: bottom right;
  background-attachment: fixed;
  z-index: 999;
}
 
ol#posts li blockquote,
body.mceContentBody blockquote {
margin-left: 0 !important;
margin-right: 0 !important;
padding-left: 10px !important;
border-width: 4px !important;
border-color: #dadada!important;
}
 
ol#posts li.notification.single_notification {
background-color: rgba(0,0,0,0.4) !important;
}

.post_header {
color: #fff !important;   
  }

/* rgba(119,59,0,.35) */

div.l-content .post_info a {
color: #ccc !important;
  }
  
.post_notes_label.note_count {
color: #ccc !important;
  font-size: 12px;
  transition: all 0.7s ease;
}

.post_notes_label.note_count:hover {
font-size: 15px !important;
}

#home_button, #inbox_button, #help_button, #preferences_button, #logout_button
{
  
}
 
#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: transparent;
}
 
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: rgba(60,25,0,0.35) !important;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border-radius: 20px;
margin: auto;
padding: 40px auto;
}

div.l-content #posts .post {
background: rgba(202,120,186,0.65) !important;
border: 1px solid rgba(255,255,255,0.2);
color: #fff;
text-shadow: 0px 1px 0px rgba(255,255,255,0.2);
}

div.l-content #posts .post a {
color: #ccc;
font-size: 12px;
transition: all 0.7s ease;
}

div.l-content #posts .post a:hover {
  font-size: 15px;
}

.post_avatar:after {
      border-right: 9px solid rgba(255,255,255,0.2);
      left: 98%;
}

.conversation_lines .chat_line:nth-child(even) {
background: #ffedc6 !important;

}

.conversation_lines {
background: #fff9ee !important;

}

.post_full.is_conversation .conversation_lines {
border: 1px solid rgba(119,59,0,.35) !important;
  
}

#posts .post_content blockquote {
border-left: solid 3px rgba(119,59,0,.35) !important;
padding-left: 8px;
margin-left: 6px;
    
}
      
li.chat_line {
border-bottom: 1px solid rgba(0,0,0,0) !important;

}

.post_source {
color: #fff !important;

}
  
.post_source a {
color: #ccc !important;

}

#pagination a {
color: rgba(255, 243, 218,.69) !important;
}

.post_tags a, .post .post_tags .post_tag {
color: #fff !important;
}

.post_tags a, .post .post_tags .post_tags:after {
background: none !important;
}

.post_tags, .post_tags:after {
      background: none !important;
    }
  
.link_button .link_title {
color: rgba(119,59,0,.6) !important;

}
      
.post_content {
background: rgba(0,0,0,0) !important;
}
        
h1 {
color: #989898;
 
}
 
.no_posts_found, h1.dashboard_header {
color: #989898;
}
 
img#content_top, img#content_bottom {
display: none;
}
 
 
#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {
background: rgba(0, 0, 0, 0.3);
}

#dashboard_controls a, #dashboard_controls .control {
color: #cfb290 !important;
 
}

#dashboard_controls {
color: #cfb290 !important;
}
  
#nav .nav_item.active {
background: transparent !important;
}
 
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background: transparent !important;
}
 
.notification  {
background: rgba(0,0,0,0.2) !important;
border:none !important;
 
font-size: 11px;
}
 
ol#posts li.post .permalink {
background: transparent;
}

#right_column{
background: rgba(0,0,0,0) !important;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
background: rgba(0,0,0,0.2) !important;
border: solid #000000;
}
 
#right_column .controls_section .with_subtitle small, .a {
color: #ffffff;
}

.controls_section li a {
color: rgba(255, 243, 218,.69) !important;
  }
 
#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
border-top: 1px solid #c07474;
border-bottom: 1px solid #c47c7c;
}
 
form.dashboard_options_form {
background-color: transparent ;
border-bottom-left-radius: 10px 10px;
border-bottom-left-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-right-radius: 10px 10px;
border-top-right-radius: 10px 10px;
color: #cccccc;
display: block;
font-size: 13px;
margin-bottom: 25px;
padding: 15px 22px;
}
 
form.dashboard_options_form .option_container {
border-top: 1px solid #d29b9b;
}
 
.no_posts_found, h1.dashboard_header {
color: #2c2c3d;
}
 
.answer_container {
background-color: #000000;
border-bottom: 0px solid #9d9d9d;
}
 
#nav .nav_item .nav_menu {
background-color: #000000;
border-bottom: 2px solid #9d9d9d;
}
 
#nav .nav_item .nav_menu a {
border-top: 1px solid #9d9d9d;
}
 
#previous_page_link, #next_page_link, .add_and_remove,
.mass_editor, .MissingE_marklist_title, .customize,
.recessed, .answer_avatar, .flat, .dashboard_options_form,
#auto_pagination_loader{background:transparent !important;
border-top: 0px;}
 
#right_column .dashboard_nav_item a:hover {
color: #fff;
}
 
#right_column .dashboard_nav_item a {
color: #fff;
}
 
#right_column .dashboard_nav_item ul.dashboard_subpages li a:hover {
color: #fff;
}
 
#right_column .dashboard_nav_item ul.dashboard_subpages li a {
color: #fff;
}
 
#right_column .dashboard_nav_item .dashboard_nav_title {
color: #fff;
}
 
#right_column {
  color: #fff;
  background: rgba(202,120,186,0.65) !important;
  border: 1px solid rgba(255,255,255,0.2);
  border-radius: 20px;
}
 
#right_column a#dashboard_switch_blog_menu_current {
color: #f2f2f2;
backgroubd: #fff;
}
 
#nav .nav_item {
color: #000000;
}
 
#nav .nav_item:hover {
color: #333333;
}
 
.new_post_notice {
background-color: #333333;
}
 
.new_post_notice img {
opacity: 0;
}
 
#search_box input {
border: 1px solid #fff;
}
 
#footer {
color: #ae9999;
}
 
#footer a {
color: #ae9999;
}
 
#dashboard_controls_radar_media_fold {
opacity: 0;
}
 
#edit_post_controls #slug_url {
color: #ae9999;
}
 
h2 .optional {
color: #ae9999;
}
 
h2 {
color: #ae9999;
}
 
#promos .promo .tidbit {
color: #ae9999;
}
 
#edit_post_controls .advanced_option label {
color: #ae9999;
}
 
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 4px #ffffff !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}
 
.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(60,25,0,0.35) !important;}
 
div.return_to_top_icon{ background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
margin-right: -12px !important;
border-bottom: solid 35px rgba(60,25,0,0.85) !important;
border-right: 20px solid transparent !important;
border-left: 20px solid transparent !important;
}

.post_question {
font-family: 'Courier new' !important;
font-size: 16px !important;
background: #151a20 !important;
color: white !important;
}
 
.post_question:after {
background: transparent !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-top: solid 7px #151a20 !important;
border-right: 7px solid transparent !important;
border-left: 7px solid transparent !important;
}
 
.follower {
background: white;
}
 
.follower.alt {
background: #eaeaea;
}
 
#right_column:after{
background: transparent !important;
}
 
#left_column #posts .post .permalink{background:transparent!important;}
 
 
.link_button {
min-height: auto !important;
font-size: 16px !important;
border: 0px !important;
background: none !important;
color: #777 !important;
}
 
.link_button .link_text_container {
padding:30px !important;
}
 
.link_button .link_title {
color: #777 !important;
font-size: 16px !important;
}
 
.link_button .link_source {
color: #777 !important;
font-size: 10px !important;
}

.link_button .link_text_outer {
height: auto !important;
}
 
.old_archive {
background: none !important;
}
 

 
.ui_stats .row .unit .brag {          
  color: #444444 !important;
}
       
.ui_stats .stat_title {          
  color: #8f8f8f !important;
}    
 
.ui_notes .ui_note .part_activity .part_main a {          
  color: #444444 !important;
}
 
.ui_notes .ui_note.is_friend {          
  background-color: #f4f4f4 !important;
}
 

 
.post_full.is_note .post_body .note_item, .main_content.note_form .note_item {
  background-color: #f5f5f5 !important;
  color: #444444 !important;
}
 
.post_full.is_note .asker, .post_full.is_note .answerer,
.main_content.note_form .note_item .asker, .main_content.note_form .note_item .answerer {
  color: #b1b1b1 !important;
  font-weight: 900 !important;
  text-transform: lowercase !important;
}
             
.post_full.is_note .answerer .name, .post_full.is_note .asker .name {
  color: #b1b1b1 !important;
}
 
.post_full.is_note .answerer a.name:hover, .post_full.is_note .asker a.name:hover {
  color: #969696 !important;
}
             
       
.post_full.is_note .nipple, .post_full.is_note .nipple::after,
.main_content.note_form .nipple, .main_content.note_form .nipple::after {
  border-left: 8px #444444 !important;
}

#footer a {
color: rgba(255, 243, 218,.69) !important;
}

.post_permalink {
background: url('http://i41.tinypic.com/30ianty.png') !important;
width: 33px !important;
height: 30px !important;
display: inline !important;
margin-top: -8px;
}

.post_permalink:hover {
background: url('http://i41.tinypic.com/30ianty.png') !important;
width: 33px !important;
height: 30px !important;
display: inline !important;
}

.xtimestamp {
color: rgba(119,59,0,.35) !important;
margin-bottom: 0px !important;
}

.active {
background-color: rgba(115,70,51,0) !important;
}

.v_center_outer {
background-color: rgba(115,70,51,0.7) !important;
}

.controls_section.user_list li a:before
{
	background: none;
    border-right: 1px solid rgba(255,255,255,0.2);
}

.controls_section.user_list li a:before
{
	background: none;
}

.quote {
  color: #fff;
}

.send_fan_mail{
  background: rgba(202,120,186,0.65) !important;
  border-bottom: 1px solid rgba(255,255,255,0.2);
  border-radius: 20px;
}

.controls_section.user_list li:hover a:before, .control_hover .controls_section.user_list li:active a:before
{
  background: none;
  border-right: 1px solid rgba(255,255,255,0.2);
}

div.ui_follow, .controls_section li div.follow {
  color: rgba(60,25,0,0.2);
}

div.ui_follow:hover, .controls_section li .follow:hover {
  background: rgba(255,255,255,0.5);
}

.search_form_row {
background-color: rgba(60,25,0,0.5) !important;
}
}
