css:
@-moz-document url("http://www.tumblr.com/settings"), url-prefix("http://www.tumblr.com") , url("https://www.tumblr.com/settings"), url-prefix("https://www.tumblr.com") {
body {
   background:url("http://i41.tinypic.com/r0t43o.png") no-repeat fixed !important;
   background-color: rgba(44,71,98, 0.95) !important;
   background-position: 0% 0% !important;
   background-size: 100% 100%  !important;
 }


 #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
   background: rgba(255,255,255, 0.3) !important;
 }


 #new_post {
   background-color: rgba(255, 255, 255, 0.99) !important;
 }


 #post .post{
 background: rgb(196,232,236, 0.8)!important; 
}


#right_column .controls_section li a,
#right_column .controls_section li a .count {
  color: #178f9d !important;
}  

#right_column .controls_section li a:hover {
  color: #ffffff !important;
  background-color: rgba(52,124,164, 0.5) !important;
  border-radius: 5px !important;
}  

.selection_nipple:not([class="selection_nipple white"]) {

  border-bottom: solid 11px rgba(255,255,255, 1)  !important;
}
  
  
.notification:link, .notification:active, .notification:visited {
        color: #328e98 !important;
        text-decoration: none !important;
        border: none !important;
}           
.notification {
    background: rgba(44,124,133, 0.5) !important; 
    border: none !important;
    color: #ffffff !important;
    font-size: 12px !important;
  }     
}
