@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kronos.mcanime.net") {

/* Background */
body {background-position:top center !important; background-attachment:fixed !important; background-color:#051121 !important;}

#content, #content-enc, #user-feeds #tweet-detail {background-color:rgba(255,250,250,0.85) !important;}
#content.main.wide {background:rgba(255,250,250,0.85) !important;}
#sidebar, div#tab-menu {opacity:0.85 !important;}
.w-forum, #user-feeds #pComment {opacity:0.85 !important;}
.pComment-item, .cmc-category a.cat:hover, .cmc-category a.cat.sel,.item.odd, div.feed2 div.finfo, h4.fs, #block-estrenos, #popular-series, .episodes_reel{ background-color:rgba(163,164,200,0.4) !important;}
.profile-story, div.message div.signature { border-color:#9E9EA6 !important;}
div#gtabs {border-color:#C7C9E8 !important;}
div#content div#user-feeds div.profile-story form.pComment-box span.story-info  textarea.pComment-placeholder, h4.fs, #sub-gtabs, .feed-subtitle, .more-feeds.r3, div.feed2 {border-color:#7A88DF !important;}
div#content div#user-feeds div.profile-story form.pComment-box span.story-info textarea {border-color:#334BB6 !important;}
div.mboard div.rules {background: #F3B6BA !Important;}
div.mboard div.crumbs {background-color: #D5D3E7 !Important;}
div#content.content-series {background:none !important;}

/*NEW*/
.top_series, #nav-encic {background:none !important; background:rgba(0,0,0,0.6) !important;}
#nav-encic {height:41px !important;}
.s-list.popular .gridbox li {background:rgba(255,255,255,0.6) !important;}

/* Links */
.downloads a:visited {color:purple !important;}
div.post div.message a.postlink {border-bottom:0px !important; color:#305474 !important; font-weight:bold !important; padding:2px !important;}
div.post div.message a.postlink:hover {border-bottom:0px !important; text-decoration:underline !important;}
div.post div.message .signature a.postlink:hover {background:none !important;}

/* Fix March */

.profile-story {min-height:100px !important;}

.pComment {width:100% !important; overflow:hidden !important; vertical-align:middle !important;}

.pComment-list > .pComment-item { margin-top:0px !important; padding:5px 0px 5px 5px !important;}

#user-feeds .add-pComment textarea {margin-right:5px !important;}
.main-feeds .pComment-list .add-pComment > label.pComment-btn {margin-right:5px !important;}

.main-feeds textarea.pComment-placeholder:not(:focus) {width:547px !important;}
.main-feeds textarea:not(.pComment-placeholder):focus {width:481px !important;}
.main-feeds textarea:not(.pComment-placeholder) {width:481px !important;}


#tweet-detail + div .comment-remove {position:absolute !important; right:20px !important;}
/* fin */

.friends-info .friends-grid .friends-grid-row div {width:60px !important; height:120px !important; margin:0px 0px 0px 8px !important; padding:0px !Important; text-align:center !Important;}

.friends-info .friends-grid .friends-grid-row div:first-of-type {
margin-left:0 !important;
}

.friends-info .friends-grid .friends-grid-row div img {width:100% !important; height:100% !important; max-width:60px !important; max-height:100px !Important; }

FORM > SPAN > UL > LI > IMG[class="pComment-author"] {width:100% !important; height:auto !important; max-width:60px !important; max-height:100px !important;}

div#mcbox.hide div.content div.mcbox_body.friends-mcbox {max-height:290px;}

div#mcbox.hide div.content div.mcbox_body div.friends-grid div.friends-grid-row {min-height:100px !important;}

div#mcbox.hide div.content div.mcbox_body div.friends-grid div.friends-grid-row div {width:60px !Important; margin-right:7px !important;}

div#mcbox.hide div.content div.mcbox_body div.friends-grid div.friends-grid-row div a.avatar {display:table-cell !important; width:60px !important; height:100px !Important; max-width:60px !important; max-height:100px !important; vertical-align:middle !important;}

div#mcbox.hide div.content div.mcbox_body div.friends-grid div.friends-grid-row div a.avatar img {display:block !important; width:100% !important; height:auto !Important; max-width:60px !important; max-height:100px !important; vertical-align:middle !important; margin:auto !important;}

div#mcbox.hide div.content div.mcbox_body div.friends-grid div.friends-grid-row div {text-align:center !important;}

div#mcbox div.content {max-height:310px !Important; overflow-y:auto !Important;}

/* Animes Watching */
[class="series-info block r5"] > h5 {text-align:center !important;}

.series-info-list > .sil-row a {color:#305474 !important;}

.series-info-list > .sil-row {text-align:center !important;}

.series-info-list > .sil-row > .sil-title {height:105px !Important;display:table-cell !important;  vertical-align:middle !important; width:77px !important; max-width:77px !important;}

.series-info-list > .sil-row > [class="sil-img r3"] {width:75px !important; height:100px !Important; margin:0 0px 0 2px !Important; border-color:#BBD1E1 !important;}

.series-info-list > .sil-row > [class="sil-img r3"] img {width:100% !important; height:auto !important; }


/* Unhide */
div[class*="dlds fd"] {position:inherit !important;}
.col > .detail > div {display:block !Important;}


/* Groups */
.gmembers img {width:60px !important; height:100px !important;}
.gmembers div {margin-bottom:0 !important; width:60px !important;}

.gmembers div{ text-align:center !important; margin:3px 3px 0px 3px !important;}
.gmembers .gmembers_info {width:130px !important;}
.gmembers {width:100% !important; margin:0px auto 0px auto !important; text-align:center !important; padding-left:3px !Important;}

/* Forum */

div.mboard div.post.online {background-position:100% 0 !important;}

.post .message {min-width:779px !important; max-width:1009px !important; padding:5px 0px 0px 0px !important; margin-left:150px !important; margin-right:10px !Important;}

[class*="post bg1"] {background-color:#D6D6D6 !important;}
[class*="post bg2"] {background-color:#E0E0DE !important;}

[class*="post bg1"] .signature {border-top:1px solid #CCCCCC !important;}

[class*="post bg1 online"] .message .icons, [class*="post bg2 online"] .message .icons {margin-right:43px !important;}

.icons li {margin:0px !important; padding:0px !important; margin-left:5px !important;}

div#wrapper.w-forum div#container div#content.groups {width:97% !Important; max-width: 1128px  !Important; min-width: 960px  !Important; margin: 0 auto 0 auto !important; float:none !Important;}

div#wrapper div#container div#content.configuration div.post div.message {margin:inherit !Important;}


/* Online Image */
#content.profile-cont.online, #content.mboard .post.online {background-image:url('data:image/png;base64,') !important;}

#content.profile-cont.online .profile-msg {min-height:24px !important;}

/* Downloads */
[class="htitle green"] {background:url(data:image/png;base64,)center repeat-x !important; color:#460000 !important; border:1px solid #996600 !important; margin-bottom:5px !important;}
.block10 #series_description {width:280px !important;}

/* Twitter Clon */
div#particular_tweet ul.pComment-list li.pComment-item, div#particular_tweet + div ul.pComment-list li.pComment-item {
background:none !important;
}

div#particular_tweet ul.pComment-list li.pComment-item.dark_color {
background:#EAEAEA !important;
}

/* Imagenes */

/* Fansub Image */
.ficon img[src*="fansubs"] {width:100% !important; height:auto !important; max-width:100px !important;}


/* Fansub Proyects */
.fansub-project-img {width:75px !important; height:100px !important;}
.fansub-project-img img {width:100% !important; height:auto !important;}
.fansub-project-info {width:116px !IMPORTANT;}


/* Notifications Images */
.user-notification .friend-avatar, .ua-box .friend-avatar img  {width:100% !important; height:auto !important; max-width:60px !important; max-height:100px !important;}

div[id*="mcbox"] div[class*="content"] div[class] div[class*="user-notification"] .friend-avatar, div[id*="mcbox"] div[class*="content"] div[class] div[class*="user-notification"] .friend-avatar img{width:100% !important; height:auto !important; max-width:60px !important; max-height:100px !important; padding-bottom:4px !important; margin-right:5px !important; display:table !important;}

div[id*="mcbox"] div[class*="content"] div[class] div[class*="user-notification"]  {clear:both !important;}

div#mcbox div.content form div.user-notification a.friend-avatar {display:block !Important; width:60px !important; height:auto !Important; max-width:60px !Important; max-height:100px !Important;}

div#mcbox div.content form div.user-notification a.friend-avatar img {width:60px !important; height:auto !important; max-width:60px !important; max-height:100px !important; padding-bottom:4px !important; margin-right:5px !important; }

/* Comments */
div.pComment a.pComment-pic {display:block !important; width:100% !important; height:auto !important; max-width:60px !important; max-height:100px !important;} 

div.pComment a.pComment-pic img  {width:100% !important; height:auto !important; max-width:60px !important; max-height:100px !important;}

.pComment-content {padding-left:30px !important;}


/* Profile */


.profile-avatar {text-align:center !important;}
div.profile-info div.profile-avatar img {max-width:120px !Important; max-height:200px !Important;}

.profile-msg {min-height:8px !important;}

div#user-feeds div.profile-story a.story-pic {display:block !important; width:60px !important; height:auto !important; max-width:60px !important; max-height:100px !important;} 

div#user-feeds div.profile-story img.pComment-author {width:60px !important; height:auto !important; max-width:60px !important; max-height:100px !important; }

div#user-feeds div.profile-story a.story-pic img {width:100% !important; height:auto !important; max-width:60px !important; max-height:100px !important;}

#user-feeds .profile-story {margin-bottom:7px !important; padding-left:70px !important;}

.main_img, .main_img img {width:300px !important; height:400px !important; }

.series_image a {margin-left:57px !important;}

div.enciclopedia div.series_info {width:462px !Important; margin-left:115px !important;}

.gimg img, .gimg {width:100% !important; height:auto !important; max-width:200px !Important;}

/* Fin */

}
