css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userscripts.org") {
body
{
color: #CA7104 !important;
background: black !important;
}

a {
color: grey !important;
border: 1px solid transparent !important;
}

a:hover {
color: #C0C0C0 !important;
border: 1px solid #CA7104 !important;
-moz-border-radius-topright : 6px !important;
-moz-border-radius-topleft : 6px !important;
-moz-border-radius-bottomright : 6px !important;
-moz-border-radius-bottomleft : 6px !important;
}

div
{
color: #CA7104 !important;
background: black !important;
}

input, textarea, select
{
color: #CA7104 !important;
padding: 0px 4px !important;
border-top: 1px solid grey !important;
border-bottom: 1px solid grey !important;
border-left: 1px solid grey !important;
-moz-border-radius-topright: 5px !important;
-moz-border-radius-topleft: 5px !important;
-moz-border-radius-bottomright: 5px !important;
-moz-border-radius-bottomleft: 5px !important;
background: black !important;
margin-right: 0em !important;
float: none !important;
}

em
{
background: #2E2E2E !important;
color: orange !important;
padding: .2em !important;
}

code
{
overflow: auto !important;
background: transparent !important;
color: #C0C0C0 !important;
padding: .12em !important;
}

p#install_script {
	padding: 1em 0 0.5em 0 !important;
}

#install_script a
{
border: 1px solid #CA7104 !important;
padding: 4px 20px !important;
text-decoration: none !important;
background: transparent !important;
font-weight: bold !important;
color: white !important;
-moz-border-radius-topright : 6px !important;
-moz-border-radius-topleft : 6px !important;
-moz-border-radius-bottomright : 6px !important;
-moz-border-radius-bottomleft : 6px !important;
}

#install_script a:hover
{
color: orange !important;
background: transparent !important;
border: 1px solid grey !important;
-moz-border-radius-topright : 6px !important;
-moz-border-radius-topleft : 6px !important;
-moz-border-radius-bottomright : 6px !important;
-moz-border-radius-bottomleft : 6px !important;
}

#install_warning
{
display: none !important;
}

h3,h6
{
overflow: auto !important;
padding: 0px 3px !important;
background: #282828 !important;
color: white !important;
border: 1px solid grey !important;
}

h1 {color: orange !important;}

pre
{
font-family: arial !important;
overflow: auto !important;
padding: .2em !important;
border: 1px solid grey !important;
background: url(data:image/png;base64,) !important;
}

#content
{
position: absolute !important;
left:12px !important;
width: 83% !important;
border: 0px solid transparent !important;
padding: 1em !important;
}

.post,.author
{
border: 1px solid black !important;
background: #2E2E2E !important;
}

.posts,.post,.body
{
width: 84% !important;
border: 0px solid transparent !important;
padding: 0px 5px !important;
background: #1B1B1B url(data:image/png;base64,) !important;
}

.posts .post .body blockquote
{
overflow: auto !important;
border: 1px solid black !important;
background: url(data:image/png;base64,) !important;
padding: .2em !important;
}

table tr td.inv
{
background: transparent !important;
color: grey !important;
}

table tr th
{
border: 1px solid grey !important;
}

table tr td
{
border: 1px solid grey !important;
padding: 4px 8px !important;
}

tr.spacer td
{
border: 0px solid transparent !important;
}

#right
{
width: 14% !important;
position: absolute !important;
right: 0px !important;
background: transparent !important;
}

.topic
{
background: transparent !important;
border: 1px solid grey !important;
color: #CA7104 !important;
}

pre.sh_sourceCode .sh_comment {color: orange !important;}
pre.sh_sourceCode .sh_string {color:orange !important;}
pre.sh_sourceCode .sh_function {color:#eee !important;}
pre.sh_sourceCode .sh_specialchar {color:#0f0 !important;}
pre.sh_sourceCode .sh_keyword {color:#4FA7FF !important;}
pre.sh_sourceCode .sh_number {color:#DBB7FF !important;}

.sh_javascript,.sh_sourceCode
{
color:#eee !important;
}

.subnav li a
{
background: #2e2e2e !important;
color: orange !important;
}

.subnav li a:hover
{
background: #2e2e2e !important;
color: #eee !important;
}

#reply table tr td {border: 0px solid transparent !important;}

}
