css:
@-moz-document url(chrome://browser/content/browser.xul){

/* Green */
#main-window[wot-status^="5"] #urlbar {background: linear-gradient(165deg, white, white, white, white, white, #00C957)  !important;}

/* Green-Yellow */
#main-window[wot-status^="4"] #urlbar {background: linear-gradient(165deg, white, white, white, white, #BCEE68) !important;}

/* Yellow  */
#main-window[wot-status^="3"] #urlbar {background: linear-gradient(165deg, white, white, white, white, white, yellow) !important;}

/* Red-Orange */
#main-window[wot-status^="2"] #urlbar {background: linear-gradient(165deg, white, white, white, white, white, DarkOrange) !important;}

/* Red */
#main-window[wot-status^="1"] #urlbar {background: linear-gradient(165deg, white, white, white, white, white, red) !important;}

/* Grey */
#main-window[wot-status^="0"] #urlbar {background: linear-gradient(165deg, white, white, white, white, lightgrey) !important;}
}
