css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("delicious.com") {

/* header */

#globalnav {
padding: 1em 0em 1em 0 !important;
}

#banner {
border-top: 1px dashed #3274D0 !important;
border-bottom: 1px dashed #3274D0 !important;
padding: 1em 0em 1em 0em !important;
margin: 0px !important;
background-color: #EFF5FB !important;
}

#navigation {
margin-left: 11.5em !important;
}

#navigation h1#site_title {
top: 0.6em !important;
background-color: transparent !important;
padding: 0 0 0 1em !important;
}

#navigation h1#site_title a {
background-color: transparent !important;
}

#navigation ul.subnav-list li a {
font-size: 97% !important;
}

#navigation ul#subnav-list-bookmarks li a:hover {
color: #B0B0B0!important;
}

#navigation ul#subnav-list-people li a:hover {
color: #8E8E8E!important;
}

#navigation ul#subnav-list-tags li a:hover {
color: #565656 !important;
}

/* links */

a { text-decoration: none !important; }
a:hover { color: #14c162 !important; }

#navigation #list-link-home:hover {
color: #3274D0 !important;
}

#navigation #list-link-bookmarks:hover {
color: #7B7B7B !important;
}

#navigation #list-link-people:hover {
color: #7B7B7B !important;
}

#navigation #list-link-tags:hover {
color: #565656 !important;
}

/* titlebar */

#ttlbar {
margin: 0 !important;
}

#ttlbar #ttl {
padding-top: 1em !important;
}

#pagetitle {
border-bottom: 1px solid #D5D5D5 !important;
margin-bottom: 1em !important;
}

#pagetitle h2 {
margin: 0px !important;
color: #8E8E8E !important;
}

#pagetitle h2 em + strong em {
color: black !important;
}

#pagetitle p#see_also {
padding: 1em 0 1em 0 !important;
}

#pagetitle #see_also strong { 
font-weight: normal !important;
color: black !important;
}

#pagetitle #see_also strong em { 
/*font-weight: normal !important;*/
}

#ttlbar #actions {
top: 1em !important;
}

#actions-list {
border-bottom: 1px dashed #3274D0 !important;
border-top: 1px dashed #3274D0 !important;
}

/* body */

#bookmark-display-options {
padding: 1em 0 1em 0 !important;
}

ul.bookmarks li.post h4 a {
font-size: 90% !important; /* from 130% ! */
}

ul.bookmarks li.post .savers a:hover {
background-color: #14c162 !important;
color: white !important;
}

ul.bookmarks li.post a.inlinesave, ul.bookmarks li.post a.action:hover {
background-color: white !important;
}

/* foot */

#ft #footernavigation {
border-top: 1px dashed #3274D0 !important;
border-bottom: 1px dashed #3274D0 !important;
padding: 1em 0em 1em 0em !important;
background-color: #EFF5FB !important;
}

#ft #footernavigation a:link {
color: #3274D0 !important;
}

#ft #footernavigation a:visited {
color: #999999 !important;
}

#ft #footernavigation a:hover, #ft #footernavigation a:active {
color: #14c162 !important;
}

#ft #footernavigation #whatsnew {
background-color: transparent !important;
margin-right: 1em !important;
}

}
